insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南推拿职业学院',' ','河南',0,3,0,0,0,' ','暂空',' ','http://www.hnzjschool.com','河南推拿职业学院是河南省人民政府批准、教育部备案的公办专科层次全日制普通高等职业院校，其前身是建于1959年的“河南省针灸推拿学校”及2001年与河南中医学院联合举办的“河南中医学院针灸推拿职业学院”。学院主要培养适应医疗卫生事业发展需要的高等技能型人才，同时举办中等职业教育。学院位于千年帝都、牡丹之乡、历史文化旅游名城——洛阳，北依洛河，南望世界文化遗产龙门石窟，东邻洛阳体育公园，西与洛阳理工学院接壤，地理位置优越，交通条件便利，周边环境优美，酒店、商场、饭店、公园遍布周围，是莘莘学子求学、生',35167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('洛阳职业技术学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.lyvtc.net','洛阳职业技术学院，部标代码：14383，本省代码：6384。学院地址：洛阳市高新区丰华路洛阳市机场路六号。邮编471000。学院是公办全日制普通高职院校。学院招生类型：普通高等职业技术教育。学业期满，成绩合格，达到毕业要求，颁发教育部统一电子注册的普通高等学校学历证书和河南省毕业生就业主管部门颁发的就业报到证。',62700);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川三河职业学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.scshpc.com/','四川三河职业学院是经四川省人民政府批准，国家教育部备案的全日制普通高等学校。学院位于重庆半小时经济圈，酒城泸州东郊，长江、赤水与习水相汇处——合江。这里山河锦绣，风光旖旎，人杰地灵，通江达海，衢贯八方，有著名的福宝国家级森林公园，有中国历史文化名镇尧坝古镇，也是中国优质晚熟荔枝之乡；现有泸渝（泸州到重庆）、泸赤（泸州到赤水）两条在建高速公路邻校而过，连通成都、重庆、昆明、贵阳等大中城市，交通便利，发展前景美好。在这片神奇美丽的土地上，孕育着三河职业学院的崛起与腾飞。学校占地面积800多亩，建筑面',19018);
