insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都师范学院',' ','四川',1,4,0,0,0,' ','暂空',' ','http://www.cdnu.edu.cn/','成都师范学院是四川省人民政府举办，四川省教育厅直管的国家公办全日制普通本科院校，前身是1955年创建的四川教育学院。2012年3月经教育部批准改建更名为成都师范学院。学校位于中国历史文化名城——“天府之国”成都市。现有人民南路、温江、白果林三个校区，总占地面积1100余亩。教学楼、实验楼、图书馆、学生宿舍、运动场等教学和学生生活设施齐全。拥有理科、工科和信息与计算三个实验中心，语言发展、教师职业技能、艺术与体育和管理类四个实训中心，共有专业实验（实训）室60余个；校外教学实习（实训）基地120余',559314);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西学前师范学院',' ','陕西',1,4,0,0,0,' ','暂空',' ','http://www.snie.edu.cn','陕西学前师范学院位于古都西安，是一所立足陕西，以教师教育为主体，职前培养与职后培训相贯通，服务与引领基础教育改革发展为主要办学特色的省属普通本科院校，是陕西省中小学教师、校园长和基础教育管理干部培养培训的重要基地之一，也是陕西省创业研究和西安市大学生创业培训基地。学院前身是陕西教育学院，1963年由陕西省西安师范学校（1906年建立）和陕西省教师进修学校（1956年建立）合并成立。2012年3月，经国家教育部和陕西省人民政府批准，学院改制更名为陕西学前师范学院。建校百余年来，学院培养了本专科生1',983648);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州幼儿师范高等专科学校',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.zyedu.org','一、基本情况郑州幼儿师范高等专科学校始建于1954年，曾在1961年国民经济调整及1970年文革动乱时期两度中断，最终于1980年由河南省政府下文恢复，1982年在现校址重新招生，有二十九班规模，在校生1500多名。学校于2000年面向全省招收“五年一贯制”学前教育专科生，2002年招收“三三制”对口学前教育专科生，2007年招收高中起点“三年制”学前教育专科生。在对专科层次幼教师资培养10年的探索中，郑州幼师积累了丰富的经验，为初中毕业起点五年制学前教育专科学历教师的培养奠定基础。郑州幼专为新',111849);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安阳幼儿师范高等专科学校',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.ayys.net.cn','安阳幼儿师范学校原名安阳市第一师范学校。始建于1978年，主要培养中师，1980年开始招收幼师专业，1999年逐步由中师向幼师转轨，2002年更名为安阳幼儿师范学校。2012年由教育部正式批准在安阳师范学校的基础建立安阳幼儿师范高等专科学校。安阳市教师培训中心与学校合署办公，内设培训部、函授部、安阳市教育干部培训学校、幼儿教育发展中心。学校实行“一套班子，4块牌子，一个实体，双重任务”的管理模式，构建了职前培养和在职培训一体化的完整教师教育体系。',71490);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('川北幼儿师范高等专科学校',' ','四川',0,4,0,0,0,' ','暂空',' ','http://www.cbyzsc.cn/','嘉陵长，蜀道远;百年薪火，川北幼专。川北幼儿师范高等专科学校是经四川省人民政府批准成立的公办全日制普通高等专科学校。学校的前身为发端于1902年、创办于1937年，受国家教委表彰的全国百所先进师范学校之一——四川省广元师范学校。学校具有百年师范办学传统，人文气息浓郁，文化积淀丰厚。1938年8月张澜先生(新中国第一届中央人民政府副主席)视察学校，向全校师生诠释的“智、仁、勇”教育思想，成为数代师生发愤自雄一往无前的奋斗理想。学校坐落于女皇故里、剑门蜀道、全国最宜人居城市——广元。学校依山傍水而建',116194);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海民航职业技术学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.shcac.edu.cn','国家公办民航直属立足华东，服务民航，特色鲜明，社会满意学院简介上海民航职业技术学院（学院代码14394）是经上海市人民政府批准、教育部备案、隶属于中国民用航空局的独立设置的一所全日制公办普通高等院校。学院面向行业，致力于培养服务民航和社会发展所需的一线高素质、高技能型人才。学院招生专业有空中乘务、民航空中安全保卫、民航运输、民航运输（民航电子商务）、民航运输（航空旅游服务）、航空物流、机场运行（民航机场气象观测）、民航安全技术管理、飞机机电设备维修、飞机电子设备维修、飞行器制造技术、飞机结构修理',407357);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京卫生职业学院',' ','北京',0,3,0,0,0,' ','暂空',' ','http://www.bjwsxx.com/','北京卫生职业学院是2012年经北京市人民政府批准设立、国家教育部批准备案的高等职业学院，由原北京卫生学校、北京护士学校和北京市中医学校合并组建。北京卫生职业学院由北京市卫生局举办，以培养大学专科层次高等卫生职业技术人才为主，学院高等职业教育部分开设护理、药学、中药、医学检验技术、医学影像技术、康复治疗技术、医疗仪器维修技术7个专业。学院设中专部，开设护理（涉外方向）、中医护理、普通护理、药剂、中药、医学检验技术、医学生物技术、口腔修复工艺（涉外方向）、卫生信息管理、中医康复保健等10个专业（专业',129949);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北轨道运输职业技术学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.hbgdys.cn/','河北轨道运输职业技术学院是经河北省政府批准、国家教育部备案，由河北省人力资源和社会保障厅举办和管理的全日制公办普通高等学校，实施专科层次高等职业技术教育，面向全国招生。学校代码14396。学院位于河北省会石家庄市，拥有三个校区，分别位于石家庄市经济开发区清源街66号、宁安路189号和国泰街17号。总占地面积510亩，建筑面积20余万平方米，是省内乃至华北地区唯一一所以轨道运输运营、管理、维修维护为特色的高职学院。首批开设铁道交通运营管理、铁道机车车辆、铁道旅客运输、机电一体化技术、数控技术5个专',152554);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('运城护理职业学院',' ','山西',0,3,0,0,0,' ','暂空',' ','http://www.ychlxy.com/','历史沿革、1970年，运城地区卫生学校成立；2001年，运城撤地改市，原运城地区卫生学校更名为运城市卫生学校；2002年，运城卫校荣升为省部级重点中专学校；2003年，经省政府批准运城市卫生学校与运城市口腔卫生学校合并为运城卫生学校，在运城卫生学校的基础上成立山西医科大学运城学院，运城卫校为山西医科大学运城学院医学部，运城口校为山西医科大学运城学院口腔部。两部均实行大专和中专办学双轨制。隶属关系、山西医科大学运城学院，运城市卫生学校是由山西省人民政府批准成立，运城市人民政府主办，运城市卫生局领导',97921);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁公安司法管理干部学院',' ','辽宁',0,11,0,0,0,' ','暂空',' ','http://www.lacpj.com','辽宁公安司法管理干部学院是经教育部批准，隶属辽宁省人民政府，由公安厅负责管理的辽宁省政法系统唯一一所集普通学历教育、成人学历教育、政法系统在职干警培训和人民警察警衔晋升培训于一身的高等院校。学院新校区座落于沈阳市棋盘山国际风景区，毗邻世界文化遗产东陵公园。校园占地400亩，规划建筑面积12万平方米，办学规模6000人，各类干警培训年3000多人次。优越的办学条件、优美的自然环境、优秀的教工队伍，使这所有着62年辉煌历史的“老字号”干部培训学院又拓展出了高职教育、成人教育的新绿。在履行全省政法系统',44079);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐齐哈尔理工职业学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.qlgxy.com/','齐齐哈尔理工职业学院始建于1981年，前身是齐齐哈尔市建设职工大学，由原齐齐哈尔市职工建筑工程学院和原齐齐哈尔市城建中专合并而成，是黑龙江省西北部地区唯一一所独立设置的成人高等学校。2011年上旬，经黑龙江省高等学校设置评议委员会专家组专家评议，经省政府批准正式成立齐齐哈尔理工职业学院。至此，学院完成了从成人高校转为普通高等职业院校的转变。学校现有专任教师120人，在校生2100多人。学院设建筑工程技术、工程造价、市政工程技术(道路与桥梁方向)、机电一体化、供热通风与空调工程技术(建筑水电方向)',21752);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('徐州生物工程职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.xzsw.net/','徐州生物工程职业技术学院前身是创建于1956年的江苏省徐州农业学校。2012年经江苏省人民政府批准、教育部备案，升格为“徐州生物工程职业技术学院”，为公办普通高等学校。学院目前是苏北唯一的农业类普通高等院校、全省唯一的生物工程类职业技术学院。目前，学校占地615亩，建筑面积21万平方米。现有教职工335人，专任教师260人，其中副教授以上高级职称74人，博士、硕士研究生137人，“双师型”教师123人。建有生物技术、生物制药、园艺园林、动物医院、信息机电等30个实训中心，200多个校外实训基地，',17268);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('景德镇陶瓷职业技术学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jcivt.com','景德镇陶瓷职业技术学院是经江西省人民政府批准设立，国家教育部备案的国家统招民办普通高等院校。学院地处中国历史文化名城、世界闻名瓷都——中国景德镇，与“江西第一衙”著称的浮梁古县衙毗邻。学院建筑面积32000余平方米，设立了艺术创作室、雕塑室、陶艺室、陶瓷工艺实验室、专业计算机设计中心等；生活配套设施完善，建有篮球场、足球场、超市、浴室、医务室等。目前正在筹建国书馆和实验基地大楼，预计2014年校园建筑面积超过90000平方米。',29404);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('共青科技职业学院',' ','江西',0,12,0,0,0,' ','暂空',' ','http://www.gqkj.com.cn/','共青科技职业学院是经江西省人民政府批准，国家教育部备案，在原南昌理工学院共青校区的基础上创办的全日制普通高等职业学院。是全国唯一以“共青团”冠名的高等职业学校。学院座落在素有“鄱阳湖畔的明珠，京九线上的名城”之称的共青城市。美丽神奇的共青城，位于江西北部，庐山南麓，东临鄱阳湖，西襟庐山西海，南接英雄城南昌，北依江西“北大门”九江，京九铁路，福银高速，昌九城际高铁穿境而过，昌北机场、庐山机场翼辅左右，黄金水路经鄱阳湖可直抵长江，交通十分便捷。共青人继承“艰苦创业、坚忍不拔、崇高科学、开拓奋进”的共',30779);
