insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东舞蹈戏剧职业学院',' ','广东',0,8,0,0,0,' ','暂空',' ','http://www.gdddc.edu.cn/','广东舞蹈戏剧职业学院是公办全日制专科艺术高职院校，于2012年2月经广东省人民政府批准，国家教育部备案，直属广东省文化厅。其前身为有着半个多世纪办学史的广东粤剧学校和广东舞蹈学校。学院的成立补齐了我省舞台艺术教育的“短板”，填补了我省戏剧、舞蹈表演、舞台美术高等职业教育空白。建院以来，学院在省文化厅领导下、省教育厅指导下，加速转变，加快发展，校区建设日新月异，师资队伍逐渐壮大，专业建设红红火火，教育教学欣欣向荣。学院设有佛山、广州天河和越秀三个校区，占地300多亩，建筑面积80000多平方米。校',44235);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('惠州卫生职业技术学院',' ','广东',0,3,0,0,0,' ','暂空',' ','http://www.hzwx.cn','惠州卫生职业技术学院为全日制普通高等职业技术学院，前身为惠州市卫生高级职业技术学校，由市政府举办，办学经费来源为市财政投入。办学初期将开设护理、助产、药学、医学检验技术、口腔医学技术等专业，学院建成后将立足惠州、服务全省，面向农村、社区，致力于培养基层特别是农村急需的护理、药剂、助产、医学检验技术等专业卫生人才，努力打造全省培养高素质、技能型卫生人才的高地。',122249);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川卫生康复职业学院',' ','四川',0,3,0,0,0,' ','暂空',' ','http://www.svchr.edu.cn/','四川卫生康复职业学院是经四川省政府批准，国家教育部备案成立的公办全日制普通高等学校，以实施专科层次的医药卫生类职业教育为主。学校创建于1918年，前身系加拿大基督教会创办的仁济义济高级护士职业学校，后改名四川省自贡卫生学校。学校占地面积61.3亩，建筑面积41000平方米，是一所历史悠久，师资力量雄厚，教学设备完善，办学条件优越，教学质量高的省部级重点高等专业学校。学校现有教职工145人，高级专业技术职务24人，中级专业技术职务54人。1993年以来，有5名高级讲师主编了6门四川省中等卫生学校统',154321);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川电影电视职业学院',' ','四川',0,8,0,0,0,' ','暂空',' ','http://www.scftvc.com','四川电影电视职业学院是经四川省人民政府批准，教育部备案的全日制民办艺术类高等职业学院，学院代码为14410。学院以四川师范大学电影电视学院领导团队、教学团队、实训设备、教学设施为雄厚基础，全力打造国内一流的影视高职学院。面向全国招生，以先进的教育理念与方式，培养德、智、体、美全面发展，具备影视专业技能、创新精神的传媒影视艺术人才。学院以四川师范大学电影电视学院本科师资团队担当高职各专业教学工作，拥有专、兼职教师300余人，其中副高以上职称的教师占37%；硕、博士教师占48%。学院教师70%来自于',40093);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川汽车职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scavtc.com/','四川汽车职业技术学院是经四川省人民政府批准、国家教育部备案的全日制民办高等职业学院。是四川省唯一的一所汽车类高等职业技术学院，是国家级汽车实训基地、全国职业教育先进单位、国家职业指导教学训练实验基地、国家职业技能鉴定所、四川省文明单位。学院位于中国科技城绵阳。学院现占地436亩，建有功能齐全的教学楼、先进的实验实训大楼、舒适的学生公寓、标准的运动场，规范的图书馆，实验实训条件优越。学院建有以汽车实训中心为代表的30余个实验实训室，拥有校办实习工厂，20余个校外实习基地。学院现有专任教师206人，',58524);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州工商职业学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.gzgszy.com/','贵州工商职业学院，是经贵州省人民政府批准、教育部备案、纳入国家计划内统一招生的全日制普通高等院校。学院坐落于素有“高原明珠”之称的红枫湖畔，规划占地面积1000亩、建筑面积20万平方米，校园环境优美。开设建筑工程技术、工程造价、护理、空中乘务、会计、财务管理、装潢艺术设计、广告设计与制作、汽车检测与维修技术、安全技术管理、资产评估与管理、工商企业管理、市场营销、旅游管理、计算机应用技术等15个专业，共计20个专业方向。学院高度重视师资队伍建设，现有教职工260余人，其中专任教师177人，硕士研究',32263);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('红河卫生职业学院',' ','云南',0,12,0,0,0,' ','暂空',' ','http://www.hhwx.org.cn/','红河卫生职业学院（学校代码14413）是2012年2月云南省人民政府批复同意并经教育部备案，在红河州卫生学校基础上组建的一所全日制公办高等职业院校。其前身红河州卫生学校创建于1958年，1996年被云南省人民政府批准为省部级重点中专，2001年被国家教育部批准为国家级重点中专。建校53年来，先后开办过医士、社区医学、卫生保健、护理、助产、中医士、妇幼卫生、预防医学、计划生育、卫生信息管理、口腔、健康教育、药剂、医学影像、医学检验等专业，为社会培养输送了2万余名卫生技术人才。曾先后被评为“国家级红',30231);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆体育职业技术学院',' ','新疆',0,7,0,0,0,' ','暂空',' ','http://www.xjsvtc.com','新疆体育职业技术学院是隶属于新疆维吾尔自治区体育局的事业单位，为非营利性事业组织，学院在自治区体育局主办、自治区教育厅主管下，在学院党委的直接领导下开展学院的各项工作。学院占地面积184.3亩，建筑面积已达到67531.34平方米，生均教学行政用房达到了22平方米；现有教学训练科研仪器设备3000多万元；藏书量25万多册；学院现有教师134人，其中专任教师116人，大学本科以上学历的教师125人，具有研究生学历的19人。学校现有高级职称教师41人，占教师总数的30.6%；学院开设5个专业（运动训',24388);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆应用职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.xjyy.edu.cn/','新疆应用职业技术学院是根据经济社会发展和高校布局调整的需要，2012年初经自治区人民政府批准成立、国家教育部备案的一所独立设置的以培养应用型、技能型人才为主的公办全日制普通高等职业院校。学院自1974年6月成立的农七师师范学校起，历经奎屯师范学校、伊犁州奎屯师范学院、伊犁州教师进修学院、伊犁教育学院、伊犁师范学院奎屯校区。经过三十多年的发展，先后培养各类学生30000余名，为新疆和伊犁哈萨克自治州的经济建设、社会发展和教育事业做出了应有的贡献。曾先后获得“全国民族教育先进集体”、“全国成人高等教',32046);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥科技职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.hfstu.cn/','合肥科技职业学院是经安徽省人民政府批准、国家教育部备案、具有独立颁发学历文凭资格的的全日制普通高等院校，学院代码为14420。学院前身是中国计算机函授学院，有着近30年的办学历史，累计培养了80多万名学子，为我国的信息化建设和高等职业教育做出了一系列积极的贡献。学院坐落于国家科教基地安徽省省会合肥市，地处国家级风景区紫蓬山东麓，毗邻国家高新开发区、经济开发区和大学城，周边自然环境优美，科教文化氛围浓厚、生活交通便利。学院占地近400亩，规划总建筑面积20多万平方米。学院按照文化校园、数字校园、生',28171);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆师范高等专科学校',' ','新疆',0,4,0,0,0,' ','暂空',' ','http://www.xjei.cn/','新疆师范高等专科学校（新疆教育学院）是一所专门为义务教育和学前教育培养人才的普通师范院校。学校创建于1978年，前身是乌鲁木齐第一师范学校，最早可追溯至1906年的新疆师范学堂。1989年，原新疆广播电视师范大学并入新疆教育学院。2003年，原新疆工业技术学校并入新疆教育学院。2012年3月，新疆师范高等专科学校挂牌，实行“一套机构，两块牌子”；12月，新疆幼儿师范学校并入新疆师范高等专科学校，重点承担新疆学前教师、小学教师、初中教师和双语教师培养培训任务。学校坐落在乌鲁木齐市风景秀丽的红山脚下',117320);
