insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨幼儿师范高等专科学校',' ','黑龙江',0,4,0,0,0,' ','暂空',' ','http://hayouzhuan.zhiye.chaoxing.com/','哈尔滨幼儿师范高等专科学校始建于1952年，是黑龙江省成立最早的幼儿师范学校，是“中国——联合国儿童发展基金会”合作项目单位、黑龙江省幼儿教育研究会秘书单位、黑龙江省哈尔滨市幼儿师资培养培训中心，先后被评为国家级和省级办学先进集体。肩负着为社会培养高素质、强技能、应用型幼教人才的神圣使命。有着60年办学历史的哈幼专，积淀了丰厚的历史底蕴，有着优良的文化传统，培养了无数幼教精英。学校坐落于素有“东方小巴黎”之称的冰城哈尔滨，校园环境优美、办学条件优越、办学理念先进、文化气息浓郁。为了进一步优化师范',74360);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林城市职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','www.jlcsxy.com','吉林城市职业技术学院是教育部批准的民办普通高等职业学校，学校主校区地址：吉林省长春市宽城区凯旋北路7666号。学校强势专业主要是数控技术、数控设备应用与维护、汽车检测与维修技术、汽车技术服务与营销等按教育部要求，实行学校负责、招办监督体制，坚持德、智、体全面考核，以公开、公平、公正为原则，以普通高校招生统一考试成绩为基本依据，从高分到低分择优录取。录取过程中，院校志愿先录取第一志愿报考我校的考生，第一志愿录不满再依次录取第二、第三志愿考生；专业志愿先按第一专业志愿从高分到低分志愿录取；两名考生总',40369);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东信息工程职业学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://121.10.238.88','广东信息工程职业学院国标代码：14427，地处广东省肇庆（大旺）高新技术产业开发区，是一所全日制民办普通高等学校。按国家招生管理规定录取并取得我校正式学籍的学生，在校期间完成教学计划规定的理论和实践教学环节,符合毕（结）业条件者,颁发相应学历证书，并报广东省教育厅电子注册。',57215);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆幼儿师范高等专科学校',' ','重庆',0,4,0,0,0,' ','暂空',' ','http://www.cqpec.com','重庆幼儿师范高等专科学校是2012年经教育部、重庆市人民政府批准建立的重庆唯一一所以培养培训学前教育师资为主的公办全日制普通高等专科学校。学校是一所具有光荣革命传统的历史名校，其前身是创建于1914年的万县师范学校和创建于1960年的万县幼儿师范学校。肖楚女、恽代英、朱德、刘伯承、何其芳等曾在此工作和讲学，刘伯坚、陈伯钧等一大批仁人志士从此走上革命道路。长期以来，学校秉承百年的师范办学优良传统，坚持“励志、笃学、仁爱、敏行”的办学理念，专业建设独具特色，培养目标清晰明确，逐步形成了以“三种素质、',105280);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北中医学院',' ','河北',1,3,0,0,0,' ','暂空',' ',' http://www.hebcm.edu.cn/','河北中医学院始建于1958年，是全国建校较早的高等中医药院校之一，河北省重点骨干大学，河北省人民政府与国家中医药管理局共建高校，教育部卓越医生(中医)教育培养计划改革试点高校。建校50多年来，培养了包括中国工程院院士在内的2万余名高级中医药人才，为中医药事业发展作出了重要贡献。学校坐落在河北省会石家庄市，拥有杏苑和橘泉两个校区，总占地面积37.06万平方米，建筑面积16.8万平方米。杏苑校区地处被誉为“省会西花园”的鹿泉区杏苑路，环境优雅，风景宜人;橘泉校区位于桥西区新石南路，与石家庄火车站毗邻',327857);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西传媒学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.arft.net/','学院简介山西传媒学院前身是1983年由原广播电视部建立的华北广播电视学校，1990年经教育部批准，在华北广播电视学校基础上成立广播电影电视部管理干部学院，是全国仅有的独立设置的广播影视类成人高等院校，2013年4月经教育部批准，在其基础上建立山西传媒学院，成为全国第三所独立设置的公办传媒类本科院校。学校现有文华和东华两个校区。文华校区位于山西高校新校区文华街，东华校区位于太原市五龙口街，校园占地面积共940余亩，校舍建筑面积28万平方米。现有教职员工近500人，其中专任教师400余人，有全国“十',360743);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('营口理工学院',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.yku.edu.cn/','营口理工学院是经教育部批准成立的普通本科高等院校,实行省、市共建，以省为主的管理体制。学校位于风景秀丽、气候宜人的渤海之滨辽宁省营口市。“高水平、有特色”是营口理工学院的总体办学思路。营口理工学院秉持开放包容、敢为人先之精神，海纳百川、兼容并蓄之理念，锲而不舍、扎实勤勉之态度，建构优秀的组织理念、研究理念和教学理念；坚持教育质量是高等学校生命线的原则，强化质量立校意识，以质量创品牌，努力提高办学水平；紧密结合经济社会发展所急需和就业最具竞争力的专业，积极开展特色办学、科学研究和技术创新。营口理工',130850);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏第二师范学院',' ','江苏',1,12,0,0,0,' ','暂空',' ','http://www.jsie.edu.cn ','江苏第二师范学院坐落于人文荟萃、素有“六朝古都”之称的历史文化名城南京，前身为创建于1952年的江苏教育学院，是新中国成立后江苏省较早设置的17所省属公办本科学校之一。2013年，经教育部批准，学院由省属成人本科学校改制为省属普通本科学校。学院现有草场门、浦口、小行3个校区，占地面积524.32亩。设有13个教学单位，开设22个本科专业、13个专科专业，覆盖教育学、文学、理学、管理学、艺术学等主要学科门类。现有全日制在校生8300余人，成教学生10006人。学院致力于打造一支教育理念先进、学术水',85159);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌师范学院',' ','江西',1,4,0,0,0,' ','暂空',' ','http://www.jxie.edu.cn/','南昌师范学院是一所省属普通高等师范本科院校，2013年由江西教育学院改制而成。该校是江西省最早举办的八所本科高校之一，具有教师教育职前职后一体化的办学特色。其前身是成立于1952年的江西省中等师资进修学校，1956年更名为南昌师范专科学校，1958年创办江西教育学院，江西省第一任省长邵式平为学院亲笔题写了校名。学院学习环境优美、教学风气优良、学术氛围浓郁、文化品位高雅，现有青山湖、昌北两个校区，各类在籍学生近2万人。61年来，为社会培养、培训各类专门人才16万余人，其中大多数已成为教育、经营、管',141805);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东管理学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.sdmu.edu.cn/','山东管理学院是一所特色鲜明的省属普通本科高校，创建于1938年。学校坐落于泉城济南，两个校区分别位于历城区和长清区，占地面积1103亩，建有功能齐全的图书馆，拥有先进的工程中心、会计模拟实训中心、同声传译实训室等56个校内实验实训室及大学生就业创业孵化基地。学校在风景秀丽的五峰山风景区建有数千亩的教学实践基地。学校拥有一支师德高尚、业务精湛的师资队伍，具有副高级以上专业技术职务的占35%以上，博士、硕士达60%以上。学校依托自身优势，建有一支以专家学者、劳动模范、技术能手为代表的兼职教师队伍。学',501421);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东省农业管理干部学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdngy.edu.cn','山东省农业管理干部学院是经国家教育部批准，由山东省人民政府举办的具有普通高等学历教育招生资格的国办高等院校。学院主校区坐落在美丽的泉城济南。学院前身是1971年6月组建的山东省“五七”干部学校，后改称山东省省级机关“五七”干部学校、山东省农林干部学校。1983年10月，经山东省人民政府批准，原国家教委备案，改建为山东省农业管理干部学院。建院四十多年来，为山东经济社会发展培养了大批高素质人才。学院设有食品科学与工程系、机械电子工程系、信息科学与工程系、国土资源与测绘工程系、农业科学与工程系、园林系',184583);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东农业工程学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.sdngy.edu.cn/','山东农业工程学院是国家教育部批准设立的普通本科高等院校,目前承办普通本科教育、普通专科教育、成人高等教育、县处级领导干部岗位培训、农业科技人员继续教育和新型农民培训。前身是1971年6月组建的山东省“五七”干部学校，1976年1月改称山东省省级机关“五七”干部学校，1979年4月改为山东省农林干部学校，1983年10月，改建为山东省农业管理干部学院，2013年4月改建为山东农业工程学院。校园占地面积2671亩，建筑面积27万平方米；现有专任教师493人，副高级以上职称148人，博士、硕士学位教师',489593);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州理工学院',' ','贵州',1,1,0,0,0,' ','暂空',' ','http://www.git.edu.cn/','贵州理工学院是应省委、省政府实施工业强省战略和城镇化带动战略对理工类应用型人才之需，经教育部批准设立的一所省属本科理工院校。学校本着“高起点、开放式”的办学理念，按照“夯实基础、提升能力、保障质量、培育特色”的思路，走“特色创校、质量立校、科研兴校、人才强校”的内涵式发展道路，紧密结合贵州省的发展战略和产业需要，突出知识与技能并重，把培养强责任、精技术、善管理、重实践、求创新的高素质应用人才作为学院的立校之本;把服务贵州省经济社会发展的能力提升作为强校之基。学校紧紧围绕贵州省实施主基调、主战略的',313759);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('张家口学院',' ','河北',1,4,0,0,0,' ','暂空',' ','http://www.zjku.edu.cn/','张家口学院是经国家教育部批准设立的一所以教育学、医学和管理学为主干学科，文学、理学等学科相互支撑、协调发展的教学应用型本科院校。学校校园占地613.4亩，规划占地1168亩，现有校舍总面积22万平方米。校园依山构建，绿荫叠翠，楼台水榭，错落有致，与雄关美景交相辉映，相得益彰。拥有附属小学、附属幼儿园和附属人民医院各1所;拥有固定实习学校(幼儿园)68所、实习医院20所;拥有财经类实习基地15个、其他类实习实训基地47个;图书馆纸质藏书76.7万册，各类中外报纸期刊734种，电子图书7914GB，',280444);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('保定幼儿师范高等专科学校',' ','河北',0,4,0,0,0,' ','暂空',' ','http://www.bdysgz.com/','保定幼儿师范高等专科学校（原保定师专涿州分校），是经河北省政府批准隶属于保定市人民政府的全日制公办学校，由原河北定兴幼儿师范和涿州师范学校合并升格而成。学校新校区位于涿州市桃园办事处光明路303号（大马村西），毗邻京津，区位优势明显，交通便利。多年来，学校实行精细化管理，努力构建和谐校园，曾连续荣获“保定市实绩突出单位”称号。优美的学习环境、深厚的艺术底蕴和高水平的师资队伍，为河北省教育事业的发展做出了重要贡献。学校开设学前教育专业始于1984年，已有近三十年的发展历程，培养了一批又一批的优秀幼',31627);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古民族幼儿师范高等专科学校',' ','内蒙古',0,12,0,0,0,' ','暂空',' ','-','内蒙古民族幼儿师范高等专科学校的前身是1980年成立的伊克昭盟蒙古族师范学校，1989年经内蒙古自治区人民政府批准改建为内蒙古民族幼儿师范学校，并开始面向全自治区招生。2000年与伊克昭盟艺术学校合并，挂“内蒙古民族幼儿师范学校”和“鄂尔多斯民族艺术学校”两个牌子，学校总名称为“内蒙古民族幼儿师范艺术学校”，包括了幼儿师范和艺术教育两大类。2011年3月整体搬迁到东胜区铁西科技教育园区，成为内蒙古地区基础条件一流的中等师范学校。2012年11月内蒙古民族幼儿师范学校升格为内蒙古民族幼儿师范高等专',2539);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南医学高等专科学校',' ','河南',0,3,0,0,0,' ','暂空',' ','http://www.hamc.edu.cn/','河南医学高等专科学校是在河南卫生职工学院的基础上，经河南省人民政府批准设置的一所医学普通专科学校，2013年5月正式通过教育部备案。学校始建于1952年，前身系河南省卫生干部学校，承担全省医疗卫生人员培训和普通中等医学教育;1984年升格为河南卫生职工学院，1993年增挂河南职工医学院牌子，承担成人专科医学教育;2002年经河南省教育厅同意开始招收普通专科学生，承担普通专科医学教育任务。学校是河南医学高等教育资源的重要组成部分。有南北两个校区，南校区位于郑州市南龙湖宜居教育园区;北校区位于郑州市',721924);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南幼儿师范高等专科学校',' ','湖南',0,12,0,0,0,' ','暂空',' ','http://www.cdgdsf.com/','湖南幼儿师范高等专科学校是2013年经省人民政府批准、教育部备案的全日制公办普通高等学校。学校由原常德师范和桃源师范学校合并升格而成，目前新校区正在市文化教育科技园区建设。原桃源师范由著名民主革命家宋教仁于1912年倡导创立，已历百年;原常德师范始创于1947年。学校是国家教委表彰的全国先进师范、全国幼教舞蹈素质与能力培训项目优秀培训基地、湖南省小学和幼儿园教师培养基地、湖南省教育科学学前教育研究基地、全国职业教育先进单位、省级文明单位。学校和日本琵琶湖学院大学是友好学校。百年来，学校为社会输送',56106);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北工艺美术职业学院',' ','河北',0,12,0,0,0,' ','暂空',' ','http://www.1964.cn/','河北工艺美术职业学院简称河北工艺美院，是河北省唯一一所旨在培养工艺美术设计专业人才的、公办全日制高等专科艺术院校。2013年2月，经河北省人民政府批准，国家教育部备案，将河北大学工艺美术学院设立为独立建制的河北工艺美术职业学院。学院前身是成立于1964年的河北工艺美术学校。学院座落于历史文化名城保定，历史悠久，文化底蕴深厚。校园占地231.8亩，总建筑面积达8.53万平方米。拥有“河北省工艺美术协会”、“武强年画工作室”、“内画艺术工作室”、美术馆等多个教学科研机构。校内实训车间2000平米，校',20777);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('渤海理工职业学院',' ','河北',0,12,0,0,0,' ','暂空',' ','http://www.bhlgxy.cn/','渤海理工职业学院学院代码：14472学院概况渤海理工职业学院是经河北省人民政府批准、国家教育部备案，由河北省教育厅直属的全日制普通高等院校。学院位于“环渤海、环京津”美丽的海滨城市黄骅大学城，占地500亩，规划建筑面积17.3万平方米。学院坚持以学生为本的办学理念和诚信办学的原则，突出教育公益性、尊重教育规律，并以“实践、实效、实操”为立足点，坚持以就业为导向，实行校内实训和校外实习相结合，通过工学结合、校企合作、顶岗实习、订单培养、五证教育等方式，把学生培养成为现代化综合性技术人才。专业设置学',20061);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏商贸职业学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.jsbc.edu.cn/','江苏商贸职业学院创办于1956年，是经江苏省人民政府批准、教育部备案的公办全日制普通高等学校。学院坐落在被誉为“中国近代第一城”的江苏省南通市，处于上海1小时经济圈内，地处南通市北翼新城核心地带，交通便捷，区位优势突出。学院占地面积502亩，规划总建筑面积32万平方米，建有物流、商贸、计算机、会计、电子等五大实训中心，共90个实训室，总面积达8.1万平方米，教学设备总值约6000万元。设有财会金融、经济贸易、信息技术、电商物流、建筑工程与管理、艺术设计等六大专业集群近30个高职专业和方向，现有全',25158);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('菏泽职业学院',' ','山东',0,12,0,0,0,' ','暂空',' ','http://www.hzzyxy.com/','菏泽职业学院是经山东省人民政府批准成立、国家教育部备案的一所公办全日制普通高等教育学校。学院位于美丽的牡丹之都―山东省菏泽市。校园占地千余亩，建筑面积31万平方米。由世博会中国馆总设计师何镜堂院士主持设计，采用新徽派风格，实现了典雅与时尚、环保与节能、人文与自然的完美结合。校园位于城北万亩牡丹种植区，与曹州牡丹园、古今园、百花园相毗邻，牡丹花开时节，香飘十里，游人如织;万花湖犹如一颗璀璨的明珠镶嵌在校园中央，熠熠生辉。优美的环境，实为莘莘学子学习、生活绝佳之处。学院目前下设5个系和国际学术交流中',28232);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南阳农业职业学院',' ','河南',0,12,1,0,0,' ','暂空',' ','oldname": "南阳农业职业学院','南阳农业职业学院是经河南省人民政府批准、教育部备案的一所公办全日制普通高等院校。学院由建校62年的南阳农校发展而来，位于物华天宝、人杰地灵的国家历史文化名城—南阳市，紧邻驰名中外的卧龙岗诸葛武侯祠景区，楚、汉文化的飘逸与雄浑为学院发展奠定了丰厚的文化底蕴。学院规划占地600余亩，建筑面积25万平方米，图书馆馆藏书40万册，教职工400余人。学院教学设施齐全，服务功能完善，师资力量雄厚，现已形成一支由3名省级教育专家、42名省市级学科带头人和省市级骨干教师组成的专家队伍，为提高教学质量，培养管理型',225);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('洛阳科技职业学院',' ','河南',0,0,0,0,0,' ','暂空',' ','http://www.lykjxy.cn/','洛阳科技职业学院是经河南省人民政府批准，国家教育部备案[国标代码：14480]，具有独立颁发国家高等教育学历证书资格、纳入全国统一招生计划的全日制普通高等职业院校。地处十三朝古都洛阳，周边邙山涧水相邻，工厂企业林立，环境优美宜人。长期以来学院坚持“以社会需求为导向，以质量求生存，以特色求发展”的办学理念，已为社会培养输送了6万余名人才。荣誉称号学院在多年办学实践中，走出了一条符合自身实际的特色发展之路，取得了显著的办学成绩，得到了政府和社会各界的广泛赞誉。学院曾荣获“省重点中专”、“全国职业培训',56796);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西培贤国际职业学院',' ','广西',0,12,1,0,0,' ','暂空',' ','http://www.peixianedu.cn/','广西培贤国际职业学院简介广西培贤国际职业学院是一所经广西壮族自治区人民政府批准成立，国家教育部备案的全日制普通高等职业学院。学院是由香港人士何厚煌博士和区寿本博士依托英、美丰富的教育资源创办起来的，创办人同时也是广东培正学院的创办人和董事。创办人决心借鉴已有的办学经验，切实投资，以高起点、高标准创办了一所独具特色的国际化品牌大学。一、基本概况学院占地面积800亩，是一所“中西合璧、现代典雅、科学合理、环境优美”的新校园，已建成教学楼、学生宿舍楼、教师公寓楼、综合服务楼、四星级酒店、学生就业创业大',2753);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆文化艺术职业学院',' ','重庆',0,0,0,0,0,' ','暂空',' ','http://www.cqyixiao.com/','重庆文化艺术职业学院是重庆市唯一公办综合性高等职业艺术院校，隶属于重庆文化委员会，以重庆艺术学校为办学基础，曾培育出三届梅花奖得主、中国剧协副主席沈鉄梅及蒋勤勤、殷桃、吕一等知名艺术家和影视明星，办学经验丰富，被誉为“艺术家的摇篮”。学院占地约500亩，办学地理位置优越，江北校区位于繁华的观音桥商圈，交通便利、设施齐全;巴南校区紧邻巴南区政府，“生态、开放、融合、智慧”的校园环境，齐备的实习实训基地和专业教学设施，为助力学生成长成才提供了全面的基础保障。学院拥有一支德高艺强的师资队伍，现有教师3',76236);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('巴中职业技术学院',' ','四川',0,12,0,0,0,' ','暂空',' ','oldname": "巴中职业技术学院','学院代码：14483巴中职业技术学院是巴中第一所高校，于2013年2月经四川省人民政府批准，教育部备案，由巴中艾林实业有限公司主办，四川省教育厅主管，以实施专科层次高等职业教育为主的全日制民办普通高校。学院位于成都、重庆、西安三大中心城市的几何中心和成渝经济区、关天经济区中间区域的巴中市。学院坐落于巴中经济开发区，占地500亩，总建筑面积24.36万平方米，总投资7.6亿元，教学楼、实验楼、艺术楼、图书馆、学生公寓、运动场等教学和学生生活设施齐全。学院坚持“立足巴中，辐射川渝陕，服务区域经济”的',33877);
