insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川电子机械职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.scemvtc.com','四川电子机械职业技术学院是经四川省人民政府批准，国家教育部备案，以实施专科层次高等职业教育为主的普通高等院校（学院代码：14485，学院招生代码：5653）。学院位于以“李白出生地，中国科技城”而闻名的四川省第二大城市绵阳。绵阳是中国唯一科技城，全国首批“三网融合”试点城市。先后荣获联合国改善人居环境最佳范例城市、全国文明城市、国家卫生城市等多项殊荣。绵阳拥有众多的国家级科研院所、高等院校、国家重点实验室、国家级工程技术研究中心，有两院院士二十六名，智力资源密集度和科教实力在西部中等城市中首屈一',56682);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大理农林职业技术学院',' ','云南',0,12,0,0,0,' ','暂空',' ','http://www.dlvtcaf.cn/','大理农林职业技术学院座落于享誉中外的国家历史文化名城---大理古城，集国家级历史文化名城、国家级风景名胜区、国家级自然保护区、中国优秀旅游城市、最佳中国魅力城市、国家地质公园、中国十佳旅游休闲城市、中国最佳休闲旅游目的地、“亚洲文化十字路口古都”等桂冠于一身，历史文化灿烂，人文氛围厚重。学院西靠点苍山，东眺碧波荡漾的洱海，北临崇圣寺三塔。气候宜人，环境优美，是教书育人，工作学习生活的理想之地。2013年经省政府批准组建，教育部备案并批准招生。学院占地面积213.68亩，拥有全新的基础设施和实验实',19604);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西艺术职业学院',' ','陕西',0,8,0,0,0,' ','暂空',' ','http://www.sxavc.com','陕西艺术职业学院是经陕西省人民政府批准，教育部备案的一所公办全日制高等艺术院校。学院坐落在历史文化名城西安，学院占地100多亩，校舍面积66000平米，在校生2000多人，馆藏图书23.6万册，学生公寓、食堂、运动场、计算机房等设施齐全。现有教职工280多名，专任教师154名，其中高级职称46人，博士、硕士47人，“双师型”教师87人。学院设戏曲系、舞蹈系、影视系、声乐系、器乐系、美术系、文化课教学部、高星级饭店运营与管理专业教研室等9个教学单位。下设戏曲表演、舞蹈表演、声乐表演、器乐表演、影视',28222);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆铁道职业技术学院',' ','新疆',0,1,0,0,0,' ','暂空',' ','http://www.xjtzy.net/','新疆铁道职业技术学院是自治区人民政府批准、教育部备案的全日制普通高等职业学校，由自治区人民政府举办，自治区人力资源和社会保障厅管理，自治区教育厅业务指导。学院位于乌鲁木齐市新市区北京中路233号，哈密校区位于哈密西郊火石泉。学院环境优美、交通便利、教育教学设施完备、师资力量雄厚，多年来为乌鲁木齐铁路局等大型企业输送了大量高素质技能型人才。学院师资力量雄厚，拥有一支业务精湛、师德高尚的教师队伍。现有专任教师213人，专任教师中，有研究生学历或学位的教师39人，占专任教师总数的18.3%，本科以上学',100338);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆服装工程职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqfzgc.com/','重庆工程职业技术学院是由重庆市人民政府举办，重庆市教育委员会主管的公办全日制普通高等院校。学院1951年由煤炭工业部创办，1998年举办高等职业教育，2001年成为独立设置的高等职业院校。2005年接受教育部高职高专人才培养工作水平评估，荣获优秀等级。2007年入选国家示范性高等职业院校建设计划，2010年通过教育部、财政部验收，成为国家示范性高等职业院校。学院现有沙坪坝校区、巴南校区、江津校区三个校区，占地面积1498亩，建筑面积21.26万平方米（江津校区建筑面积48万平方米校舍在建）。固定',17657);
