insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川护理职业学院',' ','四川',0,12,0,0,0,' ','暂空',' ','http://www.cnsnvc.com/','四川护理职业学院于2014年3月7日经四川省人民政府批准建立，2014年4月28日通过教育部备案。是由省人民政府主办，四川省卫生和计划生育委员会、四川省教育厅主管的专科层次公办普通高等学校。学院占地面积31.31万平方米，建筑面积11.68万平方米，其中教学行政用房8.22万平方米，有室内体育馆、400米标准塑胶跑道田径场、足球场、篮球场、排球场等体育设施。拥有一流的的实验实训基地，“护理实训中心”和“形态学实验中心”功能齐备、设施完善。学院现有教职员工347人，其中专任教师246人。四川护理职',17012);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川西南航空职业学院',' ','四川',0,12,0,0,0,' ','暂空',' ','http://www.xnhkxy.edu.cn','国标代码：14515四川招生代码：5658五年制招生代码：51274学院性质：全日制统招航空类高职院校开设专业：空中乘务、民航空中安全保卫、直升机驾驶技术、定翼机驾驶技术、民航安全技术管理、飞行器制造技术、无人机应用技术、飞机机电设备维修、飞机电子设备维修、航空物流、民航运输、高速铁路客运乘务、国际邮轮乘务管理、工业设计、服装陈列与展示设计、服装与服饰设计。招生对象：三年制：参加当年全国普通高考的应、往届高中毕业生五年制：参加中考的四川省应届初中毕业生(含中职三年、高职两年)四川西南航空职业学院',31762);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州科技职业学院',' ','甘肃',0,12,0,0,0,' ','暂空',' ','http://www.lzust.com/cn/index.html','兰州科技职业学院是在原兰州信息工程中等专业学校的基础上经甘肃省人民政府批准成立的一所全日制普通高等职业院校。学院始建于1996年。建校以来，学院秉承“以服务社会主义现代化建设为宗旨，以促进劳动力就业为导向，以培养社会实用性人才为目标，努力为甘肃和西部地区的经济发展做贡献”的办学方针。共计为经济管理、医疗卫生、信息技术和机械加工等行业培养输送了万名应用型技能人才。学院坐落于兰州定远东部科技新城，东临陇右第一名山——兴隆山，西接和平高等教育园区，交通便利、信息通畅、校园环境幽雅、景色宜人。学院占地面',5612);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古艺术学院',' ','内蒙古',0,8,0,0,0,' ','暂空',' ','http://zs.imac.edu.cn','内蒙古艺术学院是内蒙古自治区唯一一所独立设置的本科高等艺术学府，其前身是创建于1957年的内蒙古艺术学校，上世纪80年代成为国家文化部确定的全国重点中专，是自治区培养民族艺术人才的重要基地。1987年3月，国家教委批准，内蒙古艺术学校并入内蒙古大学，成立内蒙古大学艺术学院。2015年5月，教育部批准，在内蒙古大学艺术学院基础上独立设置内蒙古艺术学院，属全日制本科院校。近年来，学院秉承“彰显特色、强化优势、多元互动、和谐发展”的办学理念，以建设“教学、科研、创作、表演四位一体，服务、交流功能并举，',762);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('扎兰屯职业学院',' ','内蒙古',0,12,1,0,0,' ','暂空',' ','-','扎兰屯职业学院是经内蒙古自治区人民政府批准、教育部核准备案，整合内蒙古扎兰屯林业学校、内蒙古扎兰屯农牧学校、扎兰屯幼儿师范学校、呼伦贝尔市卫生学校资源创建的一所以农牧林业、学前教育、医学护理为主，兼有文、经、管、工等学科的公办全日制普通高等学校。扎兰屯职业学院一院四址，包括秀水校区、明月校区、吊桥校区、雅鲁校区，校园占地面积1000亩。现有林业实训基地7136亩，农业实训基地1590亩，标准化幼儿园一所，幼儿教师职后培训中心一处，自治区二类驾校一所;设有现代化日光温室、组织培养实验室、宠物及兽医',117);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江冰雪体育职业学院',' ','黑龙江',0,7,1,0,0,' ','暂空',' ','http://www.hljviws.com/','黑龙江冰雪体育职业学院是教育部备案、黑龙江省人民政府批准、黑龙江省体育局主管，全国首家以冰雪体育为主要特色的公办高等专科职业院校。学院同时承担哈尔滨全民健身中心、省体育行业特有工种职业技能鉴定站、省一级社会体育指导员培训基地、省中等职业学校运动训练专业体育骨干教师培训基地、省中等职业学校毕业生专业对口升学专业技能考试基地。学院坐落于风景秀丽的松花江北岸(哈尔滨松花江避暑城内)，地处国家级湿地保护区、国家生态旅游示范区，总占地60万平方米，具备国内一流体育职业院校的硬件条件。学院依托省内得天独厚的',3997);
