insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆资源与环境保护职业学院',' ','重庆',0,1,0,0,0,' ','暂空',' ','http://www.cqzhxy.cn','重庆资源与环境保护职业学院是2015年经重庆市人民政府批准教育部备案的全日制普通高等院校。学院规划占地面积500余亩，办学规模8000人，位于中国石刻之乡、西部五金之都的重庆市大足区。学院现设环境工程系、环境科学系、循环经济与低碳经济系、生态环境系、环境艺术系、建设管理系和公共管理系等7个系。按照环境保护全过程管理的理念，拟开设从环境保护源头清洁生产到中间循环经济再到末端工程治理及其延伸专业全覆盖的专业体系。首期开设环境工程技术、环境评价与咨询服务、环境艺术设计、工程造价、房地产经营与管理、道路',2222);
