insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州卫生职业技术学院',' ','广东',0,3,0,0,0,' ','暂空',' ','http://www.gzws.edu.cn','广州卫生职业技术学院(原广州医科大学卫生职业技术学院)是由教育部批准设立的广州市唯一一家公办卫生类高等职业院校，办学历史最早可追溯到1902年。学院占地面积约638.6亩，校舍建筑面积约11.5万平方米，分白云、从化、天河、越秀四个校区。在长期的办学过程中，学院为社会培养培训了数以万计的高素质卫生技术人才，在各级各类医疗机构中作出了重要的贡献。学院现有全日制在校生约3500人，开设有护理、助产、药学、医学检验技术、康复治疗技术、老年服务与管理、医学影像技术、临床医学、口腔医学、针灸推拿等10个专',7522);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波诺丁汉大学',' ','浙江',1,0,0,0,0,' ','暂空',' ','http://www.nottingham.edu.cn','宁波诺丁汉大学是中国第一家经教育部批准引进世界百强优质高教资源的中外合作大学，颁发与英国诺丁汉大学相同的文凭。宁波诺丁汉大学地处宁波市高教园区，由宁波市人民政府主办，浙江万里学院与英国诺丁汉大学联办。宁波诺丁汉大学是中国第一家经教育部批准引进世界一流大学并具有独立法人资格、独立校园的中外合作大学。宁波诺丁汉大学的创办，使中国学生能以远低于海外的留学费用、不出国门就可以享受到世界一流大学的教育资源。',1558866);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京师范大学-香港浸会大学联合国际学院',' ','广东',0,12,0,0,0,' ','暂空',' ','http://uic.edu.hk/','北京师范大学-香港浸会大学联合国际学院，即BNU-HKBUUnitedInternationalCollege，简称UIC。2005年，UIC获教育部批准，由北京师范大学和香港浸会大学携手创立，建校于广东珠海，成为港陆合办第一校，也是中国第一所以博雅教育为理念的创新型大学。成立至今，UIC已迅速发展成为一所拥有独特的教育理念的国际化大学。学院设有工商管理、人文与社会科学和理工科技三个学部，下设二十个专业，在校生近四千七百人。学院拥有一支来自二十多个国家和地区的优秀师资队伍，境外教师占所有教师比例',1411615);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西交利物浦大学',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://www.xjtlu.edu.cn/zh/','西交利物浦大学（XJTLU，简称“西浦”）是经中国教育部批准，由西安交通大学和英国利物浦大学合作创立的，具有独立法人资格和鲜明特色的新型国际大学。她是中国目前唯一一所以理工管起步，强强合作，拥有中华人民共和国学士学位和英国利物浦大学学位授予权的中外合作大学。西交利物浦大学坐落在素有“人间天堂”之称的千年古城——中国苏州，今天的苏州不仅保留了水陆并行的古城风貌和崇文重教的文化传统，同时又是一个与时俱进、对世界开放的现代化城市。近年来它的国民经济总产值以近15%的年增长率快速发展，其GDP长期保持在',1276176);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海纽约大学',' ','上海',1,0,0,0,0,' ','暂空',' ','http://www.shanghai.nyu.edu','上海纽约大学是国家教育部正式批准的、具有独立法人资格和学位授予权的第一所中美合作举办的国际化大学，也是纽约大学全球教育体系的组成部分。上海纽约大学的建设目标是：成为一所世界级的，多元文化融合，文理工学科兼有的研究性大学，成为全球化进程中不同文化交流，教育合作的典范。上海纽约大学的愿景上海纽约大学致力于践行当代高等教育的最高理想，将纽约大学全球教育体系中丰富的智力资源融入灿烂的中国文化，带领学生获得学业与精神的双重收获，培养他们成为人生旅程的领导者，在无涯的学海中求索关于人与自然的新知卓见。学校的',164453);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港中文大学（深圳）',' ','广东',1,12,0,0,0,' ','暂空',' ','http://www.cuhk.edu.cn','香港中文大学（深圳）是由香港中文大学与深圳大学，根据《中外合作办学条例》而组建的从事高等教育研究的机构。香港中文大学（深圳）于2014年3月获教育部批准，在经济特区--深圳市成立。学校定位于“立足中国、面向世界的一流研究型大学”，将秉承香港中文大学的办学理念和学术传统，为国家和地区培养新时代创新型高层次人才。学校拥有一流的师资队伍，注重国际化的教学环境和国际交流，采用香港中文大学学术质量控制和学位颁授标准，颁发香港中文大学学位证书。香港中文大学（深圳）实行理事会领导下的校长负责制。民主管理学校，',8634);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京民族大学',' ','北京',1,0,0,0,0,' ','暂空',' ','http://www.bjmzdx.org/','◆历史篇1984年，由全国著名高等学府和教育单位的一批优秀教授、学者，以“育人报国”为宗旨，以“厚基础、宽技能、重德育、强素质”为办学理念，在改革开放时期，教育大改革的浪潮中毅然创办“北京民族大学”。清皇室爱新觉罗·溥杰先生为学校题写“北京民族大学”校牌。1994年，时任全国人大副委员长布赫为学校题写了“团结、文明、勤奋、创新”的校训。1998年被国家民委和北京市人民政府评为“民族团结进步模范单位”。2007年被北京市人民政府评为“首都绿化美化花园式单位”。2011年学校站在教育现代化，教育信息',449275);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古科技大学包头师范学院',' ','内蒙古',1,4,0,0,0,' ','暂空',' ','http://www.bttc.cn','包头师范学院是2000年3月经教育部批准，由原包头师范高等专科学校、包头师范学校、包头教育学院三校合并组建的普通高等师范院校。原包头师范高等专科学校初名“包头师范专科学校”，创建于1958年，1963年停办，1971年恢复办学，1996年更名为“包头师范高等专科学校”。原包头师范学校创建于1947年。原包头教育学院创建于1978年。2003年6月，包头师范学院与包头钢铁学院、包头医学院三校合并组建内蒙古科技大学。2004年底，经自治区人民政府批准，以内蒙古科技大学包头师范学院校名恢复为具有独立法',466870);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京科技大学延庆分校',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.ustbyq.com/','北京科技大学延庆分校是根据市政府京政发[1984]97号文件由北京科技大学与延庆县政府于1984年联合创办，是北京市公办全日制普通高等职业院校。自创办以来，北京科技大学延庆分校走出了一条依托北京科技大学教育资源优势，面向社会需求，结合学校特点，服务区域经济的特色办学之路。学校牢固树立“人才培养是学校的根本任务、质量是学校的生命线、教学是学校的中心工作”的理念，遵循高等职业教育人才培养规律，积极探索和改革高职学生的培养模式。一直以来，学校的办学质量和声誉均得到学生、家长和社会的认可。学校位于举世闻',25738);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古科技大学包头医学院',' ','内蒙古',1,3,0,0,0,' ','暂空',' ','http://www.btmc.cn/','包头医学院创建于1958年，是新中国在少数民族地区较早建立的一所普通高等医学本科院校。学校自1980年开始招收硕士研究生，1985年获得硕士学位授权资格单位。2008年接受教育部本科教学工作水平评估结果为优秀。包头医学院坐落在世界闻名的稀土之都、首批全国文明城市、美丽的草原钢城——包头市。占地面积1000余亩，建筑面积18万平方米。有2.5万平方米的主教学楼，4万平方米的实验楼，有2万多平方米的图书馆，馆藏图书70余万册。有1万多平方米的体育馆及近5万平方米的室外运动场。包头医学院的学科以医学为',752686);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东北大学秦皇岛分校',' ','河北',1,0,1,0,0,' ','暂空',' ','http://zs.neuq.edu.cn ','东北大学秦皇岛分校是经教育部正式批准成立，在东北大学统一规划下，面向全国招生，相对独立办学的普通高等学校。学校始建于1987年，是东北大学的有机组成部分。1996年，正式进入首批\"211工程\"重点建设行列，承担东北大学\"211工程\"建设子项目。1998年，随东北大学划入教育部。2001年，经教育部批准，在继续面向全国独立招生的基础上，电子注册和学生毕业统一归口东北大学。2006年，开始承担东北大学\"985工程\"建设子项目。学校位于奥运协办城市之一的秦皇岛市，校园北倚燕山、南临渤海；学校占地总面积',2762860);
