insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东大学（威海）',' ','山东',1,0,1,1,1,' ','暂空',' ','http://www.wh.sdu.edu.cn','山东大学创建于1901年，是一所历史悠久、学科齐全、学术实力雄厚、办学特色鲜明，在国内外具有重要影响的教育部直属重点综合性大学，是国家“211工程”和“985工程”重点建设的高水平大学之一。百余年间，山东大学秉承“为天下储人才、为国家图富强”的办学宗旨，踔厉奋发，薪火相传，为国家和社会培养了40多万各类人才，为国家和区域经济社会发展做出了重要贡献。山东大学(威海)创建于1984年，是山东大学与威海市政府联合共建的多学科、开放式、综合性大学校区。她的诞生开创了我国名校异地办学的先河;而她的每一步成',3543575);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西南大学荣昌校区',' ','重庆',1,0,0,0,0,' ','暂空',' ','http://www.rcswu.cq.cn/','西南大学荣昌校区，前身为始建于1938年的建华高级农业职业学校，抗战时期为国民政府中央畜牧实验所、农业实验所和血清研究所所在地，解放后为国内著名的四川省荣昌畜牧兽医学校，1978年经国务院批准升建为四川畜牧兽医学院。2001年经教育部批准，与原西南农业大学、中国农科院柑桔研究所合并组建新的西南农业大学，设立荣昌校区。2005年7月，西南农业大学与西南师范大学合并组建西南大学，校区更名为西南大学荣昌校区。校区位于“中国畜牧科技城”、重庆国家现代畜牧业示范区核心区、国家卫生城市、国家文明县城、国家园',60521);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西航天职工大学',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.htzd.org/','陕西航天职工大学是经国家教育部批准成立，陕西省教育厅主管，隶属于中国航天科技集团的一所独立设置的公办高等院校。学校位于古城西安南郊航天城(中国航天科技集团总公司第六研究院)，南邻古长安文化风景区，北接高校林立的西安文化区。在航天高科技气氛的熏陶下，人杰地灵，钟灵毓秀。学校拥有完善的教学设施，拥有教学楼、图书馆、学生公寓计算机中心、各类实验室互联网电子阅览室、多功能教室、多媒体语音室、运动场、礼堂及服务设施。并拥有全国计算机等级考试辅导站，国家级航天职业技能鉴定站。学校在长期的办学发展中聚集了一批',46236);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北地质职工大学',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.dkyaolan.com','河北地质职工大学成立于1979年，地处石家庄市中山西路，地理位置优越，交通便利。校区有设施完善的教学楼、学生宿舍楼、图书阅览室、计算机室、多媒体教室、语音室、学生餐厅等，校园环境整洁幽雅、安静秀丽，是学习深造的最佳场所。学校师资力量雄厚，现有副高级职称以上教师42名。学校还从其他高等院校、科研机构、生产单位聘请了一批领导、专家、学者作为本校的兼职教授，组成了具有一定规模的专兼职教师队伍。学校设有地质系、工程勘查系、经济管理系等三个教学系。学生在校期间组织参加全国计算机等级考试、英语A、B级考试及',29514);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西职工医学院',' ','山西',0,3,0,0,0,' ','暂空',' ','http://www.sxzgyxy.cn','山西职工医学院新校区位于晋中市高校新区文津街100号，其前身是1953年8月成立的山西省卫生厅卫生人员训练班（简称卫训班），1981年省政府批准成为山西医药卫生职工高等专科学校，1986年更名为山西职工医学院，与山西省中医学校是两块牌子、一套机构。1993年经国家教委批准，举办成人专升本教育；2000年经山西省教育厅批准，开始举办高职高专教育。2013年10月学院搬迁至山西高校园区新区，位于高校新校区北侧，东临山西煤炭学院，西接太原理工大学，南连山西中医学院，新校区的建成，在学院教育发展史上具有',147601);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江农垦管理干部学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.nkgy.com/','黑龙江农垦管理干部学院是黑龙江省农垦总局党校、黑龙江农垦教师进修学院、黑龙江省农垦广播电视大学、黑龙江垦区农业广播电视学校、黑龙江农垦管理干部学院五校合署办学，隶属于黑龙江农垦总局。学院位于哈尔滨市南岗区黄河路184号，邮编：150090，办公室电话：82353621、55196188，党委书记于纪健，院长逄金明。总局党校的前身是1972年黑龙江省国营农场局在哈尔滨市香坊区建立的黑龙江省国营农场干部学校。1976年，黑龙江生产建设兵团撤销，成立黑龙江省国营农场总局，农场干部学校隶属于省农场总局并',10200);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波教育学院',' ','浙江',0,4,0,0,0,' ','暂空',' ','http://www.nbei.net','宁波教育学院是一所由国家教育部批准建立的、具有50余年办学历史的公办高等院校。学校坐落在宁波市区，位于美丽的三江文化长廊之畔，占地面积130余亩，建筑面积7万余平方米。校园精致幽雅，设施精良，交通便捷。学校坚持为基础教育和经济社会发展服务的办学指导思想，积极举办普通高等职业教育和成人高等教育，同时承担宁波市教育行政干部培训中心、宁波市中小学教师培训中心和宁波市幼儿教师培训中心的职能，形成了具有本校特色的“一校三中心”的办学格局。学校设有人文与幼教、外语与经贸、信息与艺术、成人教育、文理等5个二级',58974);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽经济管理干部学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.ahsa.edu.cn/','安徽经济管理干部学院与安徽行政学院是一个机构、两块牌子，为安徽省政府直属单位。自1983年承担专科学历教育及对外合作办学任务以来，紧紧围绕省委、省政府中心工作，服务全省经济社会发展大局，积极开展教学、科研、国际交流合作等工作，为建设我省高素质的干部队伍作出了重要贡献，为国家经济社会发展输送了数以万计的优秀人才，被誉为“江淮之滨企业家的摇篮”、“公务员的摇篮”。学院践行“忠诚融和，创新卓越”的精神，秉承“明志、求是、创新、笃行”的校训，着力培养“经管类、地方性、应用型”人才。现设有社会与公共管理系',302531);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('第一拖拉机制造厂拖拉机学院',' ','河南',0,1,0,0,0,' ','暂空',' ','http://www.ytzjw.com','本校是2002年经国家教育部批准备案的具有普通专科层次招生资格的成人高等学校和国家教育部指定的高等职业技术教育试点学校，创办于1956年，占地150余亩，建筑面积25万平方米。具有计算机、机械性能、数控技术、CAD等15个实验室和20余个机电实习车间，在一拖集团公司建有18个实习基地。图书馆图书20万册、报刊杂志300多种。拥有副教授、高级工程师42人，讲师、工程师等153人。先后被省机械电子厅、教育厅命名为“学校教育先进单位”和“成人高等教育先进学校”，在全国成人高校评估中被省教育厅评为“优秀',17789);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('平顶山教育学院',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.pdsjyxy.com/','平顶山教育学院是经国家教育部正式批准备案的具有普通高等专科教育和成人高等专科教育招生资格及师资培训的公办成人高等院校。2002年9月经省政府批准与平顶山师范学校合并组建了新的平顶山教育学院，经过教育资源的重组，办学规模进一步扩大，师资力量更加雄厚，教学设施更加完备。学院现有教职工291人，专业技术人员244人，任课教师230人，其中副教授职称以上的58人，中级职称100人，特级教师3人，国家级优秀人才1人，市级拔尖人才3人，获曾献梓基金奖励3人，跨世纪学科带头人1人。学院设立有中文系、外语系、教',16687);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('荆州教育学院',' ','湖北',0,4,0,0,0,' ','暂空',' ','http://www.jz100.net\n','荆州教育学院创建于1971年，是经湖北省人民政府批准，国家教育部备案的公办、全日制高等专科学校。学院位于荆州高新区，西望古城，南眺长江，北连沪汉蓉、荆岳广高速铁路，交通便利，设计先进，环境宜人。校园占地面积200余亩，校舍建筑面积10万平方米，按现代标准建设了教学楼、培训楼、国家标准运动场、计算机网络中心、多媒体教室、语言实验室、钢琴微格练习室、数码钢琴练习室、舞蹈训练室、绘画练习室等。建有专业实验工厂。院图书馆藏书27万余册。大型阅览室中的千余份国内外期刊报纸可供数百名师生同阅读。校园网实现学',23628);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('株洲市职工大学',' ','湖南',0,0,0,0,0,' ','暂空',' ','oldname": "株洲市职工大学','湖南省株洲市工业中等专业学校地处”唯楚有才，于斯为盛”的荆楚大地，坐落在中南工业重镇、铁路枢纽株洲市，濒临湘江，环境优美，是一所国有公办性质的全日制职业学校，是首批国家级重点职业学校、湖南省首批示范性职业学校、首批国家级中等职业教育改革发展示范性学校创建单位。2008年，被纳入株洲市职教科技园建设项目单位（规划学校占地面积约300亩），2009年被确定为株洲市职教科技园建设第一批入园学校。学校占地面积百余亩，建筑面积71036.9平方米，设备总价值为1867.1万元；有教职工222人，其中专任教',7751);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西教育学院',' ','广西',0,4,0,0,0,' ','暂空',' ','http://www.gxec.net.cn/','广西教育学院创建于1953年，迄今已有58年的办学历史，是自治区直属的唯一的成人本科高等院校，同时承担普通专科学历教育任务。校址位于广西壮族自治区首府——南宁市区中心。学院现有中文系、数学与计算机科学系、外语系、政治经济系、旅游与环境学系、信息科学与技术系、艺术系、文化传播学系、教育科学系、教育管理系等10个系，共36个普通专科专业（含专业方向）、25个成人本科专业、18个成人专科专业，分布在法学、教育学、文学、艺术学、历史学、管理学、理学、工学等8大学科门类。建立有人力资源、公共行政管理、信息',32979);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国工程物理研究院职工工学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.caepit.net/','中国工程物理研究院职工工学院（简称中物院工学院，以下称学院）创建于1978年，是经教育部批准，由我国唯一战略武器研制单位中国工程物理研究院（简称中物院）设立的成人高等院校。2004年以前主要从事成人高等教育，中国工程院院士俞大光先生曾任学院第二任院长，现为荣誉院长。2004年经教育部批准主要从事高等职业技术教育。学院还承担了中物院政策管理、专业技能、职业技术安全的培训，承担电工作业、金属焊接与热切割作业、危险化学品作业、制冷作业、起重机作业等8类特种作业培训和资质认证工作。学院现设有机电技术及自',28477);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安航空职工大学',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sfygxy.com/','西安航空职工大学是原中国航空工业总公司批准，国家教育部备案（教育部代码：50989），陕西省教育厅主管（院校代码：8107），由中国飞行试验研究院等在陕的六家大型航空骨干企事业单位承办的一所公办的具有高等学历教育资格的全日制高等学校。总校地址位于交通便利、环境幽雅的中国唯一的航空高科技产业基地——中国飞机城阎良，由亚洲最大的飞机试飞鉴定机构——中国飞行试验研究院（简称试飞院）投资兴建。试飞院是中国唯一承担军民用飞机、航空发动机、机载设备等国家级鉴定试飞和适航审定试飞的权威机构，科研人员4000多',37558);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安飞机工业公司职工工学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','oldname": "西安飞机工业公司职工工学院','西安飞机工业公司职工工学院（简称西飞工学院），国标代码50990。学院建立于1979年，隶属于中航工业西飞集团公司，是经陕西省人民政府批准、教育部备案公布的公办成人高等高校。经陕西省教育厅批准，2001年开始举办普通高职教育。学院座落在风景秀丽的被誉为“中国西雅图”之称的飞机城—西安市阎良区。学院以国家特大型企业西安飞机工业（集团）有限责任公司为依托，立足企业，面向社会，融普通高等职业技术教育、成人高等教育、电大、网络继续教育和高层次干部培训为一体，形成了多学科、多层次、多形式和多功能的办学模式',34121);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安铁路工程职工大学',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.xtzd.cn/','西安铁路工程职工大学成立于1982年，是经国家教育部注册备案的独立设置的公办高等院校。学校坐落于历史文化名城西安，地处西安高新技术开发区电子工业园区，周边科研院所、大中专院校林立，人文及自然环境优越、交通便利。学校主要担负普通高职大专学历教育和成人大专教育以及相关专业业务培训和建筑施工企业关键岗位培训资格证书教育。主要开设有铁道工程技术、道路桥梁工程技术、城市轨道交通工程技术、建筑工程技术、地下工程与隧道工程技术、工程造价、铁道通信信号等以交通土建及相关学科为主体的高职专业。学校办学条件优良，教',59925);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都工业职业技术学院',' ','四川',0,1,0,0,0,' ','暂空',' ','http://www.cdivtc.com.cn/','成都工业职业技术学院是成都市人民政府举办的一所以理工科为主的公办高职学院。成都市人民政府投资11亿元人民币，高规格、高标准建设。学院现开办铁道交通运营管理、建筑工程技术、机械制造与自动化、汽车检测与维修技术、物流管理等专业，与四川省、成都市、天府新区产业发展规划高度契合。学院地处四川省经济发展最快的区域——天府新区核心区域，具有独特的产业支撑、人才集聚、交通通讯、国际合作等优势。学院占地面积730余亩，教学用建筑面积30万平方米，教职工1000余人（副教授以上教师250余人），拥有28个校内实训',5506);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津市工会管理干部学院',' ','天津',0,0,0,0,0,' ','暂空',' ','http://www.tjghxy.com','天津市工会管理干部学院是1985年2月经天津市政府批准、国家教育部备案的一所独立设置的，具有普通高等职业教育、成人高等教育、网络教育等多种形式的公办成人高校。学院位于天津第一古镇风景如画的杨柳青，地理位置优越、交通便利。总占地面积为128亩，建筑面积达61000平方米。拥有400座位的多媒体学术报告厅、5个200座位的阶梯教室和50多个自然教室及计算机网络中心、数字录音室、编辑室、摄像演播室、图书馆等完备的教育教学设施设备，同时具有由多个会议厅构成的现代化会议中心的功能；质优价廉的学生食堂、会议',22085);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄职工大学',' ','河北',0,0,0,0,0,' ','暂空',' ','oldname": "石家庄职工大学','石家庄职工大学是经河北省人民政府批准，石家庄市总工会主办的一所公办高等专科学校，位于石家庄市胜利北街491号（原石家庄市石正路199号），创办于1984年。创办以来，学校根据社会的需求，不断调整专业结构，深化教学改革，打造特色专业，坚持“以素质教育为宗旨，以专业教育为基础，以专业技能培养为本位”的办学理念，着重培养学生的综合素质和专业技能，为社会培养应用型大专人才，深受用人单位的好评。为适应普通高职教育“依靠企业办学，面向基层育人，服务地方经济”的发展趋势，自2004年起，我校与省内印刷企业联合',20722);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都市技师学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.cdsjs.com','成都市技师学院简介成都市技师学院建于2009年，是由成都市高级技工学校、成都无线电工业学校、成都市机械技工学校、成都交通高级技工学校合并组建的职业院校，学院历史最早可溯源至1958年。学院由成都市人民政府主办，坚持“以市场为导向，以管理为基础、以技能为核心，以就业为生命”的办学理念，坚持“高端引领、多元办学、突出特色、内涵发展”的办学思路，在地方职业教育领域，领先办学，科学办学，先后获得全国职业教育先进单位、全国教育系统先进集体、第十届国家技能人才培育突出贡献奖等荣誉。学院是四川省人民政府批准设',59846);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西政法管理干部学院',' ','广西',0,11,0,0,0,' ','暂空',' ','http://www.gxzfxy.cn/','广西政法管理干部学院是广西独立设置的政法类高等学校，与广西司法学校合并办学，隶属自治区司法厅领导，担负着培养法律人才和培训政法干部职能，办学以高职专科为主体，以成人专科、普通中专及与区内外本科院校联办成人法学本科为辅。建院20多年来，在自治区党委、自治区人民政府和自治区党委政法委、司法厅、教育厅的正确领导和指导下，在社会各界的关心支持下，学院与时俱进，不断取得新的进展，培养了1.2万多名大中专毕业生，培训政法干部8000多人，这些学生（学员）目前已成为广西政法战线的骨干力量，为广西的经济建设和社',41121);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西工运学院',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sxgyxy.com/','schoolcode": "617000900',25566);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南宁地区教育学院',' ','广西',0,4,0,0,0,' ','暂空',' ','http://www.nnjy.net/','南宁地区教育学院，坐落于广西壮族自治区首府南宁。学院办学历史久远，1988年、1994年以优异成绩两次通过原自治区教委与国家教委的评估验收，是广西21世纪园丁工程支撑单位。学院前有花园小区，后倚芦仙岭，绿树成荫，风景秀丽，环境清幽，是理想的学习园地。学院交通方便，是38路公共汽车始发站。学院设有五个系，开设二十三个专业，即文化传播系[电脑艺术设计（动漫方向）、语文教育、文秘、汉语、广告设计与制作]，公共管理系[社区管理与服务、心理咨询、政治与法律教育、行政管理、地理教育、旅游管理]，外语系（应用',19711);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('赣州师范高等专科学校',' ','江西',0,4,0,0,0,' ','暂空',' ','http://www.ganzhsz.cn','赣州师范高等专科学校是经江西省人民政府批准、国家教育部备案同意设立的一所专科层次普通高等院校，是一所以教教师教育为主，涵盖文、理、体、艺等专业学科的全日制普通专科院校，前身为赣南教育学院，它整合了赣南数所有百年办学历史的师范学校，学校座落在千里赣江第一城——赣州市。学校依山傍水，错落有致，高雅大气，整体建筑采用后现代欧式风格并融入了赣南客家方形围屋的元素，学校占地1034亩，图书馆、教学楼、大学生活动中心等功能齐全的楼群座落其中。图书馆馆藏纸质图书51.7万册，电子图书12。3万册。微格教室、微',67959);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北环境工程学院',' ','河北',1,0,0,0,0,' ','暂空',' ','http://www.emcc.cn/','中国环境管理干部学院是1981年经环境保护部批准、教育部备案的由河北省政府与国家环境保护部共建的一所以培养环境保护专业人才为主的全日制公办高等学府，是我国最早开展环境教育的高校之一，被誉为环保系统的“黄埔军校”和环保人才培养的“绿色摇篮”。学院占地面积700余亩的北戴河新校区已投入使用，为实现学院十二五规划目标之一——升格为本科院校打下了坚实的基础，一座生态环保示范型、人与自然和谐型、理念文化先进型的现代化校园正以崭新的姿态迎接来自各地的莘莘学子。学院师资力量雄厚，现有专兼职教师480余人，其中',1277748);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨市职工医学院',' ','黑龙江',0,3,0,0,0,' ','暂空',' ','http://www.hrbzgyxy.com/m/','哈尔滨市职工医学院是国家教育部批准备案的医学高等专科学校。学校座落在风景秀丽的北国名城哈尔滨市中心，交通十分便利。哈尔滨市职工医学院师资力量雄厚，教学环境优雅，教学设备先进。坚持以人为本，科教兴院的办学方针，实施“教育品牌第一、教学质量第一、教书育人第一、教师地位第一”的办学宗旨，促使学校教学质量不断提高，办学规模不断扩大。目前，我院占地面积11000平方米，建筑面积10580平方米;教职工总人数173人(含职工中专)，其中专职教师114人，行政管理人员25人，教学辅助人员7人，工勤人员20人;',28766);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉冶金管理干部学院',' ','湖北',0,0,0,0,0,' ','暂空',' ','http://www.jiuye114.com/edu/whyjgl/','武汉冶金管理干部学院是1985年经国家教育部、湖北省人民政府批准设立的公办高等学院。地处武汉市中心城区。学院师资雄厚，其中副教授以上专职教师逾百人，教学设施完善，拥有现代化多媒体教室、语音实验室、财会实验室、物流和电子商务实验室以及音乐欣赏室、琴房、舞蹈室、画室、电教室、闭路电视系统和其他的教学仪器设备。图书馆建筑面积4260平方米，藏书32万册，各类期刊380余种。建有足球场、篮球场、网球场、文化活动室和健身房等各类学生活动场所。',18161);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西省政法管理干部学院',' ','山西',0,0,0,0,0,' ','暂空',' ','oldname": "山西省政法管理干部学院','山西省政法管理干部学院是具有近60年发展历史的我省唯一一所独立设置的法学成人专科学校，其前身是创办于1954年10月的山西省政法干部学校。1985年11月，经省人民政府批准，在原政法干校的基础上，改建为山西省政法管理干部学院。1986年学院开始成人学历教育和在职干部短期培训，主要为公、检、法、司培养人才，至2001年前共培训政法干部5000余名，成人大专生15451名，有力解决了政法队伍专业学历偏低和执法素质不适应的问题，为推动山西的社会进步和法治建设作出了巨大的贡献，被誉为“法学教育的基地，政',40796);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('川南幼儿师范高等专科学校',' ','四川',0,4,0,0,0,' ','暂空',' ','http://www.cnyz.edu.cn/','川南幼儿师范高等专科学校是经省人民政府批准，国家教育部正式备案，在原四川省隆昌幼儿师范学校基础上建立起来的,以培养幼儿、小学教育师资为主的一所公办全日制普通高等专科学校，是四川重要的学前教育人才输出及培训基地。学校前身是创办于1932年的隆昌县立乡村师范学校。八十余年风雨兼程，执着追求，学校不断发展壮大，为四川乃至全国许多地区的幼儿教育、小学教育事业输送了近2万名优秀人才，涌现出了全国教育系统劳动模范、全国特级教师，全国优秀教师，四川省优秀教师等杰出校友。学校曾先后被评为“全国文明单位”、“国家',6552);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国科学院大学',' ','北京',1,0,0,0,0,' ','暂空',' ','http://www.gucas.ac.cn/','中国科学院大学(英文名：UniversityofChineseAcademyofSciences)，简称“国科大”，是国家教育部正式批准成立的一所以研究生教育为主的科教融合、独具特色的高等学校。国科大的前身是中国科学院研究生院，成立于1978年，是经党中央国务院批准创办的新中国第一所研究生院，培养了我国的第一个理学博士、第一个工学博士、第一个女博士、第一个双学位博士。2014年，国科大将开始招收本科生。基于中国科学院各研究所的高水平科研优势和高层次人才资源，国科大形成了由京内4个校区、京外5个教',122331);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('国防科学技术大学',' ','湖南',1,10,0,1,1,' ','暂空',' ','http://www.nudt.edu.cn','国防科技大学是国防部和教育部双重领导下的国家重点综合性大学，列入国家“985工程”和“211工程”的重点建设。学校的前身是1953年创建于黑龙江省哈尔滨市的军事工程学院，简称“哈军工”。1970年学校主体南迁长沙，改名为长沙工学院，1978年改建为国防科技大学。学校位于中国湖南省长沙市，占地总面积373公顷。经过50多年的建设与发展，学校成为一所涵盖理学、工学、军事学、管理学、经济学、哲学、文学、教育学、法学、历史学等10个学科门类的综合大学。学校现有10个学院，下设40多个系、所、实验室，其中',4144802);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军南京陆军指挥学院',' ','江苏',1,10,0,0,0,' ','暂空',' ','oldname": "解放军南京陆军指挥学院','中国人民解放军南京陆军指挥学院，是直属解放军总参谋部的一所中级指挥院校，担负着全军指挥军官中级培训和研究生培养的任务。南京陆军指挥学院地处六朝古都南京，坐落在风景秀丽的南京市浦口区的扬子江北岸，学院交通便利，毗邻国家级南京高新技术开发区。学院景色迷人，环境优美，是一座园林式的营院。其具有得天独厚的人文、地理和发达的经济环境，是广大学员学习生活的理想之地。学院位于江苏省南京市，占地160万平方米。院区教学设施、体育场馆、文化娱乐中心和生活服务设施布局合理、配在齐全。院外还有战术训练场、野外驻训和综',130643);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民解放军信息工程大学',' ','河南',1,10,0,0,0,' ','暂空',' ','http://zhaosheng.plaieu.edu.cn/','九曲黄河，浩浩汤汤，奔腾着中华民族复兴的梦想。中原大地，苍苍莽莽，负载着华夏儿女不灭的希望。黄河之滨，中原腹地，一座现代化高等军事学府在新世纪的曙光中崛起。这，就是中国人民解放军信息工程大学！中国人民解放军信息工程大学，由原解放军信息工程学院，测绘学院，电子技术学院合并组建而成，直属总参谋部领导，是全国重点理工科高等军事院校，全军五所综合大学之一。经过多年不懈努力，信息工程大学已经由单一的电讯工程专业发展为以电子信息科学技术为核心，以工为主，理工文渗透，指挥、管理协调发展的多学科综合大学。大学下',1496726);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军理工大学',' ','江苏',1,10,0,0,0,' ','暂空',' ','http://www.plaust.edu.cn/','中国人民解放军理工大学，座落在长江之滨、人文荟萃的六朝古都南京，是一所新型的综合性全军重点院校。她是由具有半个多世纪辉煌历史的全国重点高校解放军通信工程学院，点燃“亚洲第一爆”的全国重点高校解放军工程兵工程学院，开拓新中国军事气象事业先河的空军气象学院和在高科技领域一枝独秀的总参第六十三研究所合并组建，形成了强强联手、优势互补的格局，担负着为国防现代化建设培养高素质新型军事指挥和工程技术人才的重任，是国家和军队进行科学研究、知识创新、技术创新和装备研制的重要基地。学校占地3700余亩，校舍总面积',991578);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军国际关系学院',' ','江苏',1,10,0,0,0,' ','暂空',' ','oldname": "解放军国际关系学院','军事外交官的雄辩、睿智和智勇双全的形象，曾让多少人为之倾倒，也成为许多人心中的梦想。坐落在长江之滨的中国人民解放军国际关系学院，便是“中国军事外交官的摇篮”。从该校走出去的相当一部分毕业生，成为我军军事外交战线上的专家、教授和专门人才。解放军国际关系学院是全国重点高等院校之一，隶属于总参谋部，是一所指挥与技术合一，以外语为基础，多专业、多层次、多规格的综合性军事外交学院，是军队培养国际关系、国防外事、军事外交、国际战略研究、外语师资、部队侦察、技术侦察、航天侦察、特种作战指挥等各类专业人才的重要',920880);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军外国语学院',' ','河南',1,10,0,0,0,' ','暂空',' ','oldname": "解放军外国语学院','schoolcode": "412009400',205350);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军重庆通信学院',' ','重庆',1,10,0,0,0,' ','暂空',' ','oldname": "解放军重庆通信学院','重庆通信学院是一所具有光荣传统和较长办学历史的院校，创建于1955年，座落于重庆市歌乐山南麓风景秀丽、气候宜人的林园，占地面积1500亩。解放前，林园曾是蒋介石和国民政府主席林森的官邸，“重庆谈判”期间，毛泽东、周恩来曾在此下榻。刘伯承、刘华清、余秋里等我军著名将领及全国著名雷达专家毕德显教授曾在此担任过领导职务。毛泽东等人曾多次对我院做过特殊指示。师资队伍中具有副高以上职称的占40%以上，具有博士、硕士学历学位的占62%以上。学院建筑面积30多万平方米，教学、科研、生活设施较为齐全，建有设备先',117771);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军西安通信学院',' ','陕西',1,10,0,0,0,' ','暂空',' ','oldname": "解放军西安通信学院','中国人民解放军西安通信学院，是一所为全军培养初级通信指挥军官和工程技术军官的通信专业院校，隶属于总参谋部。学院前身是东北民主联军总部通讯学校，始建于1945年。1986年改名为西安通信学院。学院定址于西安市长安中路97号，营区分为三部分，占地618.2亩。生活区分别位于长安中路和丈八东路，教学区位于西安市王曲镇。学院开设通信指挥、计算机科学与技术、信息工程、通信工程、网络工程、有线通信指挥、有线通信工程、电信交换指挥、电信交换工程、数据通信终端、计算机维修等专业。培训层次以本科为主，兼有部分大专',239874);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军陆军军官学院',' ','安徽',1,10,0,0,0,' ','暂空',' ','oldname": "解放军陆军军官学院','中国人民解放军陆军军官学院（2011年8月更名，11月2日揭牌成立）由原中国人民解放军炮兵学院改建，其前身为合肥炮兵学院改名组建的一所综合性院校，学院本部在合肥。陆军军官学院（本部）是一所全部培养本科以上层次陆军初级指挥军官的高等院校，合肥炮兵学院前身是列为全国重点院校的中国人民解放军炮兵技术学院。目前学院具有工学、管理学和军事学学士学位、硕士学位和博士学位授予权，有权免试保送研究生。陆军军官学院是全军唯一一所以培养陆军主战兵种初级指挥军官为主体任务的院校，将成为解放军步兵、炮兵、防空兵、装甲兵',864888);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军南京炮兵学院',' ','江苏',1,10,0,0,0,' ','暂空',' ','oldname": "解放军南京炮兵学院','中国人民解放军南京炮兵学院，经中央军委批准，2011年由原解放军炮兵指挥学院与原解放军炮兵学院南京分院合并组建成立。原炮兵指挥学院前身为1944年在陕北南泥湾创建的解放军第一所兵种院校“延安炮兵学校”，原炮兵学院南京分院前身为1947年3月在山东沂水满堂坡成立的华东野战军“特纵特科学校，两校已为全军部队培养了12万余名炮兵、防空兵指挥人才。南京炮兵学院选址在原国民党陆军炮兵学校旧址。国民党1931年在此开办民国时期第一所兵种学校，是当时全国唯一的炮兵学校，史称“汤山炮校”，地位仅次于陆军大学和黄',64824);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军沈阳炮兵学院',' ','辽宁',1,10,0,0,0,' ','暂空',' ','oldname": "解放军沈阳炮兵学院','院校介绍沈阳炮兵学院是一座历史悠久的军事院校，地处在风光旖旎的天柱山下——沈阳市东郊，俗称东大营。东与清太宗努尔哈赤的陵地“福陵”毗邻，西与繁华的市区连成一片，南临沈抚公路，北跨沈吉铁路，沈阳环城高速公路和大二环路为学院提供了便利的交通条件。学院院内垂柳婀娜，古松挺拔，绿草茵茵，鲜花绽放，四季景色各异，春花秋果，夏绿冬青，清新幽雅，和谐恬静。学院幅员广阔，资源优越，占地3000多亩，青砖灰瓦、雕梁画栋的历史建筑和鳞次栉比、风格各异的现代化建筑交相错落，典雅古朴。既有现代化的教学楼、办公楼、图书馆',179535);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军防化学院',' ','北京',1,10,0,0,0,' ','暂空',' ','oldname": "解放军防化学院','中国人民解放军防化指挥工程学院位于北京市昌平区，占地近3000亩，隶属于中国人民解放军总参谋部，是全军唯一的培养防化指挥与工程技术人才的综合型高等院校。中国人民解放军防化指挥工程学院前身为军委化学兵学校，1950年由毛主席、周总理批准成立。根据新的教学任务规划，中国人民解放军防化指挥工程学院承担学历教育和任职教育双重任务。现有全军重点学科2个，联合培养博士点2个，硕士点及工程硕士培养领域9个，本科专业12个，任职教育专业11个。中国人民解放军防化指挥工程学院师资力量雄厚，目前400多名教学科研人',271188);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军工程兵学院',' ','江苏',1,10,0,0,0,' ','暂空',' ','oldname": "解放军工程兵学院','中国人民解放军工程兵学院成立于1986年，位于历史文化名城江苏省徐州市，是一所培养全军工程兵中级军政指挥官、院校教官、高层次军事学术研究人才和工程兵分队指挥人才的中级指挥院校，是工程兵指挥军官的最高学府。学院拥有一支教风严谨、教学工地深厚、结构合理的师资队伍，一批学术造诣较深的正副教授、高级工程师担任硕士、博士研究生导师，几十名教师被评为全国优秀教师和全军优秀教员。经过长期教学实践和改革，形成了计算机科学、建筑设计、道路桥梁、工程机械、防水工程、爆破工程、工程兵战术、工程兵指挥、战役战术工程保障',24974);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军电子工程学院',' ','安徽',1,10,0,0,0,' ','暂空',' ','http://www.eei.edu.cn/','schoolcode": "342009500',182580);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军陆军航空兵学院',' ','北京',1,10,0,0,0,' ','暂空',' ','oldname": "解放军陆军航空兵学院','中国人民解放军陆军航空兵学院，副军级，隶属于总参谋部。是全军最年轻的一所兵种院校，是在1999年全军院校体制编制压缩调整的情况下新成立的院校。校园位于北京市通州区，环境优美，交通便利，是一所设计先进、功能齐全、布局合理、特色鲜明，融入了最新设计理念和创新精神的现中国人民解放军陆军航空兵学院代化新型军事院校。学院拥有4万平方米的教学、实验大楼、教学及科研设施完备。多年来，学院深化教学改革，推进素质教育，取得了明显成效。主要任务是培养陆军航空兵中级政治指挥人才，陆、空军直升机飞行员和陆军航空工程机务',316898);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军南京政治学院',' ','江苏',1,10,0,0,0,' ','暂空',' ','oldname": "解放军南京政治学院','南京政治学院原名为南京政治学校，成立于1977年12月，于1986年更名为南京政治学院。1999年5月，原空军政治学院转隶并入该学院，为南京政治学院上海分院。该院是直属总政治部领导的中级任职教育指挥院校，编制等级为正军级,是军队“2110工程”重点建设院校和全军研究生培养单位重点建设院校。30余年来，伴随着党的理论和实践创新步伐成长的南京政治学院，如今已成为军队政治院校最高学府，有着“军中北大”的美誉。学院面向全国全军招生，主要承担着为全军培养政治指挥干部、政治机关干部、政治工作专门人才的任务，',892308);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军后勤工程学院',' ','重庆',1,10,0,0,0,' ','暂空',' ','http://www.hqgc.net','中国人民解放军后勤工程学院是军队高等工程技术院校。创办于1961年，1979年被确定为全国重点高等学校，是全军重点建设院校之一。1999年在全军率先荣获“教学优秀单位”称号。2006年，学院成为军队”2110工程“整体条件重点建设院校。学院创建于1961年，座落在美丽的山城重庆市区。占地面积1300多亩，建筑面积40多万平方米，图书馆藏书70余万册，建有校园网和电子阅览室。现有土木工程、化学工程与技术、石油与天然气工程3个博士后科研流动站和岩土工程、应用化学、油气储运工程、后方专业勤务4个博士学',386544);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军艺术学院',' ','北京',1,8,0,0,0,' ','暂空',' ','oldname": "解放军艺术学院','中国人民解放军艺术学院创建于1960年，是中国人民解放军唯一的一所多学科、综合性高等艺术院校，担负着为全军培养文学艺术人才、文化管理干部和开展军事文艺研究等任务。设有文学、戏剧、音乐、舞蹈、文化管理、美术6个系19个专业，形成了研究生、本科、中专教育三位一体，培训、轮训和函授教育互为补充的教育体系。学院坐落在大学林立、文化艺术氛围浓厚、高科技人才密集的北京市海淀区中关村。',137522);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('第二军医大学',' ','上海',1,10,0,1,0,' ','暂空',' ','http://ao.smmu.edu.cn/','中国人民解放军第二军医大学创建于1949年9月，1951年定名为“中国人民解放军第二军医大学”,隶属中国人民解放军总后勤部。学校现设训练部、政治部、校务部、科研部、研究生院5个部院机关，下辖基础部、海军医学系、药学系、卫生勤务学系、护理学系、中医系、外训系、研究生管理大队、学员旅、长海医院、长征医院、东方肝胆外科医院、南京军医学院以及3个干休所，共有教职员工10,000余人。是一所为军队培养卫生专业技术干部和卫生事业管理干部的全国重点高等医科大学。学校现设临床医学、海军临床医学、临床精神卫生学、',1519555);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民解放军军事交通学院',' ','天津',1,10,0,0,0,' ','暂空',' ','http://www.jjxy.cn','军事交通学院隶属于解放军总后勤部，是全军培养军事交通运输、军用车辆装备和军用物资储运领域高层次人才的最高学府，是集保障指挥与工程技术于一体、多科性教学科研型军事院校，主要面向全军培养本科、硕士、博士生，同时承担任职教育和外军留学生培训任务。在长期的办学过程中，学院秉承“忠诚、博学、自强、先行”的校训，弘扬艰苦奋斗、自强不息、求实创新的光荣传统，为部队培养输送了一批批高素质军交运输合格人才。学院师资力量雄厚，拥有一支结构合理、学术精湛、锐意创新、在军内外有较大影响的专家队伍，现有教授、副教授210',509179);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军第三军医大学',' ','重庆',1,10,0,0,0,' ','暂空',' ','http://www.tmmu.edu.cn/','中国人民解放军第三军医大学座落于重庆直辖市。这所有着70多年光荣历史和鲜明军事特色的军医大学，是新中国成立后第一批全国重点大学、军队“2110工程”整体重点建设院校。学校1954年由原六、七医大合并而成。六医大的前身是第四野战军医科学校和南昌医学院（前国立中正医学院）；七医大的前身是太岳军区卫生学校（后发展成为第二野战军医科大学）。合校后定名为第七军医大学，1969年迁往上海，1975年迁回重庆，军委命名为第三军医大学。学校下设基础部、军事预防医学院、药学院、高原军事医学系、生物医学工程与医学影',894247);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军镇江船艇学院',' ','江苏',1,10,0,0,0,' ','暂空',' ','oldname": "解放军镇江船艇学院','中国人民解放军镇江船艇学院位于江苏省镇江市区，现有两座营院，占地456.3亩（其中校区326.8亩，船艇教练大队129.5亩），营房建筑面积10万多平方米。校园依山傍水，错落有致。学院教学、科研、文体及生活设施完备，为教学、科研奠定了丰厚的物质基础。园区内环境优美，生机盎然，营造了良好的育人氛围，学院享有\"花园学校\"的美誉。通过“船艇人”的不懈奋斗，中国人民解放军镇江船艇学院目前已成为全军唯一一所为陆军、空军培养船艇军事人才的初级指挥学院。学院集指挥、管理、工程技术于一体，实施本科、大专、中专三',87189);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军装备学院',' ','北京',1,10,0,0,0,' ','暂空',' ','oldname": "解放军装备学院','中国人民解放军装备学院是隶属总装备部的正军级军事院校，主要面向全军装备系统和航天领域培养装备指挥技术和航天指挥技术人才。学院是军队2110工程重点建设院校，经过三十多年的发展建设，形成了以军事学为主、军事学与工学、管理学、经济学、理学、法学6个门类相互融合的学位授权体系，拥有一批在全军乃至全国颇具影响的学科专业。现有2个博士后流动站、3个一级学科博士点、1个二级学科博士点、10个一级学科硕士点、5个二级学科硕士点，拥有1个国家重点（培育）学科、6个军队重点学科专业领域、4个总装备部重点学科和1个',312181);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民解放军装甲兵工程学院',' ','北京',1,10,0,0,0,' ','暂空',' ','oldname": "中国人民解放军装甲兵工程学院','装甲兵工程学院座落在北京市风景秀丽的卢沟桥畔，是全国重点工科院校和全军十二所综合院校之一。学院的前身是\"哈尔滨军事工程学院\"装甲兵工程系，1961年在西安扩建为装甲兵工程学院，1969年迁址首都北京，是我军培养装甲机械化部队工程技术军官和指挥军官的最高学府。学院占地面积3700余亩，建有现代化的图书馆和电子阅览室，与国际互联网、北京图书馆和总部科技情报所等大型图书资料中心建立了联机检索。有近200个实验室、专修室和7个大型专业训练场。已建成全军首家以千兆以太网为支撑的校园网，学员可通过校园网实现',639232);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军装甲兵学院',' ','安徽',1,10,0,0,0,' ','暂空',' ','oldname": "解放军装甲兵学院','中国人民解放军装甲兵学院原名蚌埠坦克学院，1978年在安徽省宿县建校，称中国人民解放军坦克学校。1982年迁址至安徽省蚌埠市，1986年9月改名为中国人民解放军蚌埠坦克学院。是一所为全军装甲兵部队培养初级指挥军官的院校。她坐落在淮河之滨的安徽省蚌埠市燕山脚下。学院共设4年制大学本科班、3年制大学专科班、2年制中专班3个培训层次。学院拥有微机监控的教学指挥中心、教学实验楼、电化教学楼、模拟楼和图书馆，给配套教学提供了条件。学院教学和生活设施配套合理。26个实验室可完成物理、化学、电子、机械、计算机',28497);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军军械工程学院',' ','河北',1,10,0,0,0,' ','暂空',' ','http://www.jxzs.net.cn/','中国人民解放军军械工程学院是一所以工为主，工学、军事学、管理学、哲学等学科门类比较齐全的学历教育院校，是全国重点高等院校，隶属于中国人民解放军总装备部。学院复建于1974年，1986年定名为军械工程学院。学院设有军械技术研究所、基础部、火炮工程系、光学与电子工程系、弹药工程系、导弹工程系、计算机工程系、装备指挥与管理系、电气工程系、外训系、研究生管理大队、轮训大队、教练团等单位。学院拥有4个博士后科研流动站、1个博士后科研工作站，2个博士学位授权一级学科、8个硕士学位授权一级学科和10个博士点、',290593);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军第四军医大学',' ','陕西',1,10,0,1,0,' ','暂空',' ','http://www.fmmu.edu.cn/','学科专业1．培养方向、学科结构与布局学校以研究生和本科生培养为主。设有七年制临床医学、口腔医学；五年制临床医学、口腔医学、空军临床医学、生物医学工程、预防医学（营养与食品卫生）、临床医学（康复医学与疗养学）；四年制药物制剂、公共事业管理（卫生统计）等10个本科专业；临床医学、口腔医学、口腔技工工艺、护理学4个大专专业。另有委托培养专业：两年制临床医学（专升本），口腔医学（专升本）；三年制临床医学大专，口腔医学大专；函授本科专业：生物医学工程，医学实验技术，公共事业管理，护理学，预防医学，药物制剂',1659964);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军海军工程大学',' ','湖北',1,10,0,0,0,' ','暂空',' ','oldname": "解放军海军工程大学','schoolcode": "422010300',296136);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军海军大连舰艇学院',' ','辽宁',1,10,0,0,0,' ','暂空',' ','oldname": "解放军海军大连舰艇学院','海军大连舰艇学院，是培养海军舰艇技术指挥军官、海军政治指挥军官和海洋测绘工程技术军官的高等院校。她坐落在风光秀丽的大连市老虎滩畔，依山傍海，风景秀丽，具有良好的学习、生活环境。学院是经毛泽东主席于1949年11月22日亲自批准成立的新中国第一所正规海军高等学府。学院被全国、全军授予“管理教育先进单位”、“全军政治理论教学先进单位”、“先进高等学校”、“优秀教学单位”等荣誉称号。',439313);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军装甲兵技术学院',' ','吉林',1,10,0,0,0,' ','暂空',' ','oldname": "解放军装甲兵技术学院','中国人民解放军装甲兵技术学院是一所为全军装甲机械化部队培养装甲装备技术保障人才的高等专业技术院校。学院坐落于长春市经济技术开发区,营区开阔秀美,布局合理,各项教学、生活设施齐全。学院始建于1951年,历经中国人民解放军第三战车编练基地、装甲兵技工学校、第五坦克学校、装甲兵技术学校、1999年更名为装甲兵技术学院。',30671);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民解放军海军航空工程学院',' ','山东',1,10,0,0,0,' ','暂空',' ','http://jz.81.cn/datajzb/zt/ym32_0511/','中国人民解放军海军航空工程学院是一所以培养海军初级指挥军官和航空航天工程技术军官为主的高等军事院校，是海军指挥与工程技术人才培养的主要基地、航空航天领域科研中心和航母骄子的摇篮。学院创建于1950年，是新中国首批组建的军事院校之一，1963年被国防科工委确定为重点院校，1979年被国务院列入全国重点高等院校，1986年获硕士学位授予权，2000年获博士学位授予权，2003年建立博士后科研流动站，同年被总部列为全军二十二所学历教育院校之一，2000年、2010年两次参加全军院校教学工作评价，均名列',516449);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军空军航空大学',' ','吉林',1,10,0,0,0,' ','暂空',' ','oldname": "解放军空军航空大学','空军航空大学校部坐落在北国春城吉林省长春市，大学共由7个校区组成，占地1900万平方米，分布在黑、吉、辽三省四个城市，似一串璀璨的明珠镶嵌在辽阔的东北大地上。大学的前身是1946年我军创办的第一所航空学校——东北民主联军航空学校（即东北老航校）。2004年，经中央军委批准，由与东北老航校血脉相连的原空军第二航空学院、空军长春飞行学院和空军第七飞行学院合并组建空军航空大学。历经60多年的风雨洗礼，航大人创造了中国空军历史上“最先培训新中国第一批女航空员”、“最先承训双学士飞行员”、“最先培养空中技',191113);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民解放军空军预警学院',' ','湖北',1,10,0,0,0,' ','暂空',' ','oldname": "中国人民解放军空军预警学院  曾用名：空军预警学院','空军预警学院组建于1952年，原名空军雷达学院，2011年改建为空军预警学院。学院是全国唯一一所预警监视领域专门院校，全军唯一一所预警监视和电子对抗为主体学科专业的院校，是军队信息能力培训基地之一、国家一类本科军级军事院校，主要承担全军预警系统和空军地面电子对抗部队指挥技术军官及士官培养任务。学院采取“一院四地”办学，院本部位于湖北省武汉市，总占地面积230多万平方米。院区环境优雅、绿树成荫、教学设施完善，是从军报国、学习成才的理想院校。学院在60余年的建设发展历程中，积淀形成了“三个鲜明特色”',781192);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军海军潜艇学院',' ','山东',1,10,0,0,0,' ','暂空',' ','oldname": "解放军海军潜艇学院','中国人民解放军海军潜艇学院是一所培养海军潜艇和防救部队初、中两级军事指挥、工程技术军官的重点军事院校，主要任务是培养海军潜艇部队和防救部队各类指挥军官、导弹防救工程技术干部及专业技术兵等。具有硕士、学士学位授予权。学院坐落于美丽的海滨城市青岛，环境幽雅，气候宜人。中国人民解放军海军潜艇学院是直属中国人民解放军海军的一所专业性很强的技术指挥院校，是亚洲唯一的一所潜艇兵种高等学府。学院创建于1953年8月。1957年9月，改名为中国人民解放军海军潜艇学校。1983年，更名为中国人民解放军海军潜艇学院',50310);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军空军指挥学院',' ','北京',1,10,0,0,0,' ','暂空',' ','oldname": "解放军空军指挥学院','中国人民解放军空军指挥学院（原名空军学院）坐落于北京西郊著名风景区颐和园昆明湖畔，依山傍水，风景秀丽。它是一所培养空军师团职指挥军官的中级指挥院校，是空军培养指挥军官的最高学府。空军指挥学院是以刘伯承元帅任院长的南京军事学院空军系为基础，于1958年9月12日在北京组建的。学院第一任院长兼政委由空军副司令员刘震上将兼任。建院四十多年来，共为空军部队培养了两万多名指挥和参谋人才，其中有150多名走上军以上领导岗位，多数担负了师团领导职务。总政治部副主任周子玉、空军司令员刘顺尧等一些高级将领均毕业于',38374);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军空军工程大学',' ','陕西',1,10,0,0,0,' ','暂空',' ','http://www.afeu.cn/','空军工程大学中心校区，位于西安市灞桥区浐河生态园区，大学下设工程学院、导弹学院、电讯工程学院和理学院，是一所融工、理、文、管、法、军等多学科于一体的综合性大学，是全国、全军重点大学。担负着为空军培养航空工程、地空导弹、电子信息等领域，高层次工程技术人才、指挥军官及培训外国留学生任务。大学现有5个博士后科研流动站，7个一级学科博士学位授权点，30个二级学科博士学位授权点，47个硕士学位授权点，29个本科专业；1个国家重点学科，5个全军重点学科，3个全军重点实验室，属于军队“2110工程”整体条件重',565457);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军空军第一航空学院',' ','河南',1,10,0,0,0,' ','暂空',' ','oldname": "解放军空军第一航空学院','中国人民解放军空军第一航空学院（简称空军第一航空学院）隶属中国人民解放军空军，正师级。学院坐落在素有“豫南明珠”之称的河南省信阳市，这里交通便利，景色怡人，毗邻国家级风景名区——南湾湖和全国三大避暑胜地之一的鸡公山风景区。营院占地1000余亩，院内\"春有花、夏有荫、秋有果、冬有青\"，是全军绿化先进单位。学院是一所培训航空工程技术人才的高等军事院校，主要担负着空军航空兵部队地勤各类技术军官和外军留学生的培训任务。1951年5月组建于辽宁沈阳，1970年迁至河南信阳。',37001);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('火箭军工程大学',' ','陕西',1,10,0,0,0,' ','暂空',' ','http://www.epgc.net','第二炮兵工程大学地处历史文化名城、十三朝古都西安，距市中心约20公里，是我国唯一一所培养战略导弹部队指挥技术人才的高等军事院校，是全国全军重点院校，是军队“2110工程”重点建设院校。学校下设3个学院（理学院、初级指挥学院、士官职业技术教育学院）、7个专业系、2个研究生管理大队和若干学员旅。我校原是一所为地面炮兵培养初级指挥军官的军事院校。1951年1月，西北军区司令员彭德怀、政治委员习仲勋签署命令，由西北军区炮兵教导团与第一野战军野炮团合并改编西北军区炮兵学校。1951年3月，更名为中国人民解',747414);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军第二炮兵指挥学院',' ','湖北',1,10,0,0,0,' ','暂空',' ','oldname": "解放军第二炮兵指挥学院','中国人民解放军第二炮兵指挥学院是为二炮部队培养指挥军官和高层次专门人才的中级指挥院校。学院创建于1977年，坐落于江城武汉，占地面积57万平方米，建筑面积18万平方米。是一所正军级中级指挥院校，是第二炮兵的指挥人才培训中心和军事理论研究中心，承担着为二炮部队培养战役指挥军官、中级指挥军官、军事学博士和硕士研究生、部分初级指挥军官以及地方高校毕业生等培训任务。建院以来，已为二炮部队培养14000余名各类人才，绝大部分成为部队建设的骨干，其中有数百人担任了军、师、旅、团的领导职务。',100924);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军南昌陆军学院',' ','江西',1,10,0,0,0,' ','暂空',' ','oldname": "解放军南昌陆军学院','中国人民解放军南昌陆军学院，简称南昌陆军学院，是一所培养基层指挥军官的初级指挥院校，座落在南昌市，创建于1955年。学校占地面积1.79平方公里，建筑面积17万平方米。学院建有电化教学中心、电子计算机中心、实验中心、两个英语实验室、一个英语教学电台，已建10个军事专业教室。有战术技术训练场、驾驶训练场、体育运动场、实习工厂、图书馆、礼堂、游泳池、印刷厂、生活服务中心等．为教学、生活提供了良好的条件。',82931);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警指挥学院',' ','天津',1,10,0,0,0,' ','暂空',' ','oldname": "武警指挥学院','中国人民武装警察部队指挥学院是一所为武警部队培养中高级指挥人才、高级理论人才、高级参谋人才和优秀师资人才的高等军事院校。是武警部队唯一的中级指挥院校，隶属中国人民武装警察部队总部，正军级。学院地址位于依山枕海的港口城市天津，占地1700余亩，建筑面积近13万平方米，具有现代建筑风格的新型军事学院耸立在津南大地上。学院具有一支素质优良的教员队伍，其中教授、副教授达70余名。形成了一支以青年教师为骨干，学术水平高，业务能力较强的教学、科研队伍。学院有4项科研成果获国家专利，定期出版《武警教育》、《武',99862);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警沈阳指挥学院',' ','辽宁',1,10,0,0,0,' ','暂空',' ','oldname": "武警沈阳指挥学院','中国人民武警警察部队沈阳指挥学院(简称武警沈阳指挥学院)，地处沈阳市苏家屯区，北邻桃仙国际机场，西靠沈丹高速公路，交通便利，景色怡人。学院占地近600亩，营区建筑面积73600平方米，教学训练场地和生活基础设施齐全。学院始建于1982年，1984年经公安部批准正式建校；2000年5月，经中央军委批准，更名为武警沈阳指挥学院，实现了由初等教育向高等教育的转变，是武警部队重点发展的指挥院校之一。',76340);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警广州指挥学院',' ','广东',1,10,0,0,0,' ','暂空',' ','oldname": "武警广州指挥学院','武警广州指挥学院是1983年全国武警第一批改校单位之一，是武警部队建设的重点指挥院校，素有“南粤警官熔炉”之称。学院位于白云山北麓，距市中心8公里，占地面积18万多平方米，校园环境幽雅，先后被评为“全国造林绿化300佳单位”、“全国绿化先进单位”。学院为培训大学本科学历层次的武警指挥院校，所培训专业为武警指挥，学制四年，并承担了武警部队预任机关科（股）长和各级各类培训、轮训任务。本科生面向社会招收普通中学应届高中毕业生男生。学院有先进配套的教学设施，有完备正规的军事训练基地，有多功能的媒体教学系',114521);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警福州指挥学院',' ','福建',1,10,0,0,0,' ','暂空',' ','oldname": "武警福州指挥学院','武警福州指挥学院原名中国人民武装警察部队指挥学院福州分院，坐落于素有“花乡”美誉的福州市仓山区洪塘状元街,于1985年6月25日经国务院、中央军委批准成立，是武警部队第一批创办的院校之一。学院是武警部队唯一一所兵种专业院校，设有初级指挥、炮兵指挥、工兵指挥、防化指挥四个专业，承担武警部队炮、工、化兵种专业人才和武警福建总队初级指挥专业人才的培训和轮训任务。建校以来，先后为武警部队培养输送优秀合格毕业生3402人，轮训在职干部3万多人次。涌现出武警部队“十大忠诚卫士”刘文斌、刘小宁、“全国百名优秀',58285);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警乌鲁木齐指挥学院',' ','新疆',1,10,0,0,0,' ','暂空',' ','oldname": "武警乌鲁木齐指挥学院','武警乌鲁木齐指挥学院的前身是武警乌鲁木齐指挥学校，组建于1983年，是国务院、中央军委、武警总部批准的新疆唯一一所武警初级指挥学校。建院20余年来，为驻新疆武警部队内卫、边防、消防、兵团指挥部、8660部队培养输送了大批军事人才。目前，武警新疆总队、武警兵团指挥部所属单位90%以上的干部，是武警乌鲁木齐指挥学院毕业的学员，其中一部分已走上团职领导岗位。2006年武警部队院校编制体制调整，学院扩编为正师级单位，更名为武警乌鲁木齐指挥学院，并由原来的专科教育升格为本科教育，实现了“办学规模化、教学现',52775);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津城建大学',' ','天津',1,0,0,0,0,' ','暂空',' ','http://www.tjuci.edu.cn/index.htm','天津城建大学是一所天津市市属普通高等学校，始建于1978年，前身是天津大学建筑分校，1987年更名为天津城市建设学院，2013年更为现名。原全国政协主席李瑞环为学校题写了校名。建校30多年来，学校全面贯彻党的教育方针，坚持“发展城市科学，培育建设人才”的办学宗旨，践行“重德重能、善学善建”的校训，坚持“依托行业，强化特色，质量为本，追求卓越”的办学理念，立足天津、面向全国、放眼世界，服务城镇化和城市现代化建设，形成了以工、理、管为主要学科，工、理、管、经、艺、文等学科相互支撑、协调发展的学科专业',2928232);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏理工学院',' ','江苏',1,4,0,0,0,' ','暂空',' ','http://www.jstu.edu.cn','江苏技术师范学院招生代码：1224江苏技术师范学院创建于1984年，是江苏省唯一一所以工科专业为主，具有职业教育特色的省属本科应用型院校。1992年学校获得学士学位授予权，1999年被教育部确立为首批全国职业教育师资培训重点建设基地，2008年以优秀成绩通过教育部本科教学工作水平评估。学校坐落在江南历史文化古城、美丽富饶的长江金三角腹地－－江苏省常州市，占地近1500亩，校舍总面积40多万平方米。学校设有机械与汽车工程学院、电气信息工程学院、计算机工程学院、艺术设计学院、商学院、教育学院等15个',1343496);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏师范大学',' ','江苏',1,4,1,0,0,' ','暂空',' ','http://www.xznu.edu.cn/','徐州师范大学的前身是1952年创办于江苏无锡的转业干部速成学校，战功卓著的刘先胜中将是第一任校长。1956年经国务院批准，转制为普通高等教育序列，成立江苏师范专科学校。1958年学校北迁徐州，1959年与徐州师范专科学校合并，成立徐州师范学院，当年起招收本科生，是当时苏北地区唯一的一所本科高校。60年代初，全国高校布局调整，敬爱的周恩来总理指示：“徐州地区地域辽阔，要有大学”，学校因此得以保留。从此，学校扎根苏北，艰苦创业，逐步发展。1989年，成立于1984年的第二所徐州师范专科学校并入我校。',1738572);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西中医药大学',' ','江西',1,3,0,0,0,' ','暂空',' ','http://www.jxutcm.edu.cn/','江西中医学院创办于1959年。自创办以来，学院致力于培养适应社会进步和中医药事业发展需要的具有市场竞争力的实践型、创新型、创业型人才，形成了以中医药教育为主体、多学科协调发展、产学研结合特色鲜明的办学优势和核心竞争力，成为江西省中医药教育、医疗和科研中心，先后多次荣获全国和全省“党建和思想政治工作先进高等学校”、“优秀校风文明单位”和“省级文明单位”等光荣称号。学院现有4个校区，共占地2308亩，建筑面积35万平方米，教学、科研仪器设备总值10582万元，设中医系、针灸骨伤系、中药系、药学系、计',1575266);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西科技师范大学',' ','江西',1,4,0,0,0,' ','暂空',' ','http://www.jxstnu.cn/','江西科技师范大学坐落于具有\"物华天宝、人杰地灵\"盛誉的历史文化名城--南昌，是我国首批建立的高等职业技术师范院校，是教育部重点建设的培养职业教育师资的高等院校，也是江西省唯一一所培养职业教育师资的多科性本科院校，主要培养职教师资、普教师资和应用型专门人才。经过61年的建设与发展，学校形成了以本科教育为主体，积极发展研究生教育，统筹发展继续教育的多层次、多规格的办学格局。2008年，学校以\"优秀\"成绩通过教育部本科教学工作水平评估。历史沿革学校办学起始于1952年，1977年创建江西师范学院南昌分',2139757);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川外国语大学',' ','重庆',1,5,0,0,0,' ','暂空',' ','http://www.sisu.edu.cn','四川外国语大学（简称“川外”）坐落在重庆市沙坪坝区，是一所全日制普通本科院校，是西南地区外语和涉外人才培养以及外国语言文化、对外经济贸易、国际问题研究的重要基地。历史沿革四川外国语大学的发展历经了6个不同的历史时期：1950年4月，在邓小平、刘伯承、贺龙等老一辈无产阶级革命家的亲切关怀和指导下，中国人民解放军西南军政大学俄文训练团成立、历经解放军第二高级步兵学校俄文大队（1951年1月）、西南人民革命大学俄文系（1952年11月）、西南俄文专科学校（1953年3月）、四川外语学院（1959年5月',2983497);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连民族大学',' ','辽宁',1,9,0,0,0,' ','暂空',' ','http://www.dlnu.edu.cn/','大连民族大学位于黄海之滨的大连市，隶属于国家民族事务委员会，是国家唯一设在东北和沿海开放地区、以工科和应用学科为主要特色的综合性民族高等学校。学校1984年立项筹建，2006年实现国家民委、辽宁省政府和大连市政府三方联合共建。学校有开发区和金石滩两个校区，设有经济管理学院、机电信息工程学院、生命科学学院、外国语言文化学院、计算机科学与工程学院、设计学院、土木建筑工程学院、文法学院、理学院、国际商学院、信息与通信工程学院、环境与资源学院、物理与材料工程学院、建筑学院、预科部、思想政治理论课教学科研',2896756);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连外国语大学',' ','辽宁',1,5,0,0,0,' ','暂空',' ','http://xsc.dlufl.edu.cn','大连外国语学院是辽宁省属高等院校，其前身为大连日语专科学校，是在周恩来总理的亲切关怀下，国家为培养外事干部和日本语翻译人才于1964年创办的。学校经过46年的建设，现已发展为一所以外语为主，文、管、经、工、法等多学科协调发展的外国语大学。学校设有日语、英语、俄语、法语、德语、朝鲜语、西班牙语、阿拉伯语、意大利语、葡萄牙语、汉语言、汉语言文学、对外汉语、新闻学、艺术设计、旅游管理、国际经济与贸易、音乐学、计算机科学与技术、信息管理与信息系统、市场营销、经济学、金融学、翻译、财务管理、软件技术等26',3465659);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('温州医科大学',' ','浙江',1,3,0,0,0,' ','暂空',' ','http://www.wmu.edu.cn/','温州医学院坐落在经济繁荣、独具特色的沿海开放城市—温州，是一所以医为主，理、工、文、管、法、教育等学科协调发展的浙江省省属高等院校，浙江省重点建设大学，原名“浙江第二医学院”，1958年由浙江医学院从杭州分迁至温州建立。学校面向全国招生，现已成为浙江省医学人才培养的主要基地之一。温州医学院是全国首批硕士学位授予单位，具有博士学位授予权。学校现有眼科学、外科学、妇产科学3个学科的博士点；临床医学、中药学2个一级学科硕士点；28个二级学科硕士点。设有1个七年制本硕连读专业，28个本科专业，其中临床医',1998796);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江财经大学',' ','浙江',1,6,0,0,0,' ','暂空',' ','http://www.zufe.edu.cn','浙江财经大学是一所以经济、管理学科为主体，经、管、文、法、理、工、艺多学科协调发展的财经类高校。学校坐落在风景秀丽的浙江省会城市杭州，前身是创建于1974年的浙江财政银行学校，1987年经原国家教委批准成立浙江财经学院，1991年获学士学位授予权，2003年获硕士学位授予权，2006年获教育部本科教学工作水平评估优秀，2012年获批服务国家特殊需求博士人才培养项目，2013年经教育部批准更名为浙江财经大学。学校设下沙、文华和长安3个校区，占地2300亩，总建筑面积97万平方米。现有15个学院，2',2790948);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州医科大学',' ','广东',1,3,0,0,0,' ','暂空',' ','http://www.gzhmc.edu.cn/','广州医科大学是一所以医学为优势和特色，开展博士、硕士、本科多层次人才培养的全日制高等医科院校。其前身是创办于1958年的广州医学院，2013年经教育部批准更名为广州医科大学。学校现有占地面积1281亩。东风西路主校区坐落于历史文化名城--广州的中心地带，南依苍翠葱茏的越秀山，北傍风光秀美的流花湖，是读书治学的理想之地。总占地面积1500亩的番禺校区正在建设之中。学校设有研究生学院、国际教育学院、继续教育学院、基础学院、药学院、口腔医学院、卫生管理学院、公共卫生学院、护理学院、金域检验学院、卫生职',2350392);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东财经大学',' ','广东',1,6,0,0,0,' ','暂空',' ','http://zsb.gdufe.edu.cn','1983年5月，学校经国务院批准设立，校名为广东财经学院;1985年6月，更名为广东商学院;2013年6月，更名为广东财经大学。经过33年的发展，学校已建设成为全日制本科生、硕士生27600多人的省属重点院校，是广东和华南地区重要的经、管、法人才培养、科学研究和社会服务基地。学校有广州和佛山两个校区，总占地面积2275亩，广州校区毗邻广州国际会展中心，佛山校区位于佛山市三水区云东海旅游经济区。现有校舍约71.97万平方米,教学科研仪器设备总值12497万元，图书468.5万册(含电子图书);自主',4557024);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海对外经贸大学',' ','上海',1,6,0,0,0,' ','暂空',' ','http://zhaosheng.suibe.edu.cn/','上海对外经贸大学前身为上海对外贸易学院，始建于1960年，是原国家对外贸易部的部属高校，1994年9月，学校划归上海市人民政府管理。2013年4月，经教育部批准学校更名为上海对外经贸大学。学校现有经济学、管理学、文学、法学、理学5个学科门类，设有国际经贸学院、国际商务外语学院、金融管理学院、法学院、工商管理学院、会计学院、会展与旅游学院、商务信息学院、国际交流学院、继续教育学院、WTO研究教育学院、国际教育学院等学院。目前学校共有32个本科专业（含方向），4个一级学科硕士点，28个二级硕士学位授',2911613);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明医科大学',' ','云南',1,3,0,0,0,' ','暂空',' ','http://www.kmmu.edu.cn/','昆明医学院是云南省省属重点院校；1933年建院，是目前云南省最大的集医疗、教学、科研为一体的高等医学院校。学院始终高举邓小平理论伟大旗帜，以党的十六大精神和“三个代表”重要思想为指导，坚持社会主义办学方向，全面贯彻党的教育方针，以教学为中心，以人才培养为根本，以教育观念创新为先导，以改革和科技创新为动力，以全面推进素质教育、提高教学质量为目标，以德为首，依法治校，充分发挥高校教学、科研、社会服务的三大功能。学院立足边疆，服务云南，面向全国，以本科教育为主同时大力发展研究生教育，积极发展留学生教育',2055636);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大理大学',' ','云南',1,0,0,0,0,' ','暂空',' ','http://www.dali.edu.cn','大理学院是经国家教育部批准，由具有20多年办学历史的原大理医学院、大理师范高等专科学校合并组建而成的一所融医、文、理、工、艺、教育、法学、管理等综合性、多学科门类的云南省属普通本科高等院校。全日制在校学生8000余人。学校具有硕士学位授予权，2005年在多个学科招收硕士研究生40名。学校现有30个本科专业、30多个专科专业，部分专业和学科已经具有较高的教学和科研水平，在云南省具有一定的特色和优势。学校加强教育国际化合作，现在读外国留学生近200人，与国外多所大学建立了联合办学关系。学校坐落在国家',1908357);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安邮电大学',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xiyou.edu.cn','西安邮电大学是一所以工为主，以信息科学技术为特色，工、管、理、经、文、法、艺多学科协调发展的普通高等学校，是国家在西北地区布点的唯一邮电通信类高等学校、教育部“卓越工程师教育培养计划”实施高校、中国人民解放军后备军官选拔培养基地、全国首批试办边防军人子女预科班的高校。学校被中共陕西省委、陕西省人民政府授予陕西省“文明校园”称号，被陕西省教育厅授予“依法治校示范校”称号，校党委被省委教工委授予“陕西高等学校先进基层党委”称号。学校占地近1500余亩，纸质藏书近150万册。现有全日制在校生16000',3511935);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春师范大学',' ','吉林',1,4,0,0,0,' ','暂空',' ','http://www.ccsfu.edu.cn/','长春师范大学坐落在美丽的北国春城——长春，是吉林省重要的基础教育、学前教育师资及应用型高级专门人才培养基地，是具有博士学位授予权的省属师范大学。学校始建于1958年，时名长春师范专科学校，1963年易名为长春师范学校，1981年更名为长春师范学院，1986年起隶属中国有色金属工业总公司，1998年起按中央与地方共建，以吉林省管理为主的机制运行，2013年更名为长春师范大学。学校现有67个本科专业，拥有文、理、史、教育、管、法、工、艺术等八个学科门类；有20个教学单位，教职工1400余人，全日制在',2437848);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林建筑大学',' ','吉林',1,1,0,0,0,' ','暂空',' ','http://www.jliae.edu.cn/','吉林建筑大学是吉林省重点建设的普通高等学校，坐落于吉林省长春净月国家高新技术产业开发区。学校始建于1956年，时为国家城市建设部设立的新中国首批十所建筑类专门学校之一。1960年经国务院批准学校定名为吉林建筑工程学院，开始举办本科教育。1997年学校通过国家教委本科教学工作合格评价，2003年取得硕士学位授予权，2008年接受教育部本科教学工作水平评估取得“优秀”等次。2013年经教育部批准，学校更名为吉林建筑大学。学校已成为吉林省城乡建设领域高级专门人才培养基地、科技研发基地和决策咨询与服务基',2119534);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉轻工大学',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.whpu.edu.cn/','武汉轻工大学位于有“九省通衢”之称的湖北省武汉市，创建于1951年，是全国最早一所培养粮食行业专门人才的学校。曾先后隶属于原国家粮食部、商业部、国内贸易部，1998年实行中央和地方共建，以湖北省管理为主的体制，是湖北省和国家粮食局重点支持建设的普通高校。在62年的办学实践中，学校始终坚持“育人为本、质量立校、人才强校、特色兴校”的发展战略，现已形成了以轻工食品类学科为特色，农产品加工与转化领域相关学科优势明显，以工科为主干，工、管、理、文、经、农、艺、法等学科协调发展的多科性大学格局。2006年',2331174);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北工程学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.hbeu.cn','湖北工程学院是由湖北省人民政府举办的普通本科高等院校。学校坐落于武汉城市圈副中心城市——孝感市，距武汉市区50公里，距武汉天河国际机场30公里，京广铁路、京武高铁、京港澳高速、107、316国道穿城而过，交通便利，区位优势明显。校办学历史悠久。前身为创办于1943年的湖北省立第三师范学校，历经孝感大学、武师孝感分院、孝感师范高等专科学校、孝感学院几个阶段，先后有湖北职业技术师范专科学校（前身为华农孝感分院）和南方城乡建设学校（孝感市建筑工程学校）整体并入，2011年12月，经教育部批准更名为湖北',1151922);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江科技大学',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://218.7.67.24:8088/enroll/home_f_index.action','黑龙江科技大学始建于1947年，地处素有东方莫斯科之称的北国冰城哈尔滨市，位于风景秀美的松花江畔，是黑龙江省人民政府与国家安全生产监督管理总局共建高校，是国家教学工作水平评估优秀院校、拥有从学士、硕士到博士的完整人才培养体系，学校现有全日制在校生21800余人，在69载的办学历程中，培养出10余万名优秀人才。学校以本科毕业生就业率高享誉省内外。先后荣获“全国文明单位”、“全国五一劳动奖状”、全国就业50强单位、“全国青年就业创业教育先进集体”等15项国家级荣誉。学校现有工学、管理学、理学、文学、',2332679);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北文理学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.hbuas.edu.cn/','襄樊学院是一所国家二类多科性普通本科院校。学院座落于全国历史文化名城、湖北省第二大城市、湖北省三大旅游城市之一的襄樊市。学院地处国家级风景名胜区古隆中――诸葛亮故居。这里环境幽雅、景色宜人，具有深厚的文化底蕴，是莘莘学子求学、深造的理想之地。学院现有占地面积1500多亩，图书馆藏书80多万册。学院现有全日制在校本、专科学生近11000多人。学院现有教职工1250人，其中专任教师653人；具有教授、副教授职称的教师有206人，具有博士、硕士学位的教师有179人。襄樊学院现设有分属教育学、文学、史学',1242531);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北水利水电大学',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.ncwu.edu.cn','华北水利水电大学是水利部与河南省共建、以河南省管理为主的高校，是河南省重点支持建设的骨干高校。学校前身是1951年创建于北京的中央人民政府水利部水利学校,1954年改为水利部北京水利学校，简称北京水利学校。1958年北京水力发电学校、北京水力发电函授学院并入，成立北京水利水电学院。1969年迁至河北磁县岳城水库办学，1971年更名为河北水利水电学院。1977年迁至邯郸市办学，1978年更名为华北水利水电学院。1990年迁至郑州市办学，2000年整建制由水利部划转河南省管理。2009年水利部与河南',5543278);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('闽南师范大学',' ','福建',1,4,0,0,0,' ','暂空',' ','http://www.mnnu.edu.cn/','漳州师范学院是福建省重点建设的省属全日制本科院校，座落在历史文化名城、闽南开放城市----漳州市。学校前身是创办于1963年的福建第二师范学院，1966年因文化大革命停办，1970年被撤销。1977年以来，在福建省委、省政府和漳州市委、市政府的高度重视下，重选校址，重建校园，先后经历了龙溪地区中学师资培训班、龙溪师范大专班、漳州师范专科学校。1986年经原国家教委批准复办福建第二师范学院，并更名为漳州师范学院，与福建师范大学形成福建省“一南一北”的本科教师教育格局。1992年获得学士学位授予权，',1560365);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃中医药大学',' ','甘肃',1,3,0,0,0,' ','暂空',' ','http://www.gszy.edu.cn/','甘肃中医药大学是教育部批准的具有高等学历教育招生资格的省属普通本科院校。其前身甘肃中医学院于1978年经国务院批准成立，2015年4月，经教育部批准甘肃中医学院更名为甘肃中医药大学。学校具有博士、硕士、学士学位授予权，具有推荐优秀应届本科毕业生免试攻读硕士研究生资格和培养留学生资格，是同等学力人员硕士学位授予工作单位、高校教师在职攻读硕士学位培养单位。学校开设中医学、针灸推拿学、中西医临床医学、中药学、药物制剂、中草药栽培与鉴定、药学、中药资源与开发、临床医学、医学影像学、预防医学、护理学、医学',912252);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州财经大学',' ','甘肃',1,0,0,0,0,' ','暂空',' ','http://www.lzufe.edu.cn/','兰州商学院前身为1958年成立的甘肃职工财经学院，1981年7月经国务院批准正式成立，是甘、青、宁三省（区）唯一一所以本科教育为主的财经类高等院校。学校先后隶属原国家商业部、原国内贸易部领导，1998年7月，根据国务院有关文件精神，实行中央与地方共建、以地方为主的管理体制。学校现有段家滩、和平两个校区，校园占地面积1300余亩，校舍总建筑面积41.47万平方米，教学科研仪器设备总值4669.97万元，图书馆藏书164.82万册，是甘肃省重要的经济管理类文献资料信息中心。学校面向全国招生，现有全日',2191223);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州医科大学',' ','贵州',1,3,0,0,0,' ','暂空',' ','http://www.gmc.edu.cn/','贵州医科大学地处祖国西南的避暑之都——林城贵阳。其前身为创建于1938年的国立贵阳医学院，是当时隶属于教育部的九所国立医学院校之一。1950年更名为贵阳医学院，2015年更名为贵州医科大学，是贵州省唯一拥有医学学士——硕士——博士完整人才培养体系的省属重点高校。1938年3月，为谋战时急切之需，立西南之医学基础，在首任院长、我国早期热带病学专家、医学教育家李宗恩博士的带领下，杨崇瑞、朱章赓、汤佩松、贾魁、杨济时、刘维德、洪谦、林绍文、侯宝璋、张孝骞、郭秉宽、王季午、朱懋根、李瑞麟、李漪、王志均等',1791965);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州民族大学',' ','贵州',1,9,0,0,0,' ','暂空',' ','http://www.gzmu.edu.cn/','贵州民族学院创建于1951年5月17日，是新中国创建最早的民族院校之一，隶属贵州省人民政府，是贵州省重点建设的高校，是贵州省人民政府和国家民委共建高校，是教育部本科教学工作水平评估优秀高校。学校坐落在山清水秀、被誉为“高原明珠”的贵州风景名胜区——贵阳市花溪区，校园占地面积88.31万平方米，有校舍面积39.54万平方米，其中教学行政用房18.23万平方米。学校图书馆总藏书量162.8万册，其中纸质图书118.8万册，电子图书44万册。学校拥有一支结构合理，发展趋势好的师资队伍。现有教职工103',1292261);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州财经大学',' ','贵州',1,6,0,0,0,' ','暂空',' ','http://www.gzife.edu.cn','贵州财经大学始建于1958年，原名贵州财经学院，2012年更为现名。是一所以经济学、管理学学科为主体，法学、哲学、文学、教育学、艺术学、理学、工学等多学科协调发展的财经类大学。是贵州省委、省政府重点建设的贵州省经济管理人才培养基地。著名经济学家、“孙冶方经济学奖”获得者、南京大学原党委书记洪银兴教授任贵州财经大学名誉校长。学校坐落于贵州省贵阳市，现有花溪、河滨、鹿冲关三个校区，占地总面积5106.8亩，校舍建筑总面积784497平方米，教学仪器设备总值3.6亿元，图书资料(纸质图书189万余册，',1996339);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古医科大学',' ','内蒙古',1,3,0,0,0,' ','暂空',' ','http://www.immc.edu.cn/html/Default.html','内蒙古医科大学坐落于内蒙古自治区首府呼和浩特市，是一所以医学为主，集理学、管理学、工学、文学、法学于一体的多科性高等医药院校。学校成立于1956年，是新中国在少数民族地区最早建立的高等医学院校之一，当时隶属于国家卫生部，1958年划归内蒙古自治区管理。前身为内蒙古医学院，2012年3月，经教育部批准，更名为内蒙古医科大学。经过60年的建设和发展，内蒙古医科大学为国家培养各级各类人才7万余名，为自治区经济发展、社会进步、科技创新、文化传承、民族团结、边疆稳定做出了重要贡献，已成为一所具有鲜明办学特',1634326);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('内蒙古财经大学',' ','内蒙古',1,6,0,0,0,' ','暂空',' ','http://www.imufe.edu.cn/','内蒙古财经大学始建于1960年，经过50多年的建设与发展，现已成为一所以本科教育为主、同时承担研究生培养任务，以经济学和管理学为主，理学、法学、工学、文学等多学科融合发展，具有鲜明地区和民族特色的多科性财经大学。学校现有各级各类普通高等教育在校生21000多人。有理论经济学、应用经济学、工商管理、统计学4个一级学科、23个二级学科硕士学位授权点以及MBA(工商管理硕士)、MPACC(会计硕士)、MF(金融硕士)3个专业硕士授权点;54个本科专业，26个蒙汉双语授课专业;5个自治区重点学科，2个自',2215047);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东财经大学',' ','山东',1,6,0,0,0,' ','暂空',' ','http://www.sdufe.edu.cn/','山东财经大学坐落在山东省省会济南市，是财政部、教育部、山东省共建高校，是中国财政发展2011协同创新中心和中国会计改革与发展2011协同创新理事单位，是一所办学历史悠久、财经特色鲜明、综合实力突出，在国内外有一定影响的普通高等财经院校。学校于2011年7月4日由原山东经济学院和原山东财政学院合并组建而成。山东经济学院的前身山东财经学院，创建于1952年，是全国建校最早的财经类普通高校之一;1978年，经国务院批准更名为山东经济学院;1993年获得硕士学位授予权。山东财政学院始建于1986年，是财',5434619);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西科技大学',' ','广西',1,0,0,0,0,' ','暂空',' ','http://www.gxut.edu.cn/','广西工学院是经国务院批准成立，直属广西壮族自治区人民政府的地方工科院校。学校始建于1958年，1961年停止招生，1962年停办，1982年合并广西轻工学院、广西机械学院、广西石油化工学院恢复重建，1985年从南宁迁至柳州办学。学校占地面积1200多亩，建筑面积37.4万平方米。设有13个系，2个二级学院，5个教学部，1个独立学院，14个研究所（中心）。有42个本科专业，5个硕士学位授权点。是一所以工为主，理、管、文、经、法、教育等学科门类协调发展的多科性高等院校，并形成了从本科生教育到研究生教',2109505);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐鲁工业大学',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.spu.edu.cn','齐鲁工业大学是山东省重点建设的省属普通本科高校，是国务院学位委员会批准的硕士学位授权单位和全国首批学士学位授权单位。学校创建于1948年，是山东省建校较早的公办本科院校之一。建校60多年来，学校已发展成为拥有71个本科专业，9个硕士学位授权一级学科、64个硕士学位授权二级学科，10个工程硕士授权领域、3个艺术硕士授权领域、1个金融硕士授权领域，涵盖工、理、文、经、管、法、艺等学科门类的多科性大学，是首批山东特色名校工程学校和“山东省2011协同创新中心”牵头单位，被主流媒体评为山东省十大“最具社',3997847);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京建筑大学',' ','北京',1,1,0,0,0,' ','暂空',' ','http://www.bucea.edu.cn','北京建筑大学概况基本情况北京建筑大学(原北京建筑工程学院)是北京市唯一一所建筑类高等学校，始建于1936年。学校历经77年的发展与变迁，现已成为一所以工为主，工、管、理、法、艺等学科相互支撑、协调发展，博士、硕士和学士多层次培养的特色鲜明的多科性高校，是北京乃至国家城乡建设的重要力量。1936年为北平市立高级工业职业学校土木工程科1952年为北京建筑专科学校1953年为北京市土木建筑工程学校1958年为北京建筑工程学院1961年为北京建筑工程学校1977年经国务院批准为本科院校1982年获批为国',2671792);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州工商学院',' ','广东',1,6,0,0,0,' ','暂空',' ','http://www.gzgs.org.cn','广州工商学院是经教育部批准设立的民办全日制普通本科院校，具有普通高等学历教育和招收留学生资格。学院立足珠江三角洲，以社会需求为导向，契合地区经济发展特点设置、调整专业，面向全国9省、市招生，现有全日制在校生15266人。广州工商学院分广州花都和佛山三水两个校区。地处广东“三大经济圈”之一的“广佛肇”经济圈内，基础设施齐全，办学资源丰富。校园总面积1358.07亩，建筑面积437066平方米。建有8个校内实验实训基地，包含财经类、经管类、电子信息类、计算机类、物流管理类等5大专业的实验实训基地，教',473896);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北外国语学院',' ','河北',1,5,0,0,0,' ','暂空',' ','http://www.hbwy.com.cn/','我院是经国家教育部批准设立的省属普通高校.学院地处河北省省会石家庄市西南高校区,占地500亩，数万学子云集，招生纳入全国普通高校招生计划。学生毕业由我院颁发普通高校毕业证书。1、多语种的专业设置：1）学院三年制大专部十四个系开设十三个语种三十五个专业（国家高考统一录取）：英汉同声传译系：应用英语（英汉同声传译）英汉随行翻译系：应用英语（随行交替翻译）、报关与国际货运（英语报关员）、报关与国际货运（英语报检员）、金融与证券（银行证券与保险营销）商务英语系：商务英语日语系：应用日语（日汉同声传译）、',689882);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨信息工程学院',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://www.hxci.com.cn','哈尔滨信息工程学院是经教育部批准设置的全日制普通本科高等学校，其前身是哈尔滨华夏计算机职业技术学院，始创于1995年。在高职教育阶段，是黑龙江省首批省级示范性职业技术学院、全省唯一的国家示范性软件职业技术学院。学院现有两个校区，分别坐落在高校林立的利民开发区学院路和哈东新区大学城，校园总占地面积1045亩(70万平方米)，校舍建筑面积20.4万平方米，教学仪器设备总值3825万元，图书64.2万册。在省内同类院校中，校园占地总面积最大、生均占地面积位列第一，生均建筑面积位列第一，生均仪器设备值位',141971);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江工业学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://zs.jxdx.com/','黑龙江工业学院是教育部批准成立并备案的公办全日制普通本科学校。面向全国招生，毕业生面向全国就业。全日制在校生(含留学生)9000余人。学校分为东、西两个校区，占地80万平方米，建筑面积37.4万平方米。是省级花园式学校，全省师德建设十佳标兵单位，省级“三育人”先进集体，省“五一劳动奖状”获得单位，省级平安校园，全省高校科研工作先进集体。学校设有安全与环境工程系、电气与信息工程系、东方语言系、经济管理系、理工系、师范学院、体育系、文法系、西方语言系、继续教育部、国际教育与交流管理中心11个教学单位',317293);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐齐哈尔工程学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.qqhrit.com/','齐齐哈尔工程学院的前身，是成立于1958年的齐齐哈尔第一机床厂重型机床制造学院，文革前实施六年制（业余）本科和四年制(业余)专科教育，其间易名为齐齐哈尔第一机床厂“七·二一”工人大学、齐齐哈尔第一机床厂职工机电学院。1993年，学校为支持国有企业公司化改造与企业签订经济分离合同，成为全国第一家国企学校与企业分离的典型,易名为黑龙江东亚大学。2001年，省政府批准在黑龙江东亚大学基础上成立齐齐哈尔职业学院，结束了齐齐哈尔市没有地方普通高校的历史。2011年4月，教育部批准齐齐哈尔职业学院升格更名为',176121);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都东软学院',' ','四川',1,1,0,0,0,' ','暂空',' ','http://www.nsu.edu.cn','成都东软学院(学院在川招生代码：5177;在渝招生代码：5167)是经教育部批准设立，由东软联合亿达投资创办的一所以工学为主，兼办管理学、人文学等学科专业的二批次民办普通高等本科院校。学院开展普通全日制本、专科教育，招生纳入国家普通高等学校招生计划。目前，全日制在校生9000余名。学院位于成都市郊的世界文化遗产——都江堰-青城山风景区，校园独具川西园林特色，占地面积近40万平方米，总建筑面积约17万平方米。学院依托东软集团IT优势及在成都独特的IT布局，拥有一流的软硬件环境和国际化的师资队伍，并',525328);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川旅游学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.sctu.edu.cn','四川旅游学院是四川省人民政府主办的全日制普通本科高校。2013年4月18日经教育部批准，由原四川烹饪高等专科学校和原四川省农业管理干部学院合并建立。目前是全国唯一一所旅游类全日制普通本科高校。学院以管理学科为重点，协调发展工学、人文等学科，突出旅游类、食品科学类、休闲运动类和语言与艺术类专业，培养旅游业、餐饮业应用型高级人才。学院位于四川省成都市龙泉驿区红岭路459号，占地面积700多亩。校园环境优美，已建成国家AAA级旅游景区。配套设施齐全，为学生提供优良的学习生活条件，具备理想的读书环境。学',400166);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉商学院',' ','湖北',1,6,0,0,0,' ','暂空',' ','http://www.wbu.edu.cn/','公办高校国标代码：11654湖北招生代码：7220武汉商学院是经教育部批准的普通本科院校，有着50年的办学历史和行业办学特色。学校坐落于武汉经济技术开发区后官湖畔，校园占地面积一千余亩，建筑面积三十余万平方米，全日制在校生规模一万余人。重点培养服务区域经济社会发展所需要的应用型人才。学校建立了特色鲜明的学科专业体系，设有工商管理学院、商贸物流学院、旅游与酒店管理学院、体育学院·国际马术学院、烹饪与食品工程学院、机电工程与汽车服务学院、信息工程系、应用艺术系、应用外语系、思想政治理论课部等10个教',639257);
