insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西科技学院',' ','江西',1,0,0,0,0,' ','暂空',' ','http://zsb.jxut.edu.cn/','江西科技学院是经国家教育部批准成立的一所普通本科高校，具有颁发国家承认的统招本、专科学历资格，并具有学士学位授予权。学校前身为江西蓝天学院，成立于1994年。建校以来，学校秉承“以科教兴国为己任，为振兴中华而办学”的宗旨，坚持公益性办学方向，以市场需求为导向，走“精品+特色”的办学之路，为国家培养了16万余名毕业生，受到社会各界的好评，学校办学声誉日益提高。校园位于江西省省会城市--南昌，占地2000余亩，环境优美，布局合理，各类教学、生活设施一应俱全。建有汽车技术中心、现代制造技术中心、机械基',2515176);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('铁道警察学院',' ','河南',1,11,0,0,0,' ','暂空',' ','http://www.rpc.edu.cn/','铁道警察学院是公安部直属的我国唯一一所培养铁路公安专门人才的高等院校，1950年创建于北京，原名铁道部公安干部学校。学院先后辗转沈阳、上海、西安、唐山办学，1980年迁建河南郑州，更名为铁道部郑州人民警察干部学校，后更名为铁道部郑州人民警察学校、铁道部郑州公安管理干部学院。2000年，由铁道部划归公安部管理，改建为铁道警官高等专科学校。2013年，更名为铁道警察学院，开始举办全日制普通本科教育。建校以来，学院为铁路公安机关培养各类毕业生3万余人，培训在职民警近2万人。目前，在全国7万多名铁路公安',835338);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('萍乡学院',' ','江西',1,4,0,0,0,' ','暂空',' ','http://www.pxc.jx.cn','萍乡高等专科学校座落在举世闻名的中国工人运动的策源地?安源（江西萍乡）。我国卓越的地理学家、地图学家、遥感应用和地理信息系统科学的创建者和奠基人、中国科学院资深院士陈述彭任名誉校长。学校创办于1978年，是教育部批准的全日制公办高等教育院校。多年来，学校坚持办学的科学性、前瞻性、实用性、多样性，坚持走发展、提高、扩规、增效之路，努力培养和造就具有创新精神和实践能力的实用型、复合型、竞争型、创造型人才，取得了良好的办学效益。学校先后接受并通过了全省高校校风建设大检查，高专办学水平评估，高校党建工作',278058);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('景德镇学院',' ','江西',1,0,0,0,0,' ','暂空',' ','http://www.jdzu.jx.cn/','景德镇高等专科学校是一所全日制综合性院校，座落在全国历史文化名城，世界闻名的千年瓷都－江西省景德镇市。1977年，景德镇市教师红专学校；1978年?1982年，景德镇市教育学院；1982年?1993年，景德镇教育学院；1993年6月至今，景德镇高等专科学校；2004年5月，江西省人民政府批准将电大景德镇市分校、景德镇陶瓷职工大学、景德镇师范学校、景德镇教师进修学校并入景德镇高等专科学校，筹建景德镇学院。建校二十八年来，在上级党委和政府的正确领导及社会各界的关心支持下，学校办学规模不断扩大，办学实',296579);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西应用科技学院',' ','江西',1,0,0,0,0,' ','暂空',' ','http://www.jxcsedu.com/zhaosheng/','学院是江西省教育厅直属，国家教育部注册备案的一所现代化多学科综合类高等学府。计算机科学技术学院、机电工程学院、经贸管理学院、人文外语学院、艺术学院、和国际交流部。自1984年至今已有20多年的办学历史，积累了丰富的教学经验。学院座落于“物华天宝，人杰地灵”的历史名城南昌市，紧邻梅岭国家森林公园风景区，与江南三大名楼之一的滕王阁隔江相望，环境优美，交通便捷，校内花团锦簇，芳草如茵，是求学读书、陶冶情操的理想境地。校园规划用地3200余亩，现有建筑面积28万平方米，在校本、专科生近20000人。专、',178614);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西工程学院',' ','江西',1,1,0,0,0,' ','暂空',' ','http://web.ygxy.com','--中国著名万人高校，特色化新型的综合性高校一、延续神话，创造梦想学院坐落在江西省新余市。新余东靠上海,南接广东,西通长沙,北至武汉，地理位置优越，是江西城镇化水平、生活水准最高的城市，是国画大师傅抱石的故乡和“仙女下凡”神话的发源地。近年来，新余努力打造江西亚中心城市，钢铁、新能源、新材料三大产业异军突起，世界“硅片之都”、“江西硅谷”初见雏形。现代技术与传统文化对接，高新产业与国粹精髓融汇，让你学有所成，学有所用。学院建有仙来、天工两大校区。校园占地面积2060亩，建筑面积45万平方米。已为',200099);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州航海学院',' ','广东',1,1,0,0,0,' ','暂空',' ','oldname": "广州航海学院  曾用名：广州航海高等专科学校','广州航海高等专科学校是一所直属广东省教育厅领导的全日制普通高等专科院校。前身为交通部广州海运学校，创办于1964年，1992年7月经原国家教委批准升格为独立建制的大专院校并更改为现名，1998年划转广东省人民政府管理，2002年8月，原广州航务工程学校并入成为学校西校区。是华南地区唯一一所独立建制的航海高等院校。学校坐落在广州珠江之滨，毗邻著名的黄埔港和广州经济技术开发区，现有校园面积428183.55平方米；校舍总建筑面积170721.52平方米（含在建项目）：其中教学行政用房面积114058',781011);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东理工学院',' ','广东',1,1,0,0,0,' ','暂空',' ','http://zsb.gdlgxy.com:81/','肇庆科技职业技术学院是广东省人民政府批准、国家教育部备案具有普通高等学历教育招生资格的省属全日制普通高等学校。学院坚持以服务为宗旨、以就业为导向，致力于培养社会急需的高等应用型专门人才，是一所以工科为主，经管、外语、艺术结合的综合性学校。2008年在校生达9400多名，是我省规模大、教学质量高，毕业生充分就业的高等职业教育和人才培养基地。2004年学院被评为全国和广东省先进民间组织；2005、2006、2007、2008连续四年荣获“广东省民办高校十强”称号。学院地处国家级历史文化名城肇庆市城区',163419);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北理工学院',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.hbpu.edu.cn/','湖北理工学院是一所以工为主，工理结合，工、理、经、管、医、文、教、艺等学科门类协调发展的省属普通本科高校。学校位于华夏青铜文化发祥地之一的湖北省黄石市。交通十分便利，距武汉市50公里，武九铁路贯穿全市，大广、沪渝、福银、杭瑞高速在市区交汇，毗邻京广、京九两条大动脉。学校依山傍水，校园布局合理，环境优美。2010年，通过教育部本科教学工作合格评估。学校自1975年建校，历经传承演变，先后经历了湖北省高等院校黄石高工班、武汉工学院(现武汉理工大学)黄石分院、黄石高等专科学校、2004年成立黄石理工学',1528211);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西中医药大学',' ','广西',1,3,0,0,0,' ','暂空',' ','http://www.gxtcmu.edu.cn/','广西中医药大学位于有“中国绿城”美誉的广西壮族自治区首府南宁市，是我国5个少数民族自治区中唯一独立建制的高等中医药院校。学校创建于1956年，其前身为1934年成立的广西省立南宁医药研究所。1970年南宁医学专科学校并入，组成新的广西中医学院。1976年开始招收留学生，1978年正式培养硕士研究生，是我国第一批开办研究生教育的中医药院校之一，1996年以来先后与国内8所大学联合培养博士研究生。2003年教育部本科教学水平评估“优秀”，2009年与广州中医药大学联合设立博士后科研流动工作站;201',1178179);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海视觉艺术学院',' ','上海',1,8,0,0,0,' ','暂空',' ','http://www.siva.edu.cn','复旦大学上海视觉艺术学院于2005年9月正式揭牌成立。是一所经国家教育部批准，由复旦大学联合上海文化广播影视集团、文汇新民联合报业集团、上海精文投资有限公司、上海精文置业（集团）有限公司、上海申教投资有限公司、上海世博（集团）有限公司、中房置业股份有限公司、香港英皇公司、上海第九城市信息技术有限公司、上海盛大网络发展有限公司等共同投资创建，以创新体制、创新机制和创新模式成立的新型艺术类本科院校。学校以国有公投为主（国有资金占90%以上），其他社会力量共同参与投资建设，引进现代大学制度和企业管理制',302328);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('文华学院',' ','湖北',1,1,0,0,0,' ','暂空',' ','http://www.hustwenhua.net','文华学院(原华中科技大学文华学院)创办于2003年5月26日，是经教育部批准设置的普通全日制本科高校，其前身是享有盛誉的华中科技大学文华学院。【教育家办学】中国院校研究会会长、华中科技大学学术委员会副主任、博士生导师刘献君教授任文华学院院长;中国工程院院士张勇传教授担任名誉院长。华中科技大学选派国家教学名师、著名学者等一批专家，担任学校、学部和系领导及学术带头人。学校建校以来一直坚持开展院校研究，将研究与实践相结合，形成了具有独立学院特色的教育研究氛围，提高了学校整体科学人文管理水平。著名教育专',2025963);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('汉口学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.hkxy.edu.cn/hkxyzs/','汉口学院是一所经教育部批准成立的多学科、综合性的普通本科学校。学校创办于2000年，始称华中师范大学汉口分校，是全国最早成立的四所独立学院之一。2011年经湖北省人民政府申报，国家教育部批准更名为汉口学院，也是全国首批独立学院转设为普通本科高校的17所学校之一。学校有中级职称评审权，学士学位授予权。学校连续五年排位全国同类院校十多名。代码为11800。学校取名为“汉口”，是因为2000年申报学校校址在汉口，2003年学校最终选址在江夏。汉口曾是中国的直辖市，是武汉市的旧称，是近代中国商业、金融、',667302);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州财经学院',' ','河南',1,6,0,0,0,' ','暂空',' ','http://www.zzjm.edu.cn','郑州财经学院是一所经教育部批准成立的全日制民办普通本科院校。位于河南省会郑州市区。学院占地900亩，建筑面积57万平方米，教学科研仪器设备总值近7000万元;建有功能先进的校内实验实训室82个，校外实习实训基地230个;教学用计算机2800台，多媒体网络教室和多媒体语音室257个;图书馆馆藏各类图书200.23万册;有教授、副教授、讲师等专职教师570名，在校生近万人，设9个二级学院和3个教学部，涵盖管理学、经济学、工学、文学、艺术等学科;千兆宽带和无线网络覆盖整个校园;学生宿舍实行标准公寓化管',876359);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉工程科技学院',' ','湖北',1,1,0,0,0,' ','暂空',' ','oldname": "武汉工程科技学院  曾用名：中国地质大学江城学院','中国地质大学江城学院是经教育部批准设立的独立学院。中国地质大学(武汉)是教育部直属、“211工程”建设的全国重点大学。学校办学历史悠久，学科专业齐全，现有5个国家级重点学科、12个省部级重点学科,62个本科专业，119个硕士点，38个博士点，13个工程硕士学科领域，3个博士后流动站，有国家地质学理科人才培养与科学研究基地、国土资源部地质工科人才培养基地，具有培养学士－硕士－博士的完整教育体系；师资力量雄厚，现有教授370人，副教授650人，其中中国科学院院士9人，博士生导师230余人。中国地质大',1253959);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武昌理工学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.wut.edu.cn/','一、学校概况武昌理工学院是经教育部批准设置的普通本科高校，前身是享有盛誉的武汉科技大学中南分校。经近20年发展，已成为一所以工学、管理学为主、多学科协调发展的知名高校。学校位于“武汉?中国光谷”，占地1413亩，建筑面积40余万平方米。学校依山傍水，座落在美丽的梅南山麓,我国最大的城中湖汤逊湖三面环抱，欧式风格校园建筑掩映在青山绿树之中，校园风景如画，与北京大学、武汉大学一起被公认为全国十大美丽校园之一。学校设有生命科学学院、信息工程学院、城市建设学院、商学院、文法与外语学院、艺术设计学院、音乐',889179);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉东湖学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.wdu.edu.cn/','●教育部批准设置的全日制普通本科高校●教育办学条件评估验收合格高校●全国十大最具办学特色示范院校●全国最具就业竞争力示范院校●校本课程开发与实施全国特色示范学校●全国创建“平安校园”示范学校●湖北省高等学校创新能力提升计划（“2011计划”）首批入选和牵头高校武汉东湖学院简介培养“一体”“两翼”的高层次应用型人才的摇篮☆地理位置优越学校坐落于素有“九省通衢”之称的江城——武汉，位于全国最大的城中湖——汤逊湖之滨，东临国家光电子产业基地——“中国光谷”,西接武昌大道、京港澳高速公路、107国道，位',1733835);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁理工学院',' ','辽宁',1,0,0,0,0,' ','暂空',' ','http://www.lise.edu.cn/','辽宁理工学院(原渤海大学文理学院)始建于1999年，隶属于辽宁省政府主管的一所以理工为主，多门类、多层次、多种办学形式的综合性大学。位于渤海之滨的历史文化名城辽宁省锦州市，是锦州市四所普通本科高校之一。校园环境优美，占地1134.2亩。学院设有8个院系，28个本科招生专业和13个专科招生专业，涵盖7个学科门类。现有全日制本专科在校生近6000人。学院现有专、兼职教师393人，其中具有博士、硕士学位的254人，具有教授、副教授专业技术职务的178人，辽宁省优秀青年骨干教师5人，双师型教师40人，此',838802);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京黄埔大学',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.hpdx.cn/','黄埔军校师生在近代历史上，为了民族的解放、祖国的统一与富强作出了突出的贡献。黄埔军校同学会是邓小平同志为促进海峡两岸统一而批准成立的，首任会长徐向前元帅，聂荣臻元帅任顾问。在新的历史时期，北京市黄埔军校同学会于1993年创建了北京黄埔大学，以继承和延续爱国革命的黄埔精神，谱写新世纪人才培养的新篇章。学校实行董事会领导下的校长负责制。李运昌（黄埔四期，全国黄埔军校同学会会长）任名誉董事长，黎原将军（黄埔十一期，原兰州军区副司令员）任董事长，贺铿教授（人大常委、九三学社副主席）、戴伦彰教授（中国国际',107347);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽中医药大学',' ','安徽',1,3,0,0,0,' ','暂空',' ','http://www.ahtcm.edu.cn/','安徽中医药大学创建于1959年，其前身为1952年创立的安徽省中医进修班(学校)。1959年，安徽省政府正式批准成立安徽中医学院，郭沫若先生题写校名。1970年，安徽中医学院并入安徽医学院。1975年，国家教育部批准恢复安徽中医学院。2000年，安徽省医药学校并入安徽中医学院。2011年，省政府批准在安徽中医学院的基础上组建成立安徽省中医药科学院。2013年，教育部同意安徽中医学院更名为安徽中医药大学。学校是国家中医临床研究基地、国家中药现代化科技产业(安徽)基地、国家中医药国际合作基地、国家药',1113126);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('信阳农林学院',' ','河南',1,2,0,0,0,' ','暂空',' ','http://www.xyafc.edu.cn/','信阳农业高等专科学校位于历史名城信阳市南郊，西依贤山，北临浉水，南望鸡公山，107国道依校而过。学校创建于1910年，始名汝宁府中等实业学堂。建校90多年来，为社会输送各类人才4万余人，他们奋战在各行各业，有的走上了领导岗位，有的成为科技专家、技术骨干，为祖国的经济建设和社会进步作出了突出贡献。今天的信阳农专，已发展成为一所覆盖农业产前、产中、产后全过程，集农、理、工、管、文为一体，具有鲜明豫南特色和良好办学基础的综合性农业高等专科学校。学校设有动物科学系、水产科学系、农业科学系、园艺林学系、规',246497);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港城市大学',' ','香港',1,0,0,0,0,' ','暂空',' ','http://www.cityu.edu.hk/cityu/index-sc.htm','一所致力提供专业教育及从事应用研究的大学香港城市大学(简称“城大”)是香港特别行政区政府资助的八所大专院校之一，是一所年轻向上、富于进取的国际化多科性大学。大学恪守「敬业乐群」之校训，致力提供专业教育及从事应用研究，每年为约25,000名学生提供超过180项不同层次及专业的优质本科及研究学位课程。建校至今，城大已建立了其富有特色的校园文化，发展为尊重学术自由、重视学术成就、学术氛围浓厚的大学。大学现设有商学院、人文及社会科学学院、科学及工程学院、创意媒体学院和法律学院一所高度国际化的大学城大拥有',362124);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁警察学院',' ','辽宁',1,11,0,0,0,' ','暂空',' ','http://www.lnpc.cn/','辽宁警官高等专科学校是辽宁省唯一的一所省属公安类普通高等学校。学校创建于1960年，1985年建立大专，1992年被公安部评为A类学校，1995年在全国同类公安高校中率先独立试办本科专业。建校47年来，为全国和辽宁公安、司法、安全、海关、武警边防部队等系统培养了2万多名毕业生。学校坐落在辽宁省美丽的海滨城市——大连。2006年10月，占地808亩的新校园建成并投入使用，学校实现整体搬迁。新校园依山傍海，环境幽雅，有教学楼、实验楼、图书馆、训练馆、食堂、学生公寓等建筑，总建筑面积近15万平方米。分',499928);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港教育大学',' ','香港',1,0,0,0,0,' ','暂空',' ','http://www.eduhk.hk','历史简介香港教育大学（教大，由香港教育学院正名）是香港特区政府资助的八所公立大学之一，历史可追溯至1853年开办的首个正规在职教师培训课程及1881年由香港总督轩尼诗爵士成立的首间师范学校。在1994年，香港政府将五所前师范学院合并成香港教育学院。2016年，香港特区政府立法会三读通过《2016年香港教育学院（修订）条例》，有关条例于2016年5月27日刊宪，标誌香港教育大学（教大）正式诞生。于2016年QuacquarelliSymonds（QS）世界大学排名中，本校在教育领域位列亚洲第二、全',100338);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('澳门理工学院',' ','澳门',1,1,0,0,0,' ','暂空',' ','http://www.ipm.edu.mo/zh/index.php','一、为何选择澳门理工学院?l澳门公立应用型高等院校，获国家教育部认可，开办课程紧贴国际社经发展，就业前景美好。l亚洲首家院校通过英国高等教育质量保证局(QAAUK)院校评鉴、大中华地区首家院校获颁“亚太教育质量奖”，被评为中国四星级大学，办学水平与课程素质获国际肯定。l设多元化海外交流、实习机会(如葡萄牙、英国、美国、澳洲等)，致力培养具国际竞争力的优秀人才。l师生比例低，小班制教学有利师生互动，提升学习成效。l以学生为本，学习支持全面，校园活动多样，促进学生全人发展。l奖学金丰富多样，鼓励优秀',274375);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆人文科技学院',' ','重庆',1,0,0,0,0,' ','暂空',' ','http://zsw.cqrk.edu.cn/','重庆人文科技学院，系原西南大学育才学院，建立于2000年5月。2013年4月，经教育部批准转设为独立设置的普通本科高校，成为重庆市第一所、也是唯一一所获得批准转设的民办普通本科高校。2013年初，学校被教育部确定为全国应用技术型大学战略研究试点高校之一，正在积极创建西部领先、全国有重要影响的应用技术型大学。学校位于重庆市合川区，占地面积2048亩，校舍面积约50万平方米，教学、科研、实验实训设备总值上亿元，有校内实验（实训）室114个、校外实习实训基地92个，各类藏书286万余册。学校设立10个',1300049);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港中文大学',' ','香港',1,0,0,0,0,' ','暂空',' ','http://www.cuhk.edu.hk','香港中文大学在1998年开始招收内地本科生，至今已有近千名内地学生在中文大学就读。本校由2005年开始通过全国普通高校统一招生计划招收内地本科生，今年更获国家教育部列入提前录取批次。香港中文大学是一所公办研究型综合大学，现有文学、工商管理学、教育、工程学、医学、理学、社会科学及法律学八个学院，共有61个学系和60个研究院学部，提供过百项学士、硕士及博士学位课程，涵盖面极为广泛。香港中文大学的内地招生计划分设文、理、工、商四个学科门类。文科类包括文学院、教育学院、法律学院及社会科学院；理科类包括医',837734);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('茂名学院高州师范分院',' ','广东',1,4,0,0,0,' ','暂空',' ','http://www.gdgzsf.cn','茂名学院高州师范分院（GaozhouNormalCollege,MaomingUniversity）坐落在粤西文化名城——高州市城区，北倚金子岭，南临鉴江，依山傍水，风景秀丽。校园面积近200亩，建筑面积10多万平方米。拥有科学楼、教学楼、图书馆、音乐楼、大礼堂、体育馆、标准运动场等现代化教育教学设施设备，实现了校园环境园林化，学生宿舍公寓化，是莘莘学子修身立志、求学成才的好地方。茂名学院高州师范分院具有悠久办学历史。其前身为“茂名县立师范学校”和“广东省立高州女子师范学校”，创办于1930年。',95131);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('海南热带海洋学院',' ','海南',1,0,0,0,0,' ','暂空',' ','http://www.qzu.edu.cn/','海南热带海洋学院坐落于美丽的国际热带滨海旅游城市——三亚市。是中国最南端唯一的一所由海南省人民政府、国家海洋局、中国海洋石油总公司、三亚市、三沙市等单位共建的全日制应用型公办本科院校。是教育部十大“教育援外基地”之一，外交部、教育部“中国-东盟教育培训中心”之一，国务院学位委员会“服务国家特殊需求人才培养项目”旅游管理硕士专业学位研究生培养试点单位，三亚国家大学科技园的依托院校，上海合作组织大学中方成员院校，同时也是海南省南部地区的人才培养摇篮和知识创新、社会服务、文化传承基地。学校创办于195',5499347);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港演艺学院',' ','香港',1,8,0,0,0,' ','暂空',' ','http://www.hkapa.edu','教育香港演艺学院于一九八四年由香港政府成立，乃本地唯一一所提供表演艺术、舞台科艺及电影电视的专业教育、训练及研究之专上学府。学院的教学政策反映了香港多样文化的特色，中西方艺术传统风格并重。演艺学院现有以下学院/学科：-舞蹈学院-戏剧学院-电影电视学院-音乐学院-科艺学院-中国戏曲五间学院均提供文凭至学士荣誉学位程度之课程。演艺学院于一九九二年经由香港学术评审局评核而获得学位颁授资格。学院之学位课程均由此学术评审局甄审。表演演艺教育的精髓在于表演本身，而香港演艺学院正为香港最重要的表演场地之一，每',100454);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春科技学院',' ','吉林',1,0,0,0,0,' ','暂空',' ','http://www.jlaudev.com.cn ','学院成立于2000年5月，是经国家教育部确认，由省属重点大学吉林农业大学与吉林东华教育集团联合创办的独立学院。校园坐落在风光秀丽的长春市南花园——双阳湖畔。学院贯彻“精心育人、服务社会”的办学宗旨，恪守“质量立校、人才兴校、特色强校、科学治校”的办学理念。锁定培养高素质应用型人才的培养目标定位。坚持以市场需求为导向，不断加强学科专业建设。现开设27个本科专业，34个专业方向，12个高职专业。形成了农、理、工、药、管、文多学科协调发展的格局。目前在校生10000多人。拥有良好的办学条件：现占地面积',604033);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('昆明医科大学海源学院',' ','云南',0,3,0,0,0,' ','暂空',' ','http://www.kyhyxy.com','昆明医学院海源学院是昆明医学院适应国家和云南省经济社会发展的需要，于2001年经省教育厅批准创办，2004年国家教育部确认的全国首批以新的机制和模式创办的独立学院。是云南省唯一一所医学类本科独立学院。昆明医学院海源学院依托云南省最大的集医疗、教学、科研为一体的多学科、多层次高等医学院校——昆明医学院的优质教育资源与先进的管理理念，努力为社会培养高素质的医药卫生专门人才。学院现设有临床医学、临床医学（整形美容方向）、临床医学（肿瘤学方向）、医学影像学、医学检验、口腔医学、药学、护理学、劳动与社会保',1188920);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黄河交通学院',' ','河南',1,1,0,0,0,' ','暂空',' ','http://www.zjtu.edu.cn','黄河交通学院是经国家教育部批准设置的河南省唯一一所培养交通专业人才为主的普通本科高等院校，前身为郑州交通职业学院。建校20余年来，学校秉承“修德、启智、博学、笃信”的校训，始终面向现代交通运输和现代汽车制造与服务行业办学，先后为社会培养了5万余名优秀人才，为中原崛起、河南振兴、富民强省提供强有力的智力支持和人才支撑。黄河交通学院位于郑州黄河北岸武陟迎宾大道，地处郑州“一刻钟经济圈”内，战略地位尤为突出。学校置身于龙泉湖畔、嘉应观旁，与国家旅游名胜“郑州黄河风景名胜区”隔河相望，乘坐城际列车从郑州',308119);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江外国语学院',' ','黑龙江',1,5,0,0,0,' ','暂空',' ','http://www.hiu.edu.cn','黑龙江外国语学院是黑龙江省唯一一所语言类普通高等本科院校。学院自成立以来，坚持走教育与国际接轨之路，积极借鉴国际先进的教学模式，充分利用国内外优质教育资源，经过多年的办学实践，各项事业取得可喜的进步，赢得了良好的社会声誉。学院加入世界大学校长联盟、国际商学院联盟、中国东北地区•俄罗斯西伯利亚及远东地区大学校长联盟、美国职业教育协会、中俄亚洲大学联盟;是英国剑桥大学国际考试中心、英国伦敦工商会考试局LCCI考试中心、英国特许注册会计师工会ACCA考试中心;曾先后被授予“全省民办非企业单位自律与诚信',793661);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('三亚学院',' ','海南',1,0,0,0,0,' ','暂空',' ','http://www.sanyau.edu.cn/','三亚学院——亿元引才省市共建迈向三亚大学三亚学院始建于2005年，位于中国最大经济特区海南省，属地三亚市享有“东方夏威夷”美誉，由全球型企业吉利集团投资，是三亚市创办最早、规模最大的民办本科院校，填补了海南省中南部高等教育缺失的空白，是中国成长最快、最具竞争力的民办大学。学校校园占地面积3000亩，建筑总面积51.77万平方米，总投资30亿元。学校下设15个教学学院和1个教学部，开设61个本科专业和方向，涉及法学、文学、经济学、管理学、工学、理学、农学、教育学、艺术学等9个学科门类。拥有学术交流',1858338);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州财经大学长青学院',' ','甘肃',0,6,0,0,0,' ','暂空',' ','http://changqing.lzcc.edu.cn','兰州商学院长青学院成立于2001年，于2004年2月被教育部确认为甘肃省首批独立学院，属本科层次普通高等学校。学院面向全国招生，招生纳入国家普通高校统招计划。长青学院始终坚持“厚基础、宽口径、应用型、有特色”的培养目标，以教育教学质量为中心，充分利用现有的优质高等教育资源，在保持和发挥传统财经类学科优势的同时，积极发展和培育社会急需的应用学科，以一流的教师队伍为依托，以完善的基础设施为保障，经过七年坚持不懈的努力，已经发展成为一所在校生近6300人，以本科教育为主体，管理学、应用经济学为重点，多',314854);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江水利水电学院',' ','浙江',1,1,0,0,0,' ','暂空',' ','http://www.zjwchc.com/','浙江水利水电专科学校位于风景秀丽的历史文化名城杭州市，具有五十多年的办学历史,是一所培养面向水利水电行业和区域经济发展需要的高等应用型专业技术人才的普通高等院校。学校现占地面积805亩，建筑面积22.59万平方米,在校普教生6900多人；目前在册教职工500多人，其中专任教师353人，教授34人，副教授92人，享受国务院特殊津贴3人，省“151人才工程”第二、三层次人选3人，省级学科带头人2人，浙江省教学名师1人，形成了一支有较深理论造诣、丰富教学经验和一定工程实践经验的能适应学校发展需要的师资',324699);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京文理研修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.bjwlxy.net','北京文理研修学院(简称“北文理”)坐落于北京市海淀区中关村科技园区，地理位置优越，教育资源丰厚。周边汇集了北京大学、清华大学、中国人民大学、北京师范大学、北京邮电大学、北京理工大学、北京科技大学、北京电影学院等著名高校。学院位于北京航空航天大学校园内,校园植被葱郁，环境优美，文化氛围浓厚。学院除具有中央空调教室外还有大型波音飞机模拟舱、图书室、形体房、化妆室、计算机室、多媒体语音室等教学设施。北京文理研修学院始建于1993年，前身为北京达德大学，是由民盟北京市委与香港达德学院北京校友会共同申请举',27153);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川工商学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.stbu.edu.cn','四川师范大学成都学院，其前身是2001年成立的四川师范大学电子信息工程学院，2004年经教育部批准设立为独立学院，面向全国统一招生，实施全日制本、专科学历教育，培养具有社会责任感、创新精神和实践能力的高素质应用型技术技能型人才。学校占地1600余亩，在成都和眉山有两个校区，成都校区位于府南河源头成都高新西区，眉山校区位于东坡故里眉山市岷东新区。学校下设9个二级学院，设有46个本专科专业，其中本科专业25个，涵盖人文学科、管理学、工学和社会学科。现有在校生17000余人，其中本科生11000余人。',1630863);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('燕京理工学院',' ','河北',1,1,0,0,0,' ','暂空',' ','http://www.ncbuct.com/','学校简介燕京理工学院，是经教育部批准，由优秀独立学院的杰出代表——北京化工大学北方学院转设而来的全日制普通本科院校。招生列入全国统一计划。经过10年的发展，已成为在校生规模超过20000人的现代化大学。地理位置学校所在地燕郊，东临天津，西与北京城市副中心通州隔河相望，距天安门38公里，北接北京顺义，距首都机场15分钟车程。燕郊是环渤海经济圈上的明珠，是京津冀一体化的核心区域，是紧邻北京的全国著名高校和科研院所的聚集地。办学理念以“勤奋创新、璞玉成器”为校训，坚持“育人为本，服务社会；质量立校，特',2492439);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春建筑学院',' ','吉林',1,1,0,0,0,' ','暂空',' ','http://www.jladi.com/','一、学校历史沿革长春建筑学院成立于2000年6月，原名为吉林建筑工程学院建筑装饰学院。2004年1月，经教育部确认为实施本科教育的独立学院。2011年4月，经教育部正式批准转设为独立设置的民办普通本科高校，并更名为长春建筑学院。二、学校校训、精神、教风、学风校训：明德、笃学、躬行、善建。精神：和衷共济，追求一流。教风：学而不厌，诲人不倦。学风：敏而好学，不耻下问。三、学校办学理念以人为本、突出特色、从严培育、质量立校。以人为本：即教育以学生为本，办学以教师为本，把凝聚人才、培育人才作为办学的着眼',901654);
