insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国信息大学',' ','北京',1,0,0,0,0,' ','暂空',' ','http://www.cieu.org.cn','学校简介中国信息大学是由国家信息中心于1993年创办的，如今已发展成为一所民办高等教育学府。建校之初由国家信息中心主任王春正任学校名誉校长、国家信息中心常务副主任刘鹤担任董事长、副主任杜链担任校长。1995年7月，时任国务院副总理邹家华为学校题写了校名。为更有效地利用国内外的办学资源，深化教学改革，2003年，学校经北京市教委批准改制重组，现已发展成为一所综合性、全日制高等学校。办学理念校训：独立思考、自主学习我国高等教育已经由精英教育阶段发展到大众教育阶段，为此学校确立了“服务多元化信息社会，',805162);
