insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('重庆第二师范学院',' ','重庆',1,4,0,0,0,' ','暂空',' ','http://www.cque.edu.cn/','重庆第二师范学院是一所教育部批准成立、由重庆市教委主管的公办普通本科高等院校。学校始建于1954年，具有悠久的办学历史和鲜明的办学特色，是全国大学生文化素质教育基地、重庆市人文社会重点研究基地、重庆市服务外包人才培训基地、重庆市教师教育创新实验区、重庆市学前教育发展中心、重庆市统筹城乡教师教育研究中心、重庆市中小学教师和教育管理干部培养培训的重要基地。学校现有南山、学府大道两个校区，占地面积621.4亩。两个校区均依山构建，绿荫叠翠，层次分明，错落有致。现设有教师教育学院（教师教学发展中心）、学',659467);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东财经大学东方学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdor.cn','山东财经大学东方学院是2005年经教育部批准，由山东财经大学与山东黄金集团联合举办，以实施全日制普通本科教育为主的独立学院。学校坐落于闻名世界的旅游胜地——山东省泰安市，北倚泉城济南，南临圣城曲阜，“山(泰山)水(泉城)圣人(孔子)”荟萃于此，文化底蕴丰厚;京沪铁路、京福、京沪高速公路及104国道在此交汇，交通十分便利。学校校园优美，环境宜人，建有现代化的实验室、学生公寓、太阳能浴池、学生餐厅以及全塑胶标准操场等教学、生活和文体设施。学校先后荣获“全国教育系统先进集体”、“2010全国现代教育理',802724);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南通理工学院',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://www.ntpc.edu.cn','南通理工学院是经国家教育部批准建立的一所全日制普通本科院校，是江苏省首批登记为事业单位法人的民办大学;是全国非营利性民办高校联盟26家发起单位之一。目前学校有南通和海安两个校区，占地面积1100亩，建筑面积30余万平米，各类在校生近万人，设有机械工程学院、汽车工程学院、建筑工程学院、经济管理学院、计算机与信息工程学院、电气与能源工程学院、船舶与海洋工程系、继续教育学院、创新创业学院和基础教学学院等10个院系，设置本专科专业40多个。学校高度重视师资队伍建设，充分发挥体制机制优势，采取“外聘选精英',129742);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连财经学院',' ','辽宁',0,6,0,0,0,' ','暂空',' ','http://www.dlufe.edu.cn/','大连财经学院是经国家教育部批准，由原东北财经大学津桥商学院转设组建的一所以经济学、管理学为主，兼有法学、文学、艺术学等五大学科门类交叉渗透、协调发展的高等财经学府。学院坐落于辽东半岛最南端、美丽的滨海城市——大连经济技术开发区。自2001年学院创立以来，学院一直秉承母体学校东北财经大学“博学济世”的传统，以培养德、智、体全面发展，适应社会经济发展和现代化建设需要，基础扎实、能力强、素质高、富于创新意识的应用型专门人才为目标，坚持走规范化、智能化、现代化和国际化的办学道路。硬件设施：大连财经学院校',1368400);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉设计工程学院',' ','湖北',0,0,0,0,0,' ','暂空',' ','http://www.hnctxy.com/','武汉设计工程学院一颗冉冉升起的新星[菁菁校园求学乐园]校园紧依波光潋滟的汤逊湖，湖中碧波荡漾，湖面白鹭时翔时集，远处水天一色。一栋栋古朴而典雅的教学楼与潺潺流水交相辉映，散发着浓郁的古典艺术气息，古典与现代融为一体。校园内花香鸟语，静谧雅致，是学子修身养性、读书求学的理想园地。学校前身是华中农业大学楚天学院，由教育部直属的“211”工程建设重点大学、国家本科教学工作优秀学校——华中农业大学申办，按照新机制、新模式举办的全日制普通本科层次的高等学校，2015年4月经教育部批准更名为武汉设计工程学院',507235);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州商学院',' ','贵州',1,12,0,0,0,' ','暂空',' ','http://www.gzcc.edu.cn','贵州商学院是经教育部批准的省属公办全日制普通本科高校，由贵州省人民政府举办。学院位于贵州省贵阳市，现有全日制在校生10100人。学院占地823.76亩，校舍33.09万㎡，教学仪器设备6182.85万元，图书75.88万册，拥有功能完善的校园网络，建有经济管理、文化艺术、工程技术等三大类实践教学中心，8个学科专业实验中心，各类实验室90个，中央财政支持的实训基地2个。同时，已建成“黔青梦工厂”创新创业基地，形成了完善的实践教学体系。学院以经济学、管理学为主，工学、人文学科互相支撑、互为依托，重点',570482);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林建筑大学城建学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.jlucc.edu.cn','吉林建筑大学城建学院位于吉林省省会长春市，创建于2000年6月，2004年经国家教育部批准首批确认为独立学院，2008年经吉林省学位委员会批准取得学士学位授予权，2015年吉林省教育厅批准我校开展硕士研究生联合培养工作，是一所面向全国招生的民办全日制省属本科普通高等学校。学校坐落于吉林省长春装备制造产业开发区，校园占地65.51万平方米，总建筑面积33.3万平方米。学校图书馆藏书81余万册、中外文期刊500余种，教学科研仪器设备总值6000余万元。学校设有建筑系、土木工程系、管理工程系、艺术设计',519392);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('太原学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.sxtyu.com','太原学院前身为1984年创建的太原大学，2013年4月经教育部批准，升格为全日制综合类普通本科院校。2014年9月太原大学外语师范学院、太原大学教育学院撤销原建制并入。学院由山西省人民政府领导、太原市人民政府管理、业务工作接受山西省教育厅领导。学院位于太原教育园区中央，与太原市经济技术开发区、高新技术开发区、晋中工业区和武宿综合保税区、山西科技创新城相毗邻，是太原市与晋中市“同城化”建设的交汇点和衔接带，区位优势突出。学院基础设施优良，办学条件优越。学院新校区规划用地1828亩，现总占地面积13',272882);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西应用科技学院',' ','山西',1,0,0,0,0,' ','暂空',' ','http://www.sxxh.org/','山西应用科技学院是在原山西兴华职业学院的基础上经国家教育部批准成立的一所国家承认学历的全日制普通本科院校，是国家首批应用型本科高校，系山西省政府确定的首批应用型试点院校。是三晋大地本科教育又一颗冉冉升起的新星。学校始建于1991年，奉行“以质量求生存，以特色求发展”的办学宗旨，从无到有、从小到大、从弱到强，步履科学创新发展之路，谱写了山西民办教育的光辉篇章。学校位于拥有2500多年历史的文化名城——山西省省会太原。新校区坐落在小店区北格镇——山西科技创新园区南侧，北与教育园区相邻，南接榆古路，西',110736);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁传媒学院',' ','辽宁',1,8,0,0,0,' ','暂空',' ','http://www.fzcollege.org','辽宁传媒学院始建于1991年，其前身为辽宁美术职业学院，2014年经教育部批准，更名为辽宁传媒学院。学院占地面积510亩，建筑面积20万平方米。学院始终秉承“志存高远，德能日进”的校训，学院多年发展取得了令人瞩目的成就，目前是东北地区唯一一所独立设置、专门培养传媒类人才的本科大学。学院跨越式的发展源自于创办者超前的理念。早在2001年，学院院长姜立教授就前瞻性地提出了“为就业提供教育”的办学理念。正是这高屋建瓴的思想指引并鞭策着辽传人团结进取，不懈追求。在学院25年的发展历程中，始终以市场需求为',262589);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州工业学院',' ','甘肃',1,1,0,0,0,' ','暂空',' ','http://www.lzptc.edu.cn/','兰州工业学院是甘肃省省属的一所全日制普通高等工科本科院校。学校的前身是由新西兰国际友人路易.艾黎于1942年建立的培黎工艺学校，1958年改建为举办中等专业教育的甘肃省机械制造学校，1962年与煤炭、电力、轻工、重工和邮电5所中专合并组建了甘肃省工业学校，1974年更名为兰州工业学校。1989年改建为兰州工业高等专科学校。2012年，经教育部和甘肃省人民政府批准，改建为兰州工业学院。学校地处甘肃省省会兰州市，占地面积1692.29亩，校舍建筑面积29.82万平方米，固定资产总值4.55亿元,教学',648280);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州文理学院',' ','甘肃',1,0,0,0,0,' ','暂空',' ','http://www.luas.edu.cn/','兰州文理学院是一所省属全日制普通本科高等学校。其前身甘肃教育学院和甘肃联合大学分别创办于1950年和1985年，2001年7月，两校合并组建新的甘肃联合大学，2013年4月，经教育部同意，在甘肃联合大学基础上建立兰州文理学院。兰州文理学院确立“服务地方经济社会发展，面向第三产业特别是现代服务业、文化产业培养应用型人才”的办学定位，坚持“立足地方需求，打造办学特色，走内涵式、差异化发展道路”的办学思路，基本形成了较为完备的整体面向第三产业特别是现代服务业、文化产业的应用型专业特色和重在培养文化、旅',430423);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('桂林航天工业学院',' ','广西',1,1,0,0,0,' ','暂空',' ','http://www.guat.edu.cn','桂林航天工业高等专科学校位于山水甲天下的国际旅游名城——桂林市的尧山风景区，是一所以工学为主，管理学、人文学和社会学等多学科协调发展，以培养生产、建设、管理、服务第一线需要的应用型人才为特色的高等工程专科学校。学校创建于1979年，原隶属于国家航天工业部。1999年4月，学校划转到广西壮族自治区，实行“中央与地方共建，以地方管理为主”的管理体制。学校占地面积825亩，建筑面积30.19万平方米，教学科研仪器设备总值近5000万元。学校建有功能齐全、设施完善的教学大楼、实验（实训）楼、电教馆、学生',394487);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南宁学院',' ','广西',1,1,0,0,0,' ','暂空',' ','http://www.nnxy.cn/','南宁学院(原邕江大学)是南宁市人民政府与民革广西区委合作共办的国有民办二本高校。学校创办于1985年，2013年被教育部遴选为应用技术大学试点高校并成为全国非营利性民办高等学校联盟盟员，系教育部批准可向港澳台招收本科生的学校。学校实行董事会领导下的校长负责制。学校位于南宁市五象新区，总占地面积1296.28亩，建筑面积近40万平方米。教学科研仪器设备总值1亿元，藏纸质图书108.2万册，中外文期刊1130种，电子图书60.2万册，电子专业期刊一万多种。目前具有专兼职教师702人，其中副高及以上职',277304);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州信息工程学院',' ','福建',1,1,0,0,0,' ','暂空',' ','http://www.qziedu.cn/','泉州信息职业技术学院是经福建省人民政府批准、国家教育部备案、纳入国家计划内招生，具有独立颁发国家承认学历的一所高等职业院校。学院地处海峡西岸经济区、历史文化名城泉州市中心市区，坐落于风景秀丽的国家重点名胜——清源山南麓，与著名的中国闽台缘博物馆、泉州市博物馆、西湖公园相隔咫尺，坐拥湖光山色，极目古港沧澜，丰富的自然景观与人文景观相得益彰，是理想的求学治学佳境。学院现占地475亩，建筑面积15.6万平方米，现有在校生5389人，专任教师269人，其中教授、副教授53人，同时注重引进实践经验丰富的企',81127);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('福州理工学院',' ','福建',1,1,0,0,0,' ','暂空',' ','http://www.fit.edu.cn/','福州理工学院是经教育部批准设立的本科层次普通高等学校，其前身为2004年创建的福州海峡职业技术学院。学院举办者为国脉科技股份有限公司，是中国下一代信息技术综合服务提供商，是福建省政府重点扶持的高新技术龙头企业之一，也是国内证券市场上第一个现代服务业上市公司。学院地处福建省会福州市门户、素有“闽都金凤”雅称的连江县，与风景秀丽的国家4A级景区——贵安温泉休闲旅游度假区相隔咫尺。学院现占地面积640.56亩，建筑面积15.55万平方米，拥有教学楼、实验楼、实训楼、图书馆、学生公寓、食堂等各类建筑设施',180670);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北商贸学院',' ','湖北',0,6,0,0,0,' ','暂空',' ','http://www.hugsmxy.com/','湖北商贸学院(原湖北工业大学商贸学院)，是经国家教育部批准成立的一所全日制普通本科高等学校。校区占地1300多亩，建筑面逾42余万平方米，校园环境优美，学习与生活设施一应俱全，是一所经、管、艺术、文、工等多学科协调发展的综合性大学。在校学生11000余人。优越的地理位置学校坐落在风景秀丽的南湖之滨，位于武汉光谷中心地带，与武汉大学、华中科技大学、中国地质大学(武汉)、华中师范大学、七0九研究所等多所著名高校和科研院所毗邻，学术氛围浓厚;紧邻地铁出口和BRT快速公交车站旁，交通十分便捷，与大学生活',786797);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武昌工学院',' ','湖北',1,0,0,0,0,' ','暂空',' ','http://www.wuit.cn/','武昌工学院是经国家教育部批准设立的全日制普通本科高等学校。学校创建于2002年，办学层次以本科为主，由国家统一下达普通高校招生计划，面向全国招生。学校位于武汉市中心城区，西傍长江，南接黄家湖大学城，北邻武昌首义“起义门”和武汉市地标武昌火车站。武汉南大门快速通道(白沙洲大道高架)与二环线在校前交汇，区位优越，交通便捷。学校环境幽雅，景色宜人，是莘莘学子求学深造的知识殿堂。校园占地面积745亩，校舍建筑面积35万平方米,全日制在校学生近12000人。学校办学条件优越，图书馆纸质藏书100万余册，电',459946);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长沙师范学院',' ','湖南',1,4,0,0,0,' ','暂空',' ','http://www.cssf.cn','长沙师范学院前身为毛泽东主席的老师、无产阶级革命家、杰出的教育家徐特立先生1912年创办的长沙师范学校，2013年升格为普通高等本科院校并更为现名。学院办学历史悠久，人文底蕴深厚，名师荟萃，英才辈出。汇聚了杨昌济、朱剑凡、周谷城等学者名流，涌现了柳直荀、罗学瓒、陈昌等20多位革命先烈，培养了以田汉、许光达、廖沫沙、刘英等为杰出代表的大批国家栋梁之材。学院现有校园面积1100余亩，两个校区（星沙校区和安沙校区）毗邻风景秀丽的松雅湖，隔水渡河而相望；设有10系6个本科专业，31个专科专业，建有16大',350765);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南信息学院',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.hnisc.com/','湖南信息学院是经教育部批准设立，湖南省人民政府管理的应用型本科高校，座落在长沙经济技术开发区毛塘工业园。设有电子信息学院、管理学院、商学院、人文艺术学院、教育学院、国际学院(筹)，开设本、专科专业30个，在校学生10000余人。办学以来，先后获得教育部人才培养工作水平评估优秀等级、省级示范高校、省党建工作先进高校、省文明高校、省平安高校、毕业生就业工作先进单位等300多项荣誉。办学条件优越。1000余亩“五湖五园五桥五广场”的校园环境优美，鸟语花香，被誉为中国最美的大学校园之一。现代化、数字化的',146213);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南应用技术学院',' ','湖南',1,0,0,0,0,' ','暂空',' ','http://www.tongde.com','湖南同德职业学院经过了三次跨越式发展。其前身常德电脑学校是1994年由常德市教育局批准成立的中等职业学校，1998年由湖南省教育厅批准为国家学历文凭试点高校，更名为常德电脑专修大学，2004年5月经湖南省人民政府批准、国家教育部备案为全日制普通高等职业学院，更名为湖南同德职业学院。校园占地面积1057亩，校舍建筑面积29.2万平方米，教学行政用房16.5万平方米，学生宿舍面积9.62万平方米。学院有中央财政拨款支持定点建设的国家级综合性实习实训基地2个，多媒体教室和语音教室座位数1886座，建成',79240);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南交通工程学院',' ','湖南',1,1,0,0,0,' ','暂空',' ','http://www.hnjtgc.com/','一、办学历史湖南交通工程学院学院坐落在全国历史文化名城、中国优秀旅游城市、国家级湘南承接产业转移示范区、国家高新技术产业基地、名人辈出的湖南工业重镇——衡阳市。湖南交通工程学院是经国家教育部批准成立的一所普通本科高校，具有颁发国家承认的统招本、专科学历资格。二、办学条件学院占地面积1059.3亩，校舍总建筑面积25.02万平方米，其中教学科研行政用房17.6万平方米，教学仪器设备总值7100万元，教学用计算机3560台，多媒体教室12145个座位和语音室座位数780个。160个校外实习实训就业基',236533);
