insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东财经大学华商学院',' ','广东',0,6,0,0,0,' ','暂空',' ','http://zs.gdhsc.edu.cn/','广东商学院华商学院(以下简称“学院”)学院代码：12621，是一所全日制省属普通本科院校，由广东商学院和广州市太阳城集团有限公司联合创办，于2006年4月经国家教育部正式批准成立，面向全国招收普通高等教育国家任务生。学院坚持社会主义办学方向，贯彻党的教育方针，以市场需求为导向，以人才培养为中心，以质量提升为重点，以改革创新为动力，积极探索应用型人才培养模式和本科民办独立学院的发展道路。立足广东，面向全国，为社会经济发展服务，为广东中小企业及社会服务机构培养理论基础厚、实践能力强、综合素质高的高级',1039898);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('新疆警察学院',' ','新疆',1,11,0,0,0,' ','暂空',' ','http://www.xjpcedu.cn','新疆警官高等专科学校是为适应新疆公安工作和队伍建设的需求，经国家教育部批准，于2001年5月11日成立的一所普通公安高等院校。学校目前分为校本部、南校区、北校区三大部分。学校自1950年建校以来，始终坚持以邓小平理论和“三个代表”重要思想为指导，全面贯彻执行中国共产党解放思想、实事求是、与时俱进的思想路线和全面发展的教育方针，着力实施素质教育，艰苦奋斗，开拓创新，努力培养政治坚定、业务精通、作风优良、执法公正的各民族公安政法高级应用型专业人才。学校属正厅级建制，内设处级机构19个，编制646人，',201382);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐鲁医药学院',' ','山东',1,3,0,0,0,' ','暂空',' ','http://www.wjmu.net/','齐鲁医药学院前身是由万杰集团1995年投资举办的民办山东万杰医学院，1999年经国家教育部正式批准为具有学历教育资格的民办普通高等院校——民办山东万杰医学高等专科学校。2008年4月经国家教育部批准升格为全日制普通本科高校——山东万杰医学院。2008年12月，经中国证监会许可，山东省商业集团有限公司(鲁商集团)重组万杰集团下属的山东万杰高科技有限公司，山东万杰医学院正式由鲁商集团接管。学院举办者变更为鲁商集团，学校由民企办学转变为国企办学。2012年6月经山东省人民政府学位委员会评审，学校建设成',560946);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛恒星科技学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.hx.cn/','青岛恒星职业技术学院是一所经山东省人民政府批准，国家教育部备案的具有大专统招资格的民办全日制普通高校，并具有高等教育自学考试本科试点（以下简称：试点本科）、成人高等教育及中专的招生资格。学院座落在美丽的沿海开放城市----青岛。地处风景秀丽的崂山脚下，校园占地面积1400亩，学院具有健全的教学、生活设施设备，已竣工投入使用的建筑面积达40.5万平方米，现有教学仪器设备价值达5860万元，除建有教学楼、图书馆、实训楼、讲学厅、实训基地外，还配有看台容纳1.5万人就座的体育场、大学生活动中心、舞蹈房',128191);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东现代学院',' ','山东',1,12,0,0,0,' ','暂空',' ','http://www.uxd.com.cn','山东现代职业学院创建于1993年，2003年在山东民进中西医进修学院和山东现代计算机专修学院的基础上，经山东省人民政府批准，国家教育部备案，成为具有独立颁发学历文凭资格的全日制民办普通高校，是民进山东省委员会唯一的一所会员培训基地。2014年5月，经国家教育部批示，同意在山东现代职业学院基础上筹建应用型本科高校——山东现代学院。学院坐落在山东省会济南,地理位置优越，环境优美，占地面积1266亩。下设电子信息学院、建筑工程学院、汽车技术学院、经济管理学院、护理学院、医学院、人文学院等7个二级学院，',227716);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东华宇工学院',' ','山东',1,1,0,0,0,' ','暂空',' ','http://www.sdhyxy.com/','山东华宇工学院是经教育部批准的全日制普通本科高校。学院位于素有“九达天衢、神京门户”之称的全国优秀旅游城市----山东省德州市，坐落于高校聚集的大学东路。学院前身是创建于2002年的德州华宇学校。2004年7月，经山东省人民政府批准、教育部备案，成立山东华宇职业技术学院。2014年5月16日，经教育部批准，在山东华宇职业技术学院的基础上建立山东华宇工学院，学院升格为全日制普通本科高校。学院现建有机械工程系、电气工程系、建筑工程系、计算机系、经济管理系、汽车工程系、实训中心和基础教学部等8个教学系',122486);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南经济管理学院',' ','云南',1,6,0,0,0,' ','暂空',' ','http://www.ynjgx.com/','云南经济管理职业学院是经云南省人民政府批准，国家教育部备案，具有独立颁发国家承认学历文凭资格的民办全日制普通高等院校。学院遵循高等教育发展规律，以就业为导向，面向社会、面向市场办学，在17年的办学过程中确立和实践了“专家治校、质量立校、特色强校、就业兴校”的战略发展之路。学院规划用地1300亩，现已建成300亩，建有综合教学楼4幢、数字化图书馆（纸质藏书55.3万册、电子图书资料23万册、中外期刊2000多种）、多功能体育馆、400米塑胶跑道标准田径运动场、计算机中心（2000台电脑配置）、英语',153643);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('无锡太湖学院',' ','江苏',1,0,0,0,0,' ','暂空',' ','http://www.wxu.edu.cn/','江南大学太湖学院，是2002年经国家教育部批准的全日制综合性、多学科的本科普通高校，荣获“全国十佳”、“‘五一’劳动奖状”、“江苏省综合治理先进单位”、“江苏省优秀科普教育基地”、“无锡市文明单位”、“无锡市安全文明校园”、“无锡市先进科技工作者之家”等荣誉称号。我院领导在全国独立学院网络视频大会上，作为全国300多所独立学院的代表作大会典型发言，当选为全国独立学院协作会理事长、全国工商联民办高教协会副会长。江南大学是进入国家教育部“211工程”的百年名校，有着悠久的办学历史、深厚的文化底蕴和优',467707);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('西安交通工程学院',' ','陕西',1,1,0,0,0,' ','暂空',' ','http://www.xikemao.com/','西安科技商贸学院是由原陕西省教委批准，于1994年兴办的一所以科技、商贸为特色的综合性全日制民办高校。2003年4月经陕西省人民政府批准，国家教育部备案为正式民办普通高校，具有独立颁发国家学历文凭资格。10年来已形成高等职业教育、高等教育学历文凭考试、高等教育自学考试、大专预科等办学形式，含大学本科、专科、预科三个学历层次。学院下设计算机科学系、电子信息工程系、经济贸易系、法学系、外语系、医学系等6系计27个专业，在历年各类考试中，成绩合格率均在同类规模院校中名列前茅。现有在校生5500余人，教',185309);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('银川能源学院',' ','宁夏',1,1,0,0,0,' ','暂空',' ','http://www.ycu.com.cn','银川科技职业学院（银川大学）是经宁夏回族自治区人民政府批准、国家教育部备案的一所多学科综合性全日制高等院校。学校位于自治区首府银川市南郊。现有校园占地632亩，新校区1500亩正在建设中；校舍建筑面积12.6万平米；图书馆藏书33.6万册，电子图书6万册；拥有现代化的化学、化工、物理、电力、电工、数控、热能动力、计算机、机械、建筑、商务等实验实训室65个，有多媒体教室2600余座，教学用计算机900余台；体育场地30000多平方米；有具备生产功能的石油炼制实训装置2套，生产型金工、电子电工实训厂',127020);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津外国语学院滨海外事学院',' ','天津',0,5,0,0,0,' ','暂空',' ','http://bhws.tjfsu.edu.cn/','天津外国语学院滨海外事学院是经国家教育部批准并于2004年3月重新确认,实施全日制普通本科高等教育的独立学院（教发函2004[22]号），举办校为天津外国语学院。学院实行董事会领导下院长负责制的新型体制，董事长由天津外国语学院校长担任，院党委及主要管理人员和教师由天津外国语学院派出。学院坐落在天津滨海新区，地处大港生态高教园内，占地面积五百亩，东邻大港经济开发区，西倚临潮湖公园，环境优美，交通便利，与周边毗邻的南开大学滨海学院、天津医科大学临床医学院、天津国土资源和房屋职业学院、中国法官学院天津',227504);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('长春光华学院',' ','吉林',1,0,0,0,0,' ','暂空',' ','http://www.ccughc.net/','长春大学光华学院是经国家教育部批准，由长春大学按照新的机制和模式举办的本科层次独立学院。学院根据市场需求，特别考虑了国家振兴东北老工业基地的就业形势需要，学院现有文、理、工、管理、经济五个学科门类共25个本科专业，3个专科专业，现有来自全国各省（市）的在校学生8800余人。光华学院经过多年的运行，形成了自己“质量立校”的办学特色。培养目标明确：学院贯彻“一切为了学生发展”的办学理念、“立志、博学、诚信、创新”的校训，以培养适应社会主义市场经济需要的高素质应用型的高级专门人才为目标。学院定位在以本',639818);
