insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳城市学院',' ','辽宁',1,0,0,0,0,' ','暂空',' ','http://www.sdkj-syu.net/','沈阳大学科技工程学院创建于2001年。是一所经国家教育部批准，并面向全国招生的综合类院校，现有全日制在校生5618人。学院位于风景秀丽的沈阳绿岛森林公园院内，欧式建筑与现代建筑点缀在青山绿水间，湖光山色，风景怡人。校园占地34.73万平方米，教学行政用房8.40万平方米。宽敞明亮的教室、拥有先进教学仪器设备的实验室以及现代化学生公寓和拥有游泳馆、体育健身中心、餐饮中心的绿岛俱乐部是大学生学习、生活的理想之所。目前设有经济、管理、文法、工学、公共课等五个教学部。开设经济学、国际经济与贸易、财政学、',335985);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('大连科技学院',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://www.dist.edu.cn/','大连科技学院坐落在浪漫之都的滨城大连，成立于2002年7月，时为大连交通大学信息工程学院。2011年4月，经教育部批准转设为独立设置的省属普通本科高等学校。校址位于辽东半岛最南端的大连旅顺经济开发区大学城内。校区位于国家自然保护区老铁山北麓延伸带之上，环视渤黄两海，驻足世界和平公园之东，毗邻沈海高速公路与烟大火车轮渡交汇之地。校园占地面积约36.6万平方米，建筑面积21.7万平方米，现有教学行政用房面积约14.1万平方米，教学仪器设备价值4633.7万元，馆藏图书64万册。建有各类实验室50个，',607748);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳城市建设学院',' ','辽宁',1,1,0,0,0,' ','暂空',' ','http://zszx.syucu.cn/','沈阳城市建设学院前身为沈阳建筑大学城市建设学院，成立于2000年7月，经过十三年的办学积累，2013年经教育部批准升格为独立设置普通高校。学校是一所以培养建筑设计、城乡规划、土木工程、道路交通、工程管理、机械制造、电子信息、艺术设计等方面人才的全日制本科学校，现有在校本专科生8555人。学校位于美丽的历史文化名城沈阳市，坐落在大浑南核心区由八所大学组成的大学城中，与沈阳化工大学科亚学院、辽宁师范大学海华学院、辽宁中医药大学杏林学院相拥为伴，与东北大学、沈阳体育学院、鲁迅美术学院、沈阳音乐学院新校',740307);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁何氏医学院',' ','辽宁',1,3,0,0,0,' ','暂空',' ','http://www.he-edu.com/','辽宁何氏医学院是经教育部批准设置的普通本科高校，位于沈阳棋盘山国际风景旅游开发区。学校现涵盖医学、理学、工学、管理学、文学、艺术学等六大学科门类，开设临床医学、护理学、眼视光学、医学影像技术、药学、制药工程、生物医学工程、应用心理学、公共事业管理、市场营销、广告学、动画、视觉传达设计、环境设计、产品设计15个本科专业以及眼视光学技术、护理、药物制剂技术等7个高职专业，现有全日制本、专科生7000余人，面向全国21个省市招生。拥有留学生、继续教育、二学历等办学资格，是东北唯一一家眼视光本科自考专业',268738);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州财经大学陇桥学院',' ','甘肃',0,6,0,0,0,' ','暂空',' ','http://www.lzlqc.com/','兰州商学院陇桥学院于2000年3月经甘肃省政府批准成立，是国家教育部第一批确认的全日制普通本科层次独立学院。招生纳入国家普通高校招生计划，学生毕业及就业与国办高校学生享有同等资格和待遇。现有全日制在校本科生12538人，教职工895人。设有经济贸易、财政金融、法学、外国语言文学、艺术设计、土木工程、信息工程、工商管理、会计学9个系4个教学部。有省级实验教学示范中心1个、甘肃省高校重点(培育)实验室1个;设有6个学科门类，涉及经济学、管理学、法学、文学、工学、艺术学等，现有本科专业42个(含专业方',300115);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西科技大学鹿山学院',' ','广西',0,1,0,0,0,' ','暂空',' ','http://www.lzls.gxut.edu.cn/','广西科技大学鹿山学院创建于2002年，是一所经国家教育部批准实施普通本科学历教育的高等学校。学校位于广西工业重镇、历史文化名城柳州市，校园占地面积近1000亩，总投资近10亿元人民币，总建筑面积31万平方米。学校拥有配套设施先进的教学与实验实训楼群，有现代制造、车辆结构、过程控制、定格动画等100多个实验室，图书馆纸质馆藏书约85万册。学校师资力量雄厚，现有专任教师600余人，其中35%具有高级职称，70%以上具有博士、硕士学位。学校现设有机械工程系、汽车工程系、土木工程系、电气与计算机工程系、',310591);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西中医药大学赛恩斯新医药学院',' ','广西',0,3,0,0,0,' ','暂空',' ','http://www.gxzyxysy.com/','广西中医药大学赛恩斯新医药学院是由广西中医药大学与广西中医药大学制药厂、广西壮族自治区中医医院、广西壮族自治区中西医结合医院合作举办，经国家事业单位登记管理局批准成立的公益性事业单位，是华南地区唯一的一所医药类普通全日制本科层次的独立学院。学院在全国医药类独立学院中综合办学实力排名前列。目前，学院全日制在校生10000余人。学院设置有医学系、医学技术系、护理系、药学系、公共管理系等5个系部，拥有中医学、针灸推拿学、护理学、中药学、药学、药物制剂、康复治疗学、医学检验技术、医学影像技术、口腔医学技',360558);
