insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('温州医科大学仁济学院',' ','浙江',0,3,0,0,0,' ','暂空',' ','http://www.wzmc.net','温州医学院仁济学院成立于1999年，是国家教育部和浙江省人民政府批准设立的独立学院。学院是按新型办学模式和运行机制建立的全日制本科普通高校。学院是一所以医为主，理、工、文、管、法等学科共同协调发展的高等院校，现有24个本科专业，全日制在校生7000余名。现已成为浙江省医学人才培养的重要基地之一。学院实行独立招生，独立颁发文凭。2009年面向浙江省计划招收本科生1750名。学院实行学分制教学管理制度，学生可以提前毕业或延长修业期限。学习期满成绩合格者，颁发由教育部统一电子注册的温州医学院仁济学院毕',344825);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('浙江财经大学东方学院',' ','浙江',0,6,0,0,0,' ','暂空',' ','http://www.zufedfc.edu.cn','浙江财经学院东方学院是1999年经浙江省人民政府批准设立，2004年经国家教育部确认的独立学院。学院是一所按新型办学模式和运行机制建立的全日制本科普通高校。现有在校本科生6700余人。学院设有文华校区和长安校区。文华校区地处风景秀丽的杭州西湖景区北侧、文化氛围浓郁的城西文教区内，占地200余亩，建筑面积74200平方米。建设中的长安校区位于杭州湾北部，地处中国皮衣之都、文化之邦的海宁市联杭经济区，占地900余亩，建筑面积31万平方米。长安校区交通便利，紧临沪杭高速公路和杭浦高速公路，离浙江财经学',283048);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽建筑大学城市建设学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahjzu.edu.cn/','安徽建筑工业学院城市建设学院是教育部2003年正式批准的(批准文号:教发函[2003]541号),由安徽建筑工业学院和徽商集团按新机制、新模式联合举办的本科层次的全日制独立学院。学院位于全国首个科技创新型试点市--合肥市市区，交通便利，校园占地面积257亩。现有土木工程、建筑学、城市规划、电气工程、艺术设计、市场营销、财务管理等10个专业。2006年，学院面向全国17个省市招生1300名。目前，学院正抓住高等教育的发展机遇，加快发展，改善办学条件，深化教育改革，坚持“规模、结构、质量、效益、特色',233559);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西中医药大学科技学院',' ','江西',0,3,0,0,0,' ','暂空',' ','http://www.jxtcmstc.com/','“落霞与孤鹜齐飞，秋水共长天一色”，在英雄城南昌市区，赣江之滨，滕王阁旁，矗立着一所以培养中医药高级人才为己任的学府—江西中医学院科技学院。学院创建于2001年，由江西中医学院申办，经国家教育部批准成立的公办全日制本科独立学院。校园占地面积150余亩，是江西省13所独立学院中唯一设置医药、护理本科专业的院校，也是全国300多所独立学院中为数不多的医药类院校，现有在校统招本科生4700余人。2005年初顺利通过教育部对我院教学工作和办学水平的专项检查，并获高度评价。校园内教学、生活设施齐备，办学条',351499);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西科技师范大学理工学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.jxstnupi.cn','江西科技师范学院理工学院（国标代码：13440）是由江西科技师范学院申办并于2001年报经省教育厅和省计划委员会批准（赣教计字[2001]197号）、国家教育部首批确认（教发函[2003]543号），融理、工、文、管和艺术、体育等学科于一体并具有普通高等学历教育招生资格的全日制本科独立学院。学院地处南昌市前湖高校园区，校园环境优美，教学设施齐全，依托校本部雄厚的师资力量，拥有一支既有理论水平，又有实践经验的高素质的专兼职教师队伍，立足江西，面向全国，坚持以教学为中心，以学生为主体，以培养人才为根',202334);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北工程学院新技术学院',' ','湖北',0,0,0,0,0,' ','暂空',' ','http://dept.hbeutc.cn/zhaosheng/\n','学院简介湖北工程学院新技术学院是2003年3月由湖北省教育厅批准试办、2004年2月经教育部确认，由湖北工程学院举办的一所本科层次的独立学院。学校地处武汉城市圈核心圈层、汉孝子董永故里——孝感市城区，与武汉市相距45公里，距武汉天河国际机场30公里。境内有京广、汉渝铁路，京广高铁，京珠(京港澳)、汉十(福银)、武荆(沪蓉)高速公路，107、316国道贯穿孝感市，武汉城市圈半小时通勤圈——孝感至武汉的城际铁路即将建成通车，交通十分便利。学校与湖北工程学院一路之隔，校园景色宜人，湖光潋滟，绿树成荫，',161019);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川传媒学院',' ','四川',1,8,0,0,0,' ','暂空',' ','http://www.scmc.edu.cn/','四川传媒学院（原成都理工大学广播影视学院创建于1997年）。面向文化产业发展，十余年砥砺奋进，学院取得了令人瞩目的跨越式发展，已为传媒及相关产业输送了近2万名高素质的专业人才，目前学院占地2000余亩，在校生20000余人，生源遍及全国30个省、市、自治区，成为全国重要的现代传媒人才培养基地之一。机制体制创新和特色发展塑造了学院的“一流”品质，被教育部广播影视类专业教学指导委员会专家概括为“一流的理念、一流的设备、一流的师资、一流的管理、一流的人才培养模式、一流的学生、一流的学校”，先后荣获了“',760193);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都文理学院',' ','四川',1,0,0,0,0,' ','暂空',' ','http://www.cdcas.edu.cn/','四川师范大学文理学院始建于2004年，由1999年建立的四川师范大学文化传播学院和四川师范大学国际商学院合并而成，2004年经教育部批准设置为独立学院。学院位于成都市外东洪河中路351号，2011年，在成都市金堂县学府大道9号建成金堂校区，现有洪河和金堂两个校区。环境优美，设施设备齐全，“书香、墨香、花香”，是一所现代化的园林式大学。学院遵循“博文、明理、弘毅、笃行”的校训;以“创办学生满意的学校，培养社会满意的学生”为办学宗旨;以“文以养德、理以求真、兼容并包、与时俱进”为办学理念;以“是否有',844117);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川外国语大学成都学院',' ','四川',0,5,0,0,0,' ','暂空',' ','http://www.cisisu.edu.cn/','四川外国语大学成都学院是由国家教育部批准建立的独立学院，是四川外国语大学按照新机制和新模式举办的本科层次的普通高等学校。四川外国语大学成都学院在秉承四川外国语大学光荣传统、吸纳四川外国语大学文化底蕴、应用四川外国语大学教育资源的同时，坚持教育创新和管理创新，确立了“以外语学科为主，相关学科互相交叉、互为渗透的培养高素质复合型涉外人才的教学型学院”的办学定位和“传播世界先进文化，促进国际交流合作，培养时代创新人才，服务经济社会发展”的办学宗旨。四川外国语大学成都学院坐落于世界风景文化名胜区成都市都',617952);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川文化艺术学院',' ','四川',1,8,0,0,0,' ','暂空',' ','http://www.cymy.edu.cn/','四川音乐学院绵阳艺术学院地处四川省第二大城市——中国科技城绵阳，学院占地面积637亩，毗邻南湖公园和南郊机场，依山望水，环境优雅，具有生态园林式大学的特征。学院成立于2001年。2006年4月，教育部以教发函[2006]91号文批准我院为大学本科层次的独立学院，面向全国招生。学生毕业颁发四川音乐学绵阳艺术学院毕业证书，符合学士授予条件的毕业生，由四川音乐学院绵阳艺术学院授予学士学位。学院拥有完善的教学设备、设施，以及一批学识渊博、经验丰富的专家、教授及中青年教师组成的优秀教师队伍。目前已设置音乐',340351);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('青岛工学院',' ','山东',1,12,0,0,0,' ','暂空',' ','http://www.oucqdc.edu.cn','中国海洋大学青岛学院已改名为青岛工学院，是经国家教育部批准设立的普通高校。学院由中国海洋大学申办、新疆生产建设兵团下属的青岛德泰投资咨询有限责任公司投资合作，是按新机制、新模式建立的本科层次的独立学院。学院坐落在景色秀丽、气候宜人、素有“金胶州”之称的滨海城市青岛胶州市。学院位于胶州市福州南路236号，北靠胶州市政府和著名的三里河文化公园，近邻胶州湾，在青岛一小时经济圈内。学院距青岛民航国际机场约30公里，济青、青莱、同三、环胶州湾四条高速公路皆从校园附近通过，交通便利。学院现有占地面积3613',639126);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('齐鲁理工学院',' ','山东',1,0,0,0,0,' ','暂空',' ','http://www.qlit.edu.cn/','齐鲁理工学院（原曲阜师范大学杏坛学院）是一所经国家教育部批准成立的普通本科高校，学院具有国家统招普通本科、专科，对口普通本科、专科等办学层次。齐鲁理工学院设有济南、曲阜两个校区。济南校区位于济南东部大学城，曲阜校区位于孔子故里曲阜市。校园占地1200余亩，校舍建筑风格独特，学习环境优雅，具有浓厚的学术和科技文化氛围。学院以本科教育为主，本科生占在校生总数98%。学院下设9院2部，即：机电工程学院、土木工程学院、护理学院、计算科学与信息工程学院、化学与生物工程学院、经济与管理学院、文学院、外国语学',835295);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨远东理工学院',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://www.fe-edu.com.cn/zs/index.html','哈尔滨远东理工学院前身是哈尔滨理工大学远东学院，2012年3月经国家教育部批准转设为独立设置的民办普通本科高等学校。办学22年来，学院始终坚持社会主义办学方向和教育公益性原则，以培养高素质应用型创新创业人才为育人目标，紧密围绕经济社会发展对人才的需求，坚持以就业为导向，国际化开放办学，以建设现代化大学制度为目标，开拓进取、追求卓越，以稳步提升的质量和鲜明的办学特色，实现了跨越式发展，先后获得教育部教师基金会、省市政府和行政部门的多项表彰和奖励。学院位于风光秀丽的哈尔滨市松花江畔大学城，校园总占地',215921);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨华德学院',' ','黑龙江',1,1,0,0,0,' ','暂空',' ','http://www.hrbhuade.net/html/main/index.htm','哈尔滨华德学院是教育部批准的全日制普通本科高校，是《黑龙江省高等教育强省建设规划》“1115”工程重点建设高校，黑龙江省首批“卓越工程师教育培养计划”试点高校，首批全国应用技术大学(学院)联盟35所理事单位之一。学校创办于1992年，坐落于拥有“音乐之都”美誉的冰雪之城哈尔滨市，占地总面积77万平方米，学校设有11个二级学院，涵盖工学、管理学、经济学、文学、艺术学5个学科门类，34个本科专业和11个专科专业，在校生近万人。传承工大传统培育优良校风学校植根哈工大沃土，秉承哈工大“规格严格、功夫到家',216185);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江工商学院',' ','黑龙江',0,1,0,0,0,' ','暂空',' ','http://www.hgs-edu.cn','黑龙江工商学院(原东北农业大学成栋学院)是2002年5月经黑龙江省人民政府批准成立，2004年2月在教育部正式备案的民办独立学院。2011年原黑龙江科技学院利民校区全部教育教学资源整体并入成栋学院，使学院的综合办学实力和核心竞争力得到大幅度提升。2015年经国家教育部门批准(教发函[2015]81号)，学院转设更名为黑龙江工商学院，标识码为4123013300。学院的上级主管部门为黑龙江省教育厅，办学地址为哈尔滨利民开发区学院路群英街(原时代大街1号)，办学层次为全日制本科学历教育，实行国家计划',330583);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨剑桥学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.jqu.net.cn/','哈尔滨剑桥学院是教育部批准的国家计划内统一招生的民办普通本科高等院校(教育部代码13303),由东方剑桥教育集团投资创办于1996年。学院经过20年的发展，已成为全日制本科万人大学。校园占地100公顷，建筑面积35万平方米，图书馆藏书109.3万册，教学科研仪器设备值近7349余万元。学院有2个校区，主校区坐落在北国”冰城”哈尔滨市，位于中心城区——香坊区;第二校区东方剑桥东北国际学校坐落在辽宁省沈本新城——高新区。学院设有机电工程学院等8个二级学院、3个教学部及学前教育研究所;设有24个本科专',234416);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨广厦学院',' ','黑龙江',1,0,0,0,0,' ','暂空',' ','http://www.gsxy.cn/','哈尔滨广厦学院是经国家教育部批准的全日制普通本科高等学校。学校占地面积45.72万㎡，位于美丽的北国冰城哈尔滨市利民开发区黄金地段——学院路1号。学校设置了财经学院、管理学院、信息学院、艺术与传媒学院、通识教育学院五个二级学院。学科涵盖经济学、管理学、工学、艺术学、文学、理学等多个学科门类。目前在校生近8000人。学校设施齐全，有设备先进的大型实验中心、实验实训室、近百个校外实训基地;有现代化的教学实训机房、多媒体教室;有纸质图书80余万册、电子期刊文献百余万篇;有亚洲田联认证的标准化田径运动场',174894);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江财经学院',' ','黑龙江',1,6,0,0,0,' ','暂空',' ','http://www.hrbtc.com        ','黑龙江财经学院现有在校生11700余人，学科专业特色鲜明、师资队伍结构优良、教学仪器设备先进、各项管理严格规范、党建工作不断创新、办学基础条件完善、校园安全稳定和谐。在办学定位、人才培养模式、专业设置、人才培养方案和管理模式上形成了自己的鲜明特色，凸显出学院社会诚信度和办学质量双高的优势。几年来先后荣获了“黑龙江省民办高校示范单位”、“黑龙江省高校文明单位标兵”、“黑龙江省平安和谐校园”、“黑龙江省文明单位”、“黑龙江省高校先进基层党组织”、“中国民办教育优秀院校”等光荣称号。一、学院的基本情况',553573);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏师范大学科文学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://kwxy.jsnu.edu.cn','徐州师范大学科文学院于2000年4月成立，2005年5月经国家教育部正式批准为独立学院。现有40个本科专业（方向），在校生7138名。校园面积400余亩，建筑面积10余万平方米。学院毗邻徐州师范大学主校区，既可实现资源共享，又使学生的学习、生活和校园文化活动与校“母体”融为一体，为学生的健康成长提供了深厚的人文底蕴和良好的育人氛围。学院除依托徐州师范大学雄厚的师资力量、一流的教学设备、现代化的图书馆等优越条件办学外，还加大资金投入，努力打造“数字化”校园，拥有51口多媒体教室、4个多功能报告厅、',318968);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏科技大学苏州理工学院',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://szlg.just.edu.cn','江苏科技大学南徐学院成立于2002年，是江苏科技大学按照新机制、新模式设置的本科层次的民办二级学院。2005年5月按照教育部2003[8]号文件精神，经国家教育部批准变更为独立学院。学院独立使用江苏科技大学南校区，占地近300亩。校区位于镇江市南徐新城中心区，东枕人文荟萃、钟灵毓秀的南山风景区，西傍亚洲第一的润扬长江大桥。教学、生活设施齐全、环境幽美、交通便捷、园林式、生态型的校园是求知深造的优选之地。学院坚持以特色求生存，以改革保质量，根据“宽口径、厚基础、重应用、求创新、强能力”的办学理念，',354002);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州财经大学商务学院',' ','贵州',0,6,0,0,0,' ','暂空',' ','http://www.gzsu.edu.cn','贵州财经大学商务学院于2001年经贵州省教育厅同意成立，贵州省人民政府批准设置，2004年经教育部确认，具有独立法人资格并获得办学许可的实施本科学历教育的独立学院。2014年4月16日，贵州财经大学根据教育部和贵州省教育厅的相关规定，规范举办独立学院，与四川希望教育产业集团签订合作办学协议，共同举办贵州财经大学商务学院，并在黔南州惠水县建立新校区，2014年9月正式投入使用。学院设有金融学、会计学等17个本科专业，涵盖经济学、管理学、理学、工学、文学、法学等6个学科门类。拥有惠水和鹿冲关两个校区',369161);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州民族大学人文科技学院',' ','贵州',0,9,0,0,0,' ','暂空',' ','http://www.gzmyrw.cn/','贵州民族学院人文科技学院是新机制独立学院。学院于2001年5月由贵州省人民政府批准成立，并开始招生。学院严格按照教育部2003年8号文件和《独立学院设置与管理办法》（26号令）要求办学。学院通过教育部专项检查，于2004年1月教育部“教发函（2004）9号”予以确认。人文科技学院校区紧靠贵州民族学院本部北面，坐落在秀美的花溪河畔；秉承校本部长期形成的校园文化，充分享受校本部的优势资源。学院所招学生属于国家统一招生计划，所开设的专业都是贵州民族学院的优势专业与热门专业，现有法学，计算机科学与技术，',168716);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州医科大学神奇民族医药学院',' ','贵州',0,3,0,0,0,' ','暂空',' ','http://sqxy.gmc.edu.cn','贵阳医学院神奇民族医药学院是经教育部批准成立，由贵阳医学院和贵州神奇集团按新机制、新模式举办、实施的普通高等本科全日制教育的独立学院，国标代码为13676。学院以贵阳医学院的优势学科为基础，依托于校本部雄厚的师资力量、良好的办学条件，由贵阳医学院统一教学、统一管理，旨在培养基础扎实、素质过硬的新时期高级医药复合型人才。2005年面向全国部份省、市、区招生，学生毕业证书和学位证书的发放按教育部有关独立学院的规定执行。贵州神奇集团创立于一九八六年，现有职工6000多人，资产总值50多亿元，年平均上缴',250182);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京自修大学',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.bjzx.edu.cn/','北京自修大学成立于1977年，是新中国成立以后创办的第一所综合性、全日制民办大学。1984年，邓小平同志将我校命名为北京自修大学并亲笔题写校名。创办30多年来，共为国家培养了三十多万各类人才。姜春云（原全国人大常委会副委员长、国务院副总理）、现任全国残联主席张海迪等同志都是我校毕业生。姜春云同志为母校题词：铸魂育才北京自修大学由中国文化书院主办，汇集了中国在人文学科与自然科学等领域的众多泰斗级大师和著名教授学者，具有浑厚的文化底蕴和丰富的思想宝库，这是我校得天独厚的宝贵财富。北京大学汤一介教授担',24423);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京应用技术大学',' ','北京',0,0,0,0,0,' ','暂空',' ','http://buat.net/','学校简介：北京应用技术大学创办于1986年，是经北京市教育委员会批准的一所全日制综合性民办非学历高等教育机构（办学许可证号：教民111010020000030号），是集人才培养、人才使用、人力资源输出于一体的教育平台。学校倡导\"以终为始\"的职业教育理念，开创\"实训教育\"的人才培养模式。应大教育机构设有北京应大信息产业研究院，致力于引进企业标准，校企合作，打造行业标准，导入国际标准。学校定位应用技术，培养实用人才，成功依托中关村，牵手大唐电信、平安集团、联想集团、合众思壮、Transcom等产业升',20899);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('澳门大学',' ','澳门',1,0,0,0,0,' ','暂空',' ','http://www.umac.mo/','為了培養中國現代化建設人才及促進內地和澳門的文化學術交流，經國家教育部批准，本校繼續在內地各省市及自治區招收學士學位課程學生。澳大在內地招生已二十六年，現有超過一千位來自內地的學生就讀本校博士、碩士、及學士學位課程。澳門大學于1981年成立，前身為私立東亞大學，經過三十年的發展，為澳門最具規模的大學。設有工商管理學院、教育學院、社會科學及人文學院、法學院、科技學院、中華醫藥研究院及榮譽學院，開辦學士、碩士、博士等約100個學位課程。授課語言以英語為主。現有學生7,400多名，本科生5,000多名',483404);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('澳门旅遊学院',' ','澳门',1,0,0,0,0,' ','暂空',' ','http://www.ift.edu.mo','旅遊学院是一所隶属于澳门特别行政区政府社会文化司的公立高等教育机构，是首间获联合国世界旅游组织颁发「旅游教育质素认证」的教育机构。学院提供一系列旅游范畴的学位课程和专业培训，包括酒店、旅游、文化遗产、会展、零售及市场推广、休闲娱乐、康体活动、文化创意及厨艺等。此外，学院亦与国际知名院校合作开办高级管理课程及硕士课程。在调研方面，学院受特区政府及其他机构委托作旅游规划及发展的政策研究。学院使命是成为提供国际化旅游及服务业课程的首选高等教育机构，不仅为澳门，更为亚太区域，培养具有专业知识和技能的学生',58880);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港科技大学',' ','香港',1,1,0,0,0,' ','暂空',' ','http://www.ust.hk','香港科技大学(下称\"科大\")是一所高度国际化的研究型大学，由四所学院组成---理、工、商、及人文社会科学。其中理、工、及商学院提供本科生及研究生课程；人文社会科学学院则只开办研究生课程，但同时为本科生提供通识教育。校舍位于清水湾，依山傍海。全校学生约8,500人，研究生约占学生总数的三分之一。科大拥有一支享誉国际的优秀教研队伍。教授、副教授、及助理教授人数共约450名，来自全球35个国家，各级教授全部拥有博士学位，其中75%的教授是从北美62所一流研究型学府取得博士学位。作为一所高度国际化的研究',160660);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南京人口管理干部学院',' ','江苏',1,0,0,0,0,' ','暂空',' ','oldname": "南京人口管理干部学院','南京人口管理干部学院创建于1980年，原属国家计划生育委员会，2001年划转江苏省管理。学院是一所以“人口科学”为特色，以管理学和经济学为主导学科，兼有法、文、理、工等多门学科，立足江苏，面向全国招生的高等学校。建院以来，一直受到各级政府的重视和支持，是联合国人口基金重点资助单位。在长期的办学实践中形成了“团结、勤奋、求实、奉献”的优良校风，各项事业都有长足发展。学院先后获得“全国精神文明建设工作先进单位”、“江苏省文明单位”、“江苏省高校毕业生就业工作先进集体”等荣誉称号。学院设有人口经济系、',236705);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军空军勤务学院',' ','江苏',1,10,0,0,0,' ','暂空',' ','oldname": "解放军空军勤务学院','中国人民解放军空军勤务学院原名中国人民解放军徐州空军学院，座落在苏鲁豫皖四省交界的江苏省徐州市，这是一个有着6000年灿烂文明的历史文化名城，它东襟淮海，西接中原，南屏江淮，北依齐鲁。铁路、公路、水路、航空纵横交错，堪称枢纽。两汉韵风，决战淮海，蜚声中外。半个世纪以来，徐州空军学院肩负着新时期军队建设的使命与责任，如同一条生命不息的大动脉，先后把7万余名毕业生源源不断地输送到空军现代化建设中来，成为培养空军作战后勤和装备保障人才的摇篮。1954年10月，徐州空军学院在山西太原组建成立，1958年',141690);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东方文化艺术学院',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.eastart-bj.com/','●1983年建校，全日制民办非学历“高等教育机构”。●北京市教委评估批准的“合格学校”（原称“优秀学校”），原“学历文凭考试校”●“国际商业美术设计师”考试校，“优秀民办高校诚信公约”签约校●办学特色：为了弘扬民族民间文化，我院特创立\"民族民间美术、插图连环画、玩具、家具设计\"等专业●专家办学，中央美院、清华大学美术学院名师名家云集，正副教授占92％(见学院名单)●四年制，学习科目40余门，共4000多学时，大大超过国家有关规定●近年有554名学生考上公立学校放弃而来我院就读，并有各地大学在校生',15734);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港大学',' ','香港',1,0,0,0,0,' ','暂空',' ','http://www.hku.hk/','历史悠久——香港大学(港大)于1911年建校，是香港历史最悠久的大学。教研成绩显赫，校内有十名老师先后获选为中国科学院院士。港大长久以来肩负着香港、中国、亚洲及世界各地学术交流枢纽的重任，努力不懈为世界的学术交流和人才培养作出贡献。国际大学——香港大学为国际知名的高等学府，多次获本港及国际媒体高度评价为亚洲及太平洋地区(包括澳大利亚)最佳大学前五名；2006年英国《泰晤士报》公布全球最佳200所大学中，香港大学高踞第三十三位。港大以英语作为教学语言，师生来自世界数十个国家，非本地生人数为全港各院',608270);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军海军航空兵学院',' ','辽宁',1,10,0,0,0,' ','暂空',' ','oldname": "解放军海军航空兵学院','中国人民解放军海军飞行学院英文名称：THENAVALFLYACADEMY，简称海军飞行学院，后更名为海军航空兵学院，为军级单位。位于辽宁葫芦岛连山区解放路1号，是一所重点培养海军航空兵的综合性技术院校，创建于1950年，后牵址至青岛沧口胜利桥附近，1990年鉴于葫芦岛重要的战略位置又牵回。海军飞行学院注重跟踪现代化科学技术和部队武器装备的发展，引进了先进的飞行模拟系统，以利于飞行员更好，更快的掌握飞行技巧和技能。海军飞行学院是为海军航空兵培养飞行人员和地面领航、航空参谋等专业人员的初级指挥院校。',55322);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北理工大学轻工学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.qgxy.cn/','华北理工大学轻工学院(原河北理工大学轻工学院)是经河北省政府批准国家教育部确认，由华北理工大学举办的独立学院。学院位于“环渤海、环津京”的开放城市——唐山市，校园环境优美，办学设施完善，是培养工、理、经、管、文、法、艺相结合的全日制普通本科院校。学院开设冶金工程、机械设计制造及其自动化、土木工程、物联网工程、电气工程及其自动化等工科类，工商管理、财务管理等金融管理类，视觉传达设计、影视摄影与制作等艺术类，英语、法学等文法类，共计62个本科专业，以及电脑艺术设计、人物形象设计、电子商务、酒店管理等',314007);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东科技学院',' ','广东',1,0,0,0,0,' ','暂空',' ','http://zs.gdst.cc/','广东科技学院创建于2003年，是国家教育部批准设立的一所以工学为主，管理学、经济学、文学、艺术学等多学科协调发展的全日制普通本科院校，培养经济社会发展需要的高素质应用型人才。学院现有全日制在校学生11000余人，专任教师600余人，其中副高以上职称教师和研究生以上学历学位教师的比例分别达到31％和32％。学院设有机电工程系、计算机系、管理系、财经系、应用英语系、艺术系、基础课部、思想政治理论课教学部、继续教育学院等六系两部一院，开设了本科专业12个，专科专业17个，承担国家计划内本、专科招生任务',369020);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华北理工大学冀唐学院',' ','河北',0,3,0,0,0,' ','暂空',' ','oldname": "华北理工大学冀唐学院  曾用名：河北联合大学冀唐学院','华北理工大学冀唐学院坐落于河北省唐山市，北依燕山，南临渤海，物产丰富，交通发达，历史文化悠久，人文底蕴丰厚。在国家京津冀一体化的发展战略下，与北京、天津构成了环渤海地区经济发展的“金三角”。华北理工大学冀唐学院(本科)始建于2001年，经河北省人民政府批准，华北煤炭医学院举办渤海分院。2002年6月28日，经河北省政府批准，渤海分院更名为华北煤炭医学院冀唐分院。2004年1月，经河北省教育厅同意并报请教育部批准，将校名规范为华北煤炭医学院冀唐学院(独立学院)。2010年12月，华北煤炭医学院与河',243052);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东财经大学燕山学院',' ','山东',0,6,0,0,0,' ','暂空',' ','http://ys.sdufe.edu.cn/','山东财经大学燕山学院是于2005年经教育部批准设立的独立学院，是以实施全日制普通本科教育为主的财经类高等学校。学院位于济南市经十东路东首南侧，依山而建，环境优美，交通便利。校园内欧式建筑古朴典雅，教学设施齐全，馆藏图书丰富。学院以山东财经大学的优势学科、专业为依托，设有会计系、工商管理系、金融系、国际贸易系、信息管理系、计算机科学与技术系等6个系，12个本科专业，目前在校生6500余人。学院与山东财经大学明水校区共享优质教学资源，为广大学生提供优良的学习环境。学院主要依托山东财经大学的师资力量建',389298);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武警警官学院',' ','四川',1,10,0,0,0,' ','暂空',' ','oldname": "武警警官学院','中国人民武装警察部队警官学院（原武警成都指挥学院）武警警官学院是武警总部直属重点发展院校,是四川省内唯一的一所军事院校，肩负着为武警部队培养初级指挥人才的重要任务。招生对象主要为中西部10余个省市的应届高中毕业生及四川、重庆、贵州三个总队的优秀士兵和全国武警部队干部子女学员，培训层次为本科。2003年以后，还承担了武警部队院校管理干部、专业技术干部、勤务战术教员集训，川、渝、黔总队基层干部晋职等任职培训任务。2011年9月1日，武警成都指挥学院正式升格为中国人民武装警察部队警官学院，简称武警警官',349697);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西医学高等专科学校',' ','江西',0,3,0,0,0,' ','暂空',' ','http://211.141.109.85/','江西医学高等专科学校前身是创办于1951年的江西省第五医士学校，1953年改名为上饶卫生学校，1958年在卫校的基础上创办上饶医学专科学校，1959年更名为赣东北医学院，1978年经江西省人民政府批准成立江西医学院上饶分院。2013年3月经江西省人民政府批准设立江西医学高等专科学校，学校站在了一个新的历史起点，步入了一个新的发展阶段。学校位于赣、浙、闽、皖四省交界处被誉为南北交通枢纽的生态园林城市--江西省上饶市。学校有新老两个校区，新校区座落在丰溪河畔，老校区位于信江南岸。校园依山傍水，景色宜',63865);
