insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军空军哈尔滨飞行学院',' ','黑龙江',1,10,0,0,0,' ','暂空',' ','oldname": "解放军空军哈尔滨飞行学院','中国人民解放军空军哈尔滨飞行学院2012年4月由中国人民解放军空军第一飞行学院和空军第三飞行学院合并而来，是一所初级指挥院校，位于黑龙江省哈尔滨市。学院是空军最早对外开放的两所飞行院校之一，先后有30多个国家的武官和领导人多次来院参观学习。目前，学院正以军委新时期战略方针统揽全局，进一步深化教学改革，在继承中创新，在改革中发展，以科技兴训、效益至上的原则，积极探索新时期教育训练的新模式，推动学院全面建设再上新台阶。',36088);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军后勤学院',' ','北京',1,10,0,0,0,' ','暂空',' ','oldname": "解放军后勤学院','中国人民解放军后勤学院占地面积30多万平方米，建筑面积近30万平方米。图书馆藏书45万册，与100多个单位建立了资料网，实行计算机管理，是后勤信息资料中心。教研综合楼近2万平方米，设备先进，功能齐全。楼内建有中国军事后勤陈列馆（含后勤历史馆和后勤装备馆）、后勤指挥训练模拟中心、电教中心、教学指挥室、战术后勤实验室和专修室。学院教学、科研、文化和生活设施（备）完善，为教学科研建设创造了良好的条件。学院设政治理论、军事、后勤指挥等近20个教研室及图书馆、电教中心和后勤指挥训练模拟中心；学术研究部设研',38977);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军国防信息学院',' ','湖北',1,10,0,0,0,' ','暂空',' ','oldname": "解放军国防信息学院','中国人民解放军国防信息学院是全军唯一的一所中级通信指挥院校，是培养三军通信指挥和指挥自动化管理人才的最高学府。主要担负全军作战部队师以下领导干部信息作战轮训，全军通信系统师以下现职干部任职培训和轮训，首长干部培训，军事学、工学研究生教育以及全国人防、人武通信干部培训等任务，同时还担负全军军事、通信科的重大学术理论、战法训法、法规政策研究和通信装备研发等任务。全日制大学本科，学制四年，学生无军籍，实行准军事化管理。中国人民解放军通信指挥学院学习期满成绩合格者。颁发国家承认的的本科毕业证书。符合学士',41558);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军海军陆战学院',' ','广东',1,10,0,0,0,' ','暂空',' ','oldname": "解放军海军陆战学院','中国人民解放军海军陆战学院原名中国人民解放军海军兵种指挥学院，是一所专门培养海军水面舰艇指挥军官的高等学府。于1977年12月12日组建。学院具有较强的师资力量，拥有教授、副教授70余人，讲师、工程师、实验师150余人，助教、助理实验师130余人。设2个系、3个专业;有30多个教练室、实验室和设备先进的电化教学中心。图书馆藏书近20万册。设置有12个专业，此外，还担负兵种战术学和水面舰艇指挥硕士研究生的培训任务。2000年与国防科技大学联合开设了\"管理科学与工程专业博士研究生\"培养点。院建有教学',60111);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军空军西安飞行学院',' ','陕西',1,10,0,0,0,' ','暂空',' ','oldname": "解放军空军西安飞行学院','中国人民解放军空军西安飞行学院为2011年由空军第二飞行学院和空军第五飞行学院合并而来，空军第二飞行学院与共和国同年诞生，有着光辉的战斗历程和优良的革命传统。其前身是“东北老航校”，1949年12月1日组建于东北长春，当时被命名为“第二轰炸学校”。同年底，改称为“中国人民解放军第二航空学校”。1966年3月1日，根据中央军委命令，校部由吉林长春迁至四川夹江；1976年6月被命名为“空军第二航空学校”；1986年9月命名为空军第二飞行学院。1992年9月改名为空军第十六飞行学院（夹江轰炸机）分院；',47569);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军西安政治学院',' ','陕西',1,10,0,0,0,' ','暂空',' ','oldname": "解放军西安政治学院','中国人民解放军西安政治学院座落在古城西安文化区，毗邻历史名胜小雁塔，与陕西省图书馆、国际展览中心隔街相望。学院具有良好的校风，师资力量雄厚，教学生活设施齐全，交通便利，环境优美。占地22.6万平方米，建筑面积14万平方米，有教学大楼、机关办公大楼、电化教学中心、图书馆、学术报告厅、刑事科学技术实验室、犯罪现场勘察室、文化工作教学专修室、联络工作教学专修室、语言视听室、科研成果档案室、礼堂、俱乐部、运动场、延安教学基地等设施。学院图书馆藏书33.1万册，国内期刊近800种，各中国人民解放军西安政治',88867);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('解放军边防学院',' ','陕西',1,10,0,0,0,' ','暂空',' ','oldname": "解放军边防学院','中国人民解放军边防学院是在中国人民解放军西安陆军学院的基础上改建的，2011年9月8日上午，中国人民解放军边防学院成立大会在西安隆重举行，该院是全军第一所边防学院。中国人民解放军西安陆军学院的前身是1941年7月诞生于山西兴县李家湾的“中国人民抗日军政大学第七分校”。学院在战火中诞生，在战斗中成长。至今已走过70年的光辉历程。',54863);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('香港理工大学',' ','香港',1,1,0,0,0,' ','暂空',' ','http://www.polyu.edu.hk','香港理工大学（简称理大）是一所既充满活力又拥有骄人历史及卓越成绩的大学。其前身为香港理工学院（由香港工业专门学院和香港官立高级工业学院发展而来），于一九九四年正名为香港理工大学。理大位于红磡海底隧道毗邻，占地约九万三千五百平方米。过去十年间，理大经历了大规模又迅速的扩展。理大现为全港学生人数最多的教资会资助大学，提供的课程均能切合工商界及社会人士的需求。理大在满足香港社会在人力资源需求的同时，亦积极为公营及私营机构提供专业顾问、培训及应用研究等服务。透过这些服务，大学与工商界亦建立了紧密的伙伴关',276256);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京服装学院国际学院',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.biftedu.com','北京服装学院是全国唯一以服装命名，艺工为主，艺、工、经、管、理等多学科协调发展，具有鲜明办学特色的全日制普通高等学校。学校拥有艺术设计、服装设计与工程2个国家级和北京市级特色专业建设点。学校在国际教育交流与合作方面拥有丰富的经验及优势，近年来与英国伦敦艺术大学、德蒙特福德大学、谢菲尔德哈勒姆大学、法国MORD‘ART时装学院、美国帕森斯艺术设计学院、澳大利亚悉尼大学等国外多所著名大学相继开展了多种形式的合作，使热爱艺术设计的学生在北服完成阶段性学业后，顺利踏上国际留学的通路，进而获得学士学位、硕',14218);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国政法大学继续教育学院',' ','北京',1,0,0,0,0,' ','暂空',' ','oldname": "中国政法大学继续教育学院','中国政法大学是一所以法学为特色和优势，兼有文学、史学、哲学、经济学、管理学、教育学等多学科的“211工程”重点建设大学，“‘985’优势学科创新平台”项目重点建设高校，直属于国家教育部。学校现有海淀区学院路和昌平区府学路两个校区。学校的前身是1952年由北京大学、清华大学、燕京大学、辅仁大学四校的法学、政治学、社会学等学科组合而成的北京政法学院。1983年，北京政法学院与中央政法干校合并，组建为中国政法大学。1985年，学校开辟昌平校区新校址。学校现有法学院、民商经济法学院、国际法学院、刑事司法',36957);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国政法大学司法考试学院',' ','北京',1,11,0,0,0,' ','暂空',' ','http://www.fdpx.com','中国政法大学司法考试学院成立于2005年3月，是我国高校中首家司法考试学院，也是唯一的一个公办司法考试培训中心。基于这样的地位和权威，司考学院一直坚持走在国家司法考试的最前端，为行业做出表率。学院主要承担国家司法考试制度的研究工作，同时承担着本校学生（包括本科生、双学士和研究生）司法考试考前的辅导工作以及面向社会的司法考试培训工作。学院下设院务办公室、切题研究中心、对外合作办公室、招生办公室、教务办公室、网络办公室等职能部门。拥有专门从事切题工程、题库、教材资料和教学法研究的百人研究团队。司法考',21862);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('华南师范大学凤凰国际学院',' ','广东',1,8,0,0,0,' ','暂空',' ','http://www.hs1933.com','学院简介华南师范大学始建于1933年，是一所学科门类齐全的国家“211工程”重点建设大学和广东省省属重点大学。现有4个国家重点学科，9个国家“211工程”重点建设学科。学校拥有实力雄厚的国家级实验室和研究基地，也是全国高等学校培训中心和研修基地。学校现有广州石牌校区、南海校区和广州大学城校区共3个校区，占地面积共3079亩，校舍面积共126万平方米。校园景色怡人，人文景观遍布，文化气息浓厚，为广大师生提供了良好的学习、工作和生活环境。凤凰卫视控股有限公司成立于1996年，是一家在国际社会享有盛誉',52585);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('澳门科技大学',' ','澳门',1,0,0,0,0,' ','暂空',' ','http://www.must.edu.mo/','澳門科技大學是澳門特區在澳門回歸之際批准創辦的第一所非牟利的、以培養應用型人才為主的綜合性大學。校園總佔地面積約二十一萬平方米，坐落在風景優美的澳門氹仔島，比鄰澳門國際機場和眾多世界一流酒店。辦學理念與培養目標大學依託澳門多元文化兼容並蓄的獨特背景，以“增進文化交流，致力人才培養，促進經濟發展，推動社會進步”為使命，努力把學校建成一所開放的大學，一所兼容並蓄的大學，一所有影響力的大學。大學的培養目標是實踐能力強的中、高級應用型人才，創建了多元文化下的兼容並蓄和堅持專業教育與通識教育並重的教育理念',164262);
