insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东职业技术学院',' ','广东',0,1,0,0,0,' ','暂空',' ','http://www.gdptc.cn/zsb/Default.shtml','幽雅的校园环境良好的社会形象广东职业技术学院是经广东省人民政府批准、国家教育部备案的一所省属全日制普通高等学校，是一所以工为主，涵盖文、理、艺术等专业体系的高等职业院校，是广东省高素质技术技能人才培养的重要基地。学院两个校区分别坐落于佛山市禅城区和高明区，总占地面积1127亩，校园典雅秀丽。学院设有8系3部即纺织系、服装系、经济管理系、轻化工程系、艺术设计系、机电工程系、信息工程系、应用外语系和公共课教学部、思想政治理论课教学部和继续教育部，教职工500多人，面向全国13省市招生，全日制在校生1',635144);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海兴伟学院',' ','上海',0,0,0,0,0,' ','暂空',' ','oldname": "上海兴伟学院  曾用名：上海兴韦信息技术职业学院','上海兴韦学院是一所私立非营利的四年制博雅学院，她在引进有数百年历史的美式博雅教育的基础上，融入数千年的中华文化元素，旨在培养能更好地适应全球一体化进程下瞬息万变的社会需求、具有创新型思维和广博知识的国际化人才。博雅教育是一种经典的“素质教育”，追求人的全面发展，相比传统的“专业教育”，它更能发现和发掘每一个学生的天赋和潜能，更能激发学生的学习兴趣和热情，从而帮助他们找到愿意为之奋斗终生的事业。上海兴韦学院按照博雅教育的理念，不为学生预设任何专业，而注重为学生提供涵盖文理各个学科的大量通识课程。学',71942);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海工商职业技术学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://zhaosheng.sicp.sh.cn/','上海新侨职业技术学院是上海市首批由国家教育部批准的独立设置、面向全国招生的民办全日制高职院校之一，也是上海市首批依法自主招生的高校之一。学院创建于1993年，批准1999年，由上海海外联谊会、上海市海外交流协会、上海市归国华侨联合会和上海中华职教社等统战团体共同举办。学院曾2次被评为上海市级“文明单位”；2006年荣获“上海市职教先进单位”称号。学院现设8系（部），26个专业，具有完善的教学设施和规范的管理机制。拥有一批由资深的教授、副教授组成的专兼职师资队伍，专任教师134人，兼任教师233人',146295);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄工程职业学院',' ','河北',0,1,0,0,0,' ','暂空',' ','http://www.sjzevc.com','石家庄工程职业学院（原石家庄计算机职业学院）是教育部备案、河北省政府批准成立的民办普通高职院校，面向全国实行计划内统招。学院位于石家庄市，与河北经贸大学、河北体育学院等十几所高校毗邻,有着良好的文化氛围。校园内群楼环抱，绿树掩映，芳草如茵，园林亭阁点缀其中，校园外的片片梨园，春暖时节，花香四溢，空气清新宜人，是莘莘学子静心学习的理想场所。学院开设了航空机电设备维修、机电一体化技术、电气自动化技术、汽车电子技术、汽车检测与维修技术、计算机应用技术、计算机网络技术、移动通信技术、物联网应用技术、软件',102489);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄财经职业学院',' ','河北',0,6,0,0,0,' ','暂空',' ','http://www.sjzcj.edu.cn/index.htm','历史沿革石家庄财经职业学院是经河北省人民政府批准、教育部备案、具有独立颁发高等教育学历证书资格的省属全日制普通高等院校。其前身为创办于1999年的石家庄知行专修学院，2002年更名为石家庄外经贸职业学院，正式纳入国家统招计划，2011年更名为石家庄财经职业学院。学院概况学院坐落于河北省省会石家庄西北高教区学府路236号，占地面积500余亩，建筑面积二十余万平米,馆藏图书60余万册，现有在校生近万人，专职教职工500余人。学院教学、文体和生活设施齐全：拥有综合教学楼、实训楼、多媒体教室、语音教室、',225346);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('襄阳职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbxftc.com','襄阳职业技术学院是省属公办全日制多科性高等职业院校（教育部统一代码：12354），迄今已有百年办学历史。学院先后被国家教育部、财政部确定为“中央财政重点支持建设的示范性职业技术院校”、“国家示范（骨干）院校立项建设单位”。占地面积1137.9亩，规划面积1500亩，校舍建筑面积42万平方米；图书馆藏书106万册；专任教师670人，双师素质教师415人，外籍教师4人，教授42人，副教授295人，国家教学名师1人，湖北省楚天技能名师11人，享受国务院和省、市政府津贴专家4人。在校生14000余人。护',134164);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西卫生职业学院',' ','江西',0,3,0,0,0,' ','暂空',' ','http://www.jxhlxy.com.cn/','江西卫生职业学院是一所全日制医药类高等职业院校，隶属于江西省卫生和计划生育委员会。学院前身江西省卫生学校创办于1955年，南昌铁路卫生学校创办于1958年，两校均为国家级重点中专学校。经江西省人民政府批准，教育部备案，2004年6月两校合并升格为江西护理职业技术学院，2015年4月更名为江西卫生职业学院。2012年10月学院整体搬迁至小蓝新校区办学，办学条件优越。新校区占地面积29万平方米，建筑面积26万平方米，固定资产总值5亿余元，教学科研仪器设备总值5000多万元，图书馆藏书68万余册。学院',162482);
