insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西药科职业学院',' ','山西',0,3,0,0,0,' ','暂空',' ','http://www.sxbac.net.cn','山西药科职业学院，1957年建校，前身是中专建制的山西省中药材学校，2001年4月升格为高职高专层次的山西生物应用职业技术学院，2012年2月经山西省人民政府批准、国家教育部同意更名为山西药科职业学院。山西药科职业学院隶属于山西省食品药品监督管理局，主要为山西乃至全国医药和食品行业培养高端技能型人才。学院地处太原国际机场北侧，毗邻武宿立交桥、太原火车南站和山西高校教育园区，占地439亩，建筑面积10.77万平方米，馆藏图书文献23.37万册。多媒体教室43个，语音室6个；校内建有实训基地12个，',242717);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏食品药品职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.jsfsc.edu.cn','“国家示范性高等职业院校建设计划”骨干高职院校江苏食品职业技术学院是经江苏省人民政府批准、国家教育部备案的具有高等学历教育资格的公办全日制普通高校，是国内唯一独立设置的以食品科学为特色的高等职业技术学院。学院位于一代伟人周恩来总理的故乡——江苏省淮安市，这里交通便利，水运发达，新长铁路、宁连高速、京沪高速、京杭大运河等穿境而过。学院占地面积1500亩，总建筑面积25万平方米，总投资5亿多元，是一座集花园式、生态型、智能化、现代化于一体的美丽校园，是青年学生求知读书的理想学府。图书馆馆藏图书42万',132228);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武昌职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.wczy.cn/','武汉语言文化职业学院是由湖北省人民政府批准、教育部备案、湖北第二师范学院举办的专科层次的普通高等职业学校。学院以外语外事和信息技术为办学特色。学院座落在武汉东湖新技术开发区光谷腹地，毗邻湖北第二师范学院主校区，与湖北第二师范学院在师资、设备、管理等办学资源方面完全共享，优势互补。目前校园占地面积302亩，校舍建筑面积8万多平方米，固定资产9000余万元，现有在校生6000余人。配置了现代化的教学设施，建有高档次的计算机房、多媒体教室、数字语音室、专业实训室等，设备齐全，数量充足。学院坚持“养一身',145238);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京理工大学房山分校培训中心',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.bitfsfx.cn/','1985年北京市房山区人民政府，根据北京市人民政府为郊区培养建设人才的决定，与北京理工大学联合创办了北京理工大学房山分校，是房山区属第一所公办高等学校，北京理工大学负责专业设置、招生计划、教育教学管理、师资配备、学籍、学历管理等工作，对办学质量全面负责。房山区政府负责提供学校办学用地，承担全部硬件建设投资，提供人才需求信息并协助做好毕业生就业的指导工作。分校坐落于北京市房山区阎村镇，东接良乡卫星城和京石高速公路，西临燕山石化工业区，南与燕化星城毗邻，北靠中国科学院原子能研究院。房山分校占地60亩',40268);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东省经济管理干部学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdai.gov.cn/','山东省经济管理干部学院建立于1983年10月，是一所经教育部、山东省人民政府批准成立的从事本、专科学历教育的正规化、多学科、综合性公办高等院校。1992年12月，省委、省政府为适应建立公务员制度和培养公务员的需要，在山东省经济管理干部学院的基础上扩建成立山东行政学院，实行“一个机构，挂两块牌子”。目前，学院已成为山东省唯一一所既开展高等学历教育，又培养高、中级国家公务员和全省大中型企业领导干部的综合性管理学院。学院占地总面积1304亩，其中新校区占地1100亩，一期建设工程15.6万平方米；老校',241821);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东轻工职业学院',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.sdlivc.com','山东轻工职业学院是一所省属公办全日制普通高等学校，坐落在著名的“现代工业城市”、“历史文化名城”——山东省淄博市，始建于1960年。学院占地面积500多亩，建筑面积13万余平方米，教学设施齐全，装备比较先进，教学手段科学，建有“高标准，高水平，高境界”的校内外人才培养技能训练平台，包含纺织标准实验室、纺织CAD实训室、服装立体裁剪实训室、纺织品检验实验实训室等100余个校内实验实训室和丝织厂、金工厂等4个校内实训基地，校外实训基地200多个。被评定为山东省纺织职业技能鉴定培训考核基地、山东省服装',203482);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林工程职业学院',' ','吉林',0,2,0,0,0,' ','暂空',' ','http://www.jlevc.cn/','吉林农业工程职业技术学院是经吉林省人民政府批准的公立普通高等院校，学院现有公主岭校区和四平新校区，占地28.2万平方米，建筑面积11.2万平方米，交通十分便利。新校区现正在建设和规划阶段，预计7月份交付使用。学院始建于1958年，有着悠久的办学历史。在多年的办学历程中，学院始终坚持“以服务为宗旨，以就业为导向，以提高学生综合能力为目的”的职业教育方针，坚持以“培养高素质、高技能、创新型一线人才”为办学目标，根据市场对人才的需求，不断深化教育教学改革，己为社会输送了大批优秀人才。在服务地方、贡献社',76215);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西同文职业技术学院',' ','山西',0,5,0,0,0,' ','暂空',' ','http://www.sxtwedu.com/','山西同文外语职业学院是山西省人民政府晋政函[2004]75号文批准正式建立、国家教育部备案的一所高等学历教育机构，高职层次，学制三年，类型为民办高等学校。学院位于山西腹地、晋商之乡、三贤故里---山西省介休市，东临平遥古城，西接汾河之畔，南眺绵山风景区、大运高速，北临108国道，交通便捷，经济文化发达，气候环境宜人。学院占地300余亩，校舍面积达10余万平方米，在校学生3000余人，校园分教学区、生活区、运动区、休闲区。校园内绿树成荫，湖水涟漪，风光幽美。学院坚持以服务为宗旨，以就业为导向，以培',49150);
