insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('宁波卫生职业技术学院',' ','浙江',0,3,0,0,0,' ','暂空',' ','http://www.nbchs.net/','宁波天一职业技术学院是一所培养高素质技能型卫生技术人才的全日制国有公办高职院校。前身是创办于1925年的宁波华美高级护士学校，历经宁波卫生学校、宁波医学专科学校、宁波医学院、浙江医科大学宁波分校、宁波大学卫生职业技术学院等发展阶段，迄今已有80余年办学历史。学院概况学院位于宁波市高教园区（南区），占地面积近400亩,环境优美，教学条件优良，生活设施齐全。现有全日制在校生5780人，成人学历教育学生3300余人。学院设有护理学院、医学技术系、卫生管理系、成人教育学院、基础学科部、人文社科部等教学机',115101);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥职业技术学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://www.chzy.org/','合肥职业技术学院是一所公办学校，隶属于合肥市人民政府，业务上接受省教育厅领导,是我省成立较早的高职院校之一，也是安徽省首批地方技能型高水平大学建设单位。学院现有校区占地面积784亩,总建筑面积24万平方米，总投资8.5亿元;现有教职工507名。专任教师379人，具有高、中级职称教师占师资总数80.7%，“双师型”教师占专任教师总数的73.4%，校内外兼职教师175人。学院办学条件能满足学生学习、生活、活动等需要。已立项建设的合肥校区占地446亩，建设总投资14.8亿元。合肥校区建成后，将实现“一',139021);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('万博科技职业学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://www.wbc.edu.cn','万博科技职业学院是于2000年6月经安徽省人民政府批准、国家教育部核准、安徽省最早一批面向全国招生的高等民办院校，学院坐落于安徽省会合肥市中心，交通便利，环境优美。学院下辖经管、理工、人文、艺术设计四个分院，44个国家计划统招专业，并设有企业品牌与形象设计、VisualBasic程序设计等省级精品课程。2005年12月，成为安徽首家通过“高职高专院校人才培养工作水平评估”，取得“优良”成绩。2012年12月，批准成为安徽省自主招生改革试点高校之一。建院十多年来，本着“万众一心博采众长”的校训，学',38139);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥经济技术职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.hfet.com/','合肥经济技术职业学院是国家教育部授权，安徽省人民政府批准成立,由合肥博大集团投资兴建的全日制普通高等院校。致力培养面向社会、紧贴经济、聚焦一线的高级实用人才。学院位于合肥市区环湖东路，地处合肥市西郊风景区蜀山湖畔。学院占地近300亩，建筑面积10余万平方米，能容纳在校生5000余人。学院现有全日制专科在校生3500余人。办学特色1、实施“订单式”教学，实行“双证书”制度学院坚持党的教育方针，以育人为中心，以就业为导向,以市场需求为导向。全面推进素质教育，努力培养德、智、体、美全面发展的复合型技能',55545);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥财经职业学院',' ','安徽',0,6,0,0,0,' ','暂空',' ','http://www.hffe.cn/','合肥财经职业学院是经安徽省人民政府批准、国家教育部备案、具有独立颁发高等学历文凭资格的全日制高等院校。学院座落在国家级合肥经济技术开发区大学城，占地面积近500亩，建筑面积20多万平方米，现代化、智能化的财经大楼、教学楼、学生公寓、餐饮大厦等建筑掩映在碧草绿荫之间，布局错落有致，装饰气派自然，是最适宜学习、生活的“常绿、生态式园林”校园。学院设六个二级学院：会计学院、商务学院、工程学院、人文经济学院、农商学院、传媒与艺术学院，共开设会计与审计、营销与策划、商务经纪与代理等22个专业，是一所以财经',78597);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州工艺美术职业学院',' ','福建',0,8,0,0,0,' ','暂空',' ','http://www.dhcc.cc','德化陶瓷职业技术学院系福建省人民政府批准设立由泉州市人民政府举办，中华人民共和国教育部备案的公办全日制高等职业院校。学院地处素有“世界瓷塑之都”、“中国瓷都”、“中国陶瓷艺术之乡”、“福建省优秀旅游县”、“福建省最适宜人居住的地方”、“福建省最佳旅游目的地”之称的泉州德化，交通便捷，距泉州市区仅50分钟高速路程，与全省各地市均有高速公路相连接。校园座落德化城区凤凰山上,毗邻德化陶瓷博物馆、唐寨山省级森林公园；校园内绿草如茵、繁花似锦，7万多平方米的建筑楼群宏伟别致，是“福建省花园式学校”、“省级',38147);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州海洋职业学院',' ','福建',0,1,0,0,0,' ','暂空',' ','http://www.qzoiedu.com/','泉州泰山航海职业学院是经福建省人民政府批准、教育部备案，纳入全国高考统招计划，具有独立颁发国家普通高等学校专科文凭资格的全日制民办高职院校。学院坐落于福建省东南沿海著名侨乡、国家级中心渔港----石狮市祥芝镇，地理位置优越，有着悠久的航海历史，人文航海意识浑厚。学院由泰山石油（新加坡）有限公司独资的福建石狮泰山船员管理有限公司举办。该项目为福建省“十一五”重点项目，计划总投资为6亿元人民币。总占地面积为823.15亩，其中主校区占地690.15亩，海边训练基地133亩（距主校区仅1.2公里），总',52424);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('漳州科技职业学院',' ','福建',0,2,0,0,0,' ','暂空',' ','http://www.tftc.edu.cn','漳州天福茶职业技术学院，是由全球最大茶业集团天福集团出资人民币2.5亿元创办，校址在福建省漳州市漳浦县盘陀镇天鹅湖边，占地1200亩，校舍16万平方米。是经国家教育部备案，省教育厅颁发办学许可证，纳入国家高校统一招生，具有独立颁发由教育部制订认可大专学历毕业证书的全日制高等院校，也是全球第一所茶专业高等院校。在校生规模第一期计划为3000人，现任校长为茶学著名专家周巨根教授，名誉校长为中国工程院陈宗懋院士。学院依山傍水，环境优美，交通便捷，教学设施先进，办学特色鲜明，就业前景宽广。天福茶学院专业',36331);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('泉州华光职业学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://www.hgu.cn/','泉州华光摄影艺术职业学院，是经福建省人民政府批准、国家教育部备案的具有高等学历教育资格的普通高等院校，也是我国第一所独立建制的摄影艺术高校。学院位于国际花园城市、全国最佳魅力海滨城市、国务院首批公布的历史文化名城——泉州市,占地面积700多亩，环境优美，教学生活设施齐全，是一座充满艺术氛围的现代化花园式高等学府。学院实行“艺术+技术”零距离订单式的教育模式，面向全国招生，设有摄影、设计、鞋服、经管、建筑工程等5个系21个专业。其中，摄影摄像技术专业、鞋类设计与工艺专业为福建省高等职业教育精品专业',47607);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北工业职业技术学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbgyzy.edu.cn/','“国家示范性高等职业院校建设计划”骨干高职院校十堰职业技术学院（简称十堰职院）是一所以工为主，人文、管理、经济等多专业门类兼备的公办全日制普通高等院校，其前身为1976年在十堰师范基础上创办的十堰高师班和1983年成立的十堰职业大学，1986年合并为十堰大学，1998年经原国家教委批准更名为十堰职业技术学院，是湖北省和国家首批举办的高等职业技术学院之一，2008年被确定为湖北省示范性高等职业院校立项建设单位。她坐落在湖北省西北部武当山麓、汉水之滨的十堰市中心城区。十堰是著名的汽车城。十堰因车而建',83984);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南邮电职业技术学院',' ','湖南',0,1,0,0,0,' ','暂空',' ','http://www.hnydxy.com/','长沙通信职业技术学院始建于1958年，在原湖南省邮电学校的基础上建立的公办、全日制普通高校,隶属中国电信湖南公司，教育业务由湖南省教育厅管理。2003年被评为湖南省“文明高等学校”,2005年获“教育部高职高专院校人才培养工作水平评估优秀等级”，2008年被评为湖南省普通高校招生工作“先进单位”。学院占地12.07万平方米,固定资产总值1.3亿元，其中实验实习设备总值4800多万元。拥有与通信行业同步的专业设备，建有程控交换、光纤通信、数据通信、移动通信（包括3G移动通信设备）、通信电源、计算机',51013);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东文理职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gdwlxy.cn/Html/Article/list_533.html','广东文理职业学院是广东省人民政府批准成立，国家教育部备案，面向全国招收国家任务生的全日制普通高等院校，隶属省教育厅。学院坐落在南海之滨的中国首批对外开放沿海港口城市、中国优秀旅游城市、北部湾新兴科技工业城市——湛江市，这里人文荟萃，景色美丽，交通便利，水陆空交通网络直通全国各地，是莘莘学子理想的求学圆梦之地。千亩生态园林式校园依山傍水，环境优雅，常年绿树成荫，四季繁花飘香，威武将军山、俏丽日月湖把校园点缀得绚丽多姿。学院原有用地803亩，廉江市委市政府大力支持学院办学，再新增办学用地500亩，使',399598);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('东营科技职业学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.dycollege.net/','山东大王职业学院是山东省人民政府批准成立的专科层次高等职业院校，始建于1985年，是一所文理结合的多科性重点职业学院。招生工作列入国家普通高校统一招生计划，是山东省职业教育教学与科研基地。学院座落在东营市大王高新技术开发区，环境优美、交通便利、企业林立。学院教学设施齐全，教学仪器配备先进，有适合教学需要的实习、实训基地和研究机构。学院有着雄厚的师资力量，专任教师充分满足了目前的教育教学工作。学院遵循“依托企业办教育，办好教育为企业”的办学原则，为企业输送了一批又一批优秀毕业生，有力的促进了当地企',63231);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南工程职业学院',' ','云南',0,0,0,0,0,' ','暂空',' ','http://www.sailingedu.com/','昆明扬帆职业技术学院是为适应社会经济持续、快速、健康发展对应用型技术人才和管理人才的需要而创办的一所全日制高等学校，经由云南省政府、省教育厅批准，并在国家教育部备案，具备全国高考统招资格，于2005年正式招收大专学历制学生。学院拥有校舍建筑5万平方米，教学科研仪器设备价值1000万元，图书馆藏书18万册。校内座拥植被茂密的星宿山（仅为学生学习休闲之用）和标准足球运动场，配置了宽带网络的计算机机房以及多媒体阶梯式教室为教学提供了确切的保障，44幢纯花园别墅式学生公寓，依赖先进设施设备进行管理的学生',49440);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南通科技职业学院',' ','江苏',0,2,0,0,0,' ','暂空',' ','http://www.ntac.edu.cn/','地理位置优越，人文底蕴深厚南通科技职业学院始建于1945年，坐落于长三角北翼经济中心的江苏省南通市市中心，位于上海一小时都市圈，是教育部高职高专人才培养水平评估优秀院校、江苏省示范性高等职业院校建设单位。办学实力雄厚，专业门类齐全学校设有生物工程系、园林园艺系、环境与资源系、机电工程系、经济贸易与管理系、信息工程系、人文科学系、基础教学部、继续教育学院、创新创业学院、天樱国际学院等教育教学机构，开设专业46个。在籍生近万人，留学生近50名，教职工400余人。就业竞争力强，升学渠道通畅学校实行“双',48096);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('苏州健雄职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','http://www.wjxvtc.cn/','苏州健雄职业技术学院是2004年7月经江苏省人民政府批准建立的公办全日制普通高等学校，以出生太仓的“中国居里夫人”——吴健雄的名字命名，位于苏州太仓科教新城，毗邻国际大都市上海。目前，学院占地面积700亩，建筑面积近20万平方米，教职员工385人，拥有省“333”、“青蓝工程”和“东吴学者”等一批高层次人才。全日制在校生5000余人，每年开展社会培训6000人次，与德国、美国、加拿大等国家的国际教育合作不断深化，建立了以全日制高职教育为主体，应用型本科、专业硕士和继续教育为补充的区域技术应用型人',30763);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('盐城工业职业技术学院',' ','江苏',0,1,0,0,0,' ','暂空',' ','oldname": "盐城工业职业技术学院  曾用名：盐城纺织职业技术学院','盐城工业职业技术学院是江苏省盐城地区第一所省属公办示范性综合型高职学院，是全国办学水平优秀学校、江苏省首批高等教育人才培养模式创新实验基地。学校先后获得江苏省高校毕业生就业工作先进集体、江苏省高校学生资助工作优秀单位、江苏省文明单位、江苏省职业教育先进单位等省级以上荣誉50多项。学校建筑面积20多万平方米，资产总值近6亿元，仪器设备总值1亿多元，教职工近600人，在校生8000多人，建有国家及省财政资助的实训基地5个、省级技术研发中心2个、省级技术转移中心1个、省级大学生创业培训基地1个、专业实',33684);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁建筑职业学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.lnjzxy.com','辽宁建筑职业学院坐落于古城辽阳，隶属于辽宁省教育厅，由原辽宁信息职业技术学院与辽宁建筑职业技术学院合并组建。学校有着长达60余年职业教育的历史，现为辽宁省首批职业教育发展改革示范学校建设单位。学校坚持以土建类专业为中心，充分发挥机械制造、电子信息、财经管理类专业在省内外形成的优势，各类专业相互支撑，协调发展，现设50个专业。学校占地面积358500平方米，建筑面积210000平方米，教职工592人。学校设有土木工程系、城建交通系、设备工程系、信息工程系、自动控制系、机械工程系、建筑艺术系、建筑经',153803);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽旅游职业学院',' ','安徽',0,5,0,0,0,' ','暂空',' ','http://www.ahlyedu.cn/','安徽旅游职业学院是经安徽省政府《皖政秘（2007）48号》文件批准设立、获国家教育部《教发（2008）12号》文件备案、安徽唯一一所旅游类/大专层次/全日制职业学院。学生学业三年期满，各科成绩合格，可获得国家认可的大学专科毕业证书。学院坐落在全国首批十大“园林城镇”、国家AAAA级旅游风景区—颍上县迪沟镇天鹅湖畔，占地358亩，首创了“旅游胜地学旅游”的崭新办学模式。交通便捷，合-淮-阜高速公路相连省城合肥，车程约1小时左右。学院以“旅游大省”向“旅游强省”发展战略目标和学生就业为导向，以精导游',27871);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('合肥滨湖职业技术学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.hfbhxy.com/','合肥滨湖职业技术学院是2002年经安徽省人民政府批准、国家教育部备案设立的普通高等学校，座落于拥有“大湖名城、创新高地”之称的合肥市核心区域，毗邻中国五大淡水湖之一的巢湖，区位优势明显。学院占地560多亩，校舍建筑面积17万多平方米，馆藏图书50多万册，校内实验实训室30多个，仪器设备总价值近2000万元。教学楼、图书馆、阅览室、学生公寓、塑胶运动场、实验中心、学生餐厅、超市、医疗室、银行等教学生活设施齐全。学院确立了“立足合肥、面向安徽，为现代服务业培养德智体美全面发展的具有社会责任感和创新精',55564);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江西新能源科技职业学院',' ','江西',0,1,0,0,0,' ','暂空',' ','http://www.tynxy.com/','江西太阳能科技职业学院(前身江西中山职业技术学院)是经江西省人民政府批准，国家教育部备案，具有独立颁发普通高等教育高职（专科）学历文凭资格，纳入普通高等教育计划内招生的高等职业技术院校（国标代码：14166）。是全国首家也是唯一一家太阳能科技职业学院，学院致力服务于太阳能光伏和光热利用、新能源和节能减排等制造业为主线，以太阳能材料、应用技术、工程等专业为主，兼顾艺术设计和人文经管等多学科专业协调发展。学院坐落在环境优美、交通便捷的国家级园林城市、世界太阳能之都、国家新能源设备制造产业基地——江西',73278);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖北工程职业学院',' ','湖北',0,1,0,0,0,' ','暂空',' ','http://www.hbei.com.cn/index.html','黄石职业技术学院是经湖北省人民政府批准设立，国家教育部备案的公办全日制普通高等学校。学院地处我国著名的工业重镇，湖北省第二大城市黄石市。校园依山傍水、环境幽雅、交通便捷，是成才、治学的理想园地。学院占地400亩，建筑面积13万平方米，设有机械制造与自动化、数控技术、模具设计与制造等13个专业，这些专业均为国家制造业紧缺性人才培养专业。学院教学设施优良，总投资2个亿的校园扩建项目一期工程已竣工。拥有总值超1509万元的教学实验实训设备，多个校内实习车间和百余个校外实习基地。可编程控制室、PLC实验',195811);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京艺术传媒职业学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.ymyu.com/','北京新圆明职业学院（原北京圆明园学院）成立于1994年，是在北京大学、清华大学、中国科学院的一批关心社会力量办学的科学家和著名教授支持下创办的一所具有文、经、管、理工多学科相结合并协调发展，以专科教育为基础的全日制综合性民办普通高等学校。全国政协副主席马万祺、原副主席杨汝岱担任学院总顾问。国学大师季羡林教授担任学院的名誉院长。近百位院士和著名专家学者担任学院顾问。学院办学理念是：以德治教、育人为本。学院以培养基础理论扎实，知识面较宽，技术应用能力强，素质高，具有创新和敬业精神的高等应用型专门人才',57539);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁医药职业学院',' ','辽宁',0,3,0,0,0,' ','暂空',' ','http://www.lnyyzyxy.com/','辽宁医药职业学院是辽宁省职业教育改革发展示范校立项学校，是辽宁省唯一一所培养护理、药学、医学技术、康复技术、健康管理类技术技能人才的省属公办医药类全日制普通高等职业院校。学院主校区坐落在沈阳市浑河之南，毗邻沈阳国际展览中心，本溪校区位于沈本新城，目前正在建设中。校园总占地面积55.89万平方米，校舍建筑面积20.34万平方米。教学、科研仪器设备总值3614.97万元，馆藏纸质图书32.33万余册。辽宁医药职业学院的前身是辽宁省卫生职工医学院，创建于1984年。1998年被确定为辽宁省高等职业教育',259830);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东南方职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.gdnfu.com/index.html','schoolcode": "447000400',43675);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('石家庄经济职业学院',' ','河北',0,6,0,0,0,' ','暂空',' ','http://www.sjzjjxy.net/Index.html','石家庄经济职业学院是经河北省人民政府批准、教育部备案的民办普通高等院校，是河北省培养高级外事管理人才的重要基地，具有独立颁发国家普通高等学历文凭的资格。生源纳入全国普通高校招生计划，面向全国招生，招生对象为参加全国普通高考的高中毕业生及参加中考的初中毕业生，分为普通高等教育和中等教育两个层次。学院位于石家庄北部高校区，分为三简路校区和北校区。共占地600余亩，有学生5000余人，专兼职教师600余名，其中教授、副教授占1/3以上，并有多名外籍教师。学院初创于1999年，经过十二年的发展，现已发展',68881);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湘潭教育学院',' ','湖南',0,4,0,0,0,' ','暂空',' ','http://www.hnxtjyxy.com/','湘潭教育学院创建于1959年，是国家教育部批准备案的成人高校。2005年，经教育部批准，湘潭教育学院成为具有普通高等学历教育招生资格的高等学校。湖南省中专层次幼儿园教师培养学校。湘潭教育学院亦为湘潭市教师培训中心，全国教师培训特色示范学校，全国教师教育联盟学习与资源中心，湖南省农村小学英语计算机骨干教师培训学校，湘潭市中小学教师和教育行政管理干部培训基地。学院坐落于湘潭市中心，毗邻风景秀丽的雨湖公园，西望巍巍韶峰，东依迢迢湘水，既饱览湖光山色，又彰显人文气势，分雨湖和姜畲两个校区（高职学生就读于',21126);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('陕西省建筑工程总公司职工大学',' ','陕西',0,1,0,0,0,' ','暂空',' ','http://www.sjzdedu.com/','陕西省建筑职工大学是经陕西省人民政府批准成立，国家教育部首批备案的一所独立设置的全日制公办成人高等学校（教育部核准代码51010），2000年经省教育厅批准为第一批具有独立招收全国普通高校高职生资格的院校之一，是目前省内唯一一所建筑类高职高专院校。陕西省建筑职工大学以土木建筑类专业为特色，土建类学科历史悠久，教育设施先进完备，专业梯队完善，拥有一支富有创新精神和丰富教学实践经验的“双师型”师资队伍。多年来，学校以就业为导向，专业设置符合社会需求，把专业做强做优，成功地开辟了一条具有自身特色的高职',43405);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('开封教育学院',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.kfie.edu.cn','开封教育学院是一所公办的具有普通专科招生资格的成人高等专科院校。学院成立于1979年，是一所以师范为主、文理兼备的专科学院，在省内外有着良好的声誉，曾被授予“全国学校体育工作先进单位”、“省科研先导型学校”、“师资培训先进单位”、“语言文字工作先进单位”、“教师资格认定工作先进单位”等荣誉称号。学院占地面积414.42亩，建筑面积11.6万多平方米，固定资产1.72亿元。设有卫星地面接收站及闭路电视系统、千兆校园网络、多媒体教学评估系统和其他现代化教学、实验设备，教学、实验仪器设备总值2383万',41578);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽人口职业学院',' ','安徽',0,0,0,0,0,' ','暂空',' ','http://www.ahrkxy.com/','安徽人口职业学院是经省人民政府批准，教育部同意备案的专科层次的公办高等学校，隶属于安徽省人口和计划生育委员会，业务上接受安徽省教育厅指导。为全国第一所人口高等职业学院。安徽人口职业学院是一所具有八十多年历史的公办学校，学院的前身是始建于1926年的芜湖戈矶山医院附设怀让高级护士学校，1969年，学校迁址池州，更名为池州卫生学校。1987年，更名安徽省计划生育学校，隶属安徽省计划生育委员会。2012年3月经省人民政府批准设置安徽人口职业学院并经教育部同意备案。学院位于安徽省池州市教育园区，依山傍水',33112);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽长江职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','oldname": "安徽长江职业学院','安徽长江职业学院是经安徽省人民政府批准、国家教育部备案，具有独立颁发专科学历文凭的民办全日制普通高等职业院校。学院座落在合肥经济圈环巢湖旅游景观大道旁黄麓镇境内，占地面积530亩，东距巢湖市区29公里，西接合肥市滨湖新区30公里，南滨浩淼巢湖，北靠著名“和平将军”张治中的故里、素有“文化之乡”美誉的黄麓古镇，交通便捷，风景秀丽。学院利用周边优越的自然景观，通过艺术性创造与自然环境相结合，全方位营造出具有时代感的绿色校园及浓郁的大学文化氛围，为学生创造了优良的学习环境。学院坚持“人才兴校、质量强校',18217);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都经济贸易大学密云分校',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.jmdx-myfx.com','首都经济贸易大学密云分校是密云县人民政府和首都经济贸易大学根据北京市人民政府（84）97号文件精神，于1984年联合举办的一所公办高等院校，主要培养专科层次技能型经济贸易类管理人才。建校二十七年来，为北京地区培养了近万名毕业生，为首都的经济社会发展做出了应有的贡献。学校设有贸易经济系、经济管理系、计算机与信息工程系及相关的职能处室，开设了会计、会计电算化、财务管理、金融与证券、国际经济与贸易、工商企业管理、物业管理、旅游管理、房地产经营与估价、物流管理、市场营销、商务英语、电子商务、软件技术、计',44449);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('延边职业技术学院',' ','吉林',0,1,0,0,0,' ','暂空',' ','http://www.ybvtc.com','延边职业技术学院是国家教育部备案的公办全日制普通高职院校，占地254亩、建筑面积7.2万平方米，图书馆藏书18万册，教学设施齐全，是国家紧缺人才培训基地、省中小企业培训基地，现有44个实验实训室。设延边州唯一指定的会计从业资格证书无纸化考试考点、职业技能公共实训基地。',23982);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('云南外事外语职业学院',' ','云南',0,5,0,0,0,' ','暂空',' ','http://www.fafl.cn/','学院简介云南外事外语职业学院始建于2012年5月，是一所按新体制、新机制设立，国家教育部备案，纳入国家计划内统一招生，具有独立颁发国家承认学历资格的全日制普通高等院校。由云南亨德森投资控股集团有限公司投资，在原亨德森学校雄厚师资力量的基础上，坚持以多学科协调发展的整体专业布局，以“特色鲜明、应用为本”的理念培养人才。云南外事外语职业学院位于昆明市杨林职教园区东北角，占地393.58亩,建筑面积262387.6平方米，整齐漂亮的教学楼、图书馆、办公楼、实验室，会堂、多功能活动室、标准化的田径场和篮',27844);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江省政法管理干部学院',' ','黑龙江',0,11,0,0,0,' ','暂空',' ','oldname": "黑龙江省政法管理干部学院','黑龙江省政法管理干部学院是经黑龙江省人民政府批准并报教育部备案的具有普通高等学历教育招生资格的公办高等院校，隶属于中共黑龙江省委政法委，由省司法厅代管，是东北地区唯一一所集普通教育、成人教育、干部培训为一体的法学高等院校。学院拥有省内一流的法学教育师资队伍，成熟的法律人才培养模式，良好的政法系统培养环境，稳定的毕业生就业市场。建院以来，共培养法律人才4万余人，遍及全国，尤其集中于我省公安、法院、检查、司法、安全、监狱、税务、工商等系统的各级单位。其中相当一部分人也已经走上了各级领导岗位，很多人被',16719);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('沈阳北软信息职业技术学院',' ','辽宁',0,1,0,0,0,' ','暂空',' ','http://www.nsi-soft.com','沈阳北软信息职业技术学院隶属于辽宁省教育厅，前身是沈阳航空航天大学北方软件学院，是辽宁省复合型、实用型软件、翻译人才培养的重要基地。沈阳航空航天大学北方软件学院成立于2001年9月，是由沈阳格微软件有限责任公司等单位共同组建的省属院校，是辽宁省教育厅首批批准设立的省级示范性软件学院，是培养国际化、复合型软件开发人才的重要基地和特区。通过产学研一体化，构建了人才培养、科学研究、技术孵化和产品开发多位一体的高等教育模式，积极进行教学内容、课程体系的改革与创新，学院把基础研究、应用研究、科技创新作为科',47826);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('安徽粮食工程职业学院',' ','安徽',0,1,0,0,0,' ','暂空',' ','http://www.ahlsxy.cn/','安徽粮食工程职业学院是经安徽省人民政府批准，国家教育部备案的公办省属全日制普通高等专科院校，是教育部、国家发改委等七部委联合表彰的全国职业教育先进单位。学院坐落于省会合肥市高教基地，教学和生活设施齐全、功能配套，能充分满足学生的学习和生活需要。学院设有粮食工程系、食品生物系、工商管理系、机电工程系、信息技术系和基础学科部，建有图书馆、实验实训中心、现代教育技术中心。教师队伍中，高级职称教师占40%，其中“双师型”教师占专业教师的73%。近三年承担市级以上科研项目24项，有2项获省级重点课题优秀奖',617649);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广州大学市政技术学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://221.4.38.121:8080/','广州大学市政技术学院是广州大学属下的校外公办二级学院，招生名称：广州大学，办学地点：花都校区，招生批次：专科A线。学院举办全日制大专层次高等职业教育，同时开办成人专（本）科以及特殊高等教育,主要培养城市建设与管理专门人才，目前在校生达7000人。学院建筑风格新颖，环境宜人，建有功能齐全、设施先进的教学楼、实验实训楼、图书馆、运动场、学生公寓等，共建有90多个专业实验室、实训室。学院重视专业设置与课程建设，以市场需求为导向，构建了“大市政”专业链群，现设专业21个。市政工程技术、道路桥梁工程技术是',85468);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('哈尔滨传媒职业学院',' ','黑龙江',0,0,0,0,0,' ','暂空',' ','http://www.hrbmcc.com/','哈尔滨传媒职业学院是经黑龙江省人民政府批准,教育部备案的全日制普通高校。学院坐落于素有“东方小巴黎”之称的北国冰城哈尔滨，距市区20分钟的车程。学院占地16万平方米，建筑面积6.4万平方米。现有专任教师134人，副教授以上教师占教师总数的34%，硕士以上学历教师44人，双师型教师60人。同时拥有230多人的兼职教师团队。广播影视类教师主要来自省内一线广播电台、电视台、演艺团体和传媒机构。专本衔接本科阶段教学主要由吉林大学教师承担课程，其中60%以上教师为博士生导师。学院教学科研仪器设备先进，与省',37377);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('四川文轩职业学院',' ','四川',0,12,0,0,0,' ','暂空',' ','http://www.scwxzyxy.cn/','四川文轩职业学院是由国有资产创办的，经四川省人民政府批准、国家教育部备案、纳入国家计划内统一招生的全日制普通高等职业院校。学院以“办人民满意的教育”为宗旨，以“立德、务实、勤学、求真”为校训，以“成长、成才、成功”为目标，努力培养基础理论扎实、实践能力过硬、综合素质高、创新意识强、具有爱岗敬业品质的应用型高级人才。学院开设有以出版与发行为龙头，以服装、数控、建筑为重点，涵盖信息技术、经济管理等领域的6个专业群和专业方向。学院的办学历史可以追溯到1993年，已培养出17万名合格的大中专毕业生，输送',54398);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁水利职业学院',' ','辽宁',0,0,0,0,0,' ','暂空',' ','http://zszx.lnslzy.cn','辽宁水利职业学院（原沈阳农业大学高等职业技术学院）坐落于辽宁省省会——沈阳市，是经辽宁省人民政府批准，国家教育部备案的一所隶属于辽宁省教育厅的专科层次的全日制公办普通高等学校，是目前辽宁省内乃至东北地区唯一的一所水利类高职院校。学院前身为辽宁省水利学校，成立于1951年，2000年10月并入沈阳农业大学成立沈阳农业大学高等职业技术学院。2012年12月，经辽宁省人民政府批准，学院从沈阳农业大学剥离，成立辽宁水利职业学院，独立开办高等职业教育。63年的办学历史，15年的高等职业办学实践，学院积淀了',188137);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁轻工职业学院',' ','辽宁',0,12,0,0,0,' ','暂空',' ','http://www.lnqg.com.cn/','拥有五十年职业教育辉煌历程的辽宁轻工职业学院，位于钟灵毓秀、经济发展日新月异的大连市金普新区。她的前身为大连工业大学职业技术学院。2012年8月，经辽宁省人民政府批准，学院独立设置，成立辽宁轻工职业学院，是辽宁及东北地区唯一一所轻工类高等职业院校，也是大连地区唯一一所省属公办高职专科院校。2015年学院进入省示范校建设行列。学院隶属于辽宁省教育厅，占地面积16万平方米，总建筑面积12万平方米，现有全日制在校生6091人。拥有各类专业工作室、教学实训室77个。专业校外实习实训基地及毕业生顶岗实习基',70779);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁轨道交通职业学院',' ','辽宁',0,12,0,0,0,' ','暂空',' ','http://www.lgzy.com.cn/','辽宁轨道交通职业学院坐落在我国重工业基地辽宁省省会沈阳市，是一所以服务轨道交通业为特色、以服务装备制造业为主体，全日制学历教育、职业培训和应用性技术服务并重发展的高职院校。2012年，学院经辽宁省政府批准，正式成立。学院在六十二年的办学历史中，先后举办过中专、本科和专科教育。多次被评为国家级重点中等职业学校，辽宁省职业教育示范校，先后荣获全国职业教育先进集体、全国中等职业学校德育工作先进集体、辽宁省职业教育先进集体等荣誉称号，多次在全国职业院校技能大赛、机器人比赛、“文明风采”比赛中取得优异成绩',77086);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南三一工业职业技术学院',' ','湖南',0,12,0,0,0,' ','暂空',' ',' http://www.sanyedu.com/','湖南三一工业职业技术学院简介湖南三一工业职业技术学院坐落于山清水秀的长沙县榔梨工业园区，是由三一集团出资创办、湖南省政府批准、国家教育部备案的全日制高职院校。学院根植三一、服务三一，着力为三一集团培养售后服务、生产管理、物流管理等高技能、紧缺人才。历届毕业生推荐到三一集团就业的比例达98%以上。培养三一湘军，传承三一文化。办学历程中，湖南三一工业职业技术学院秉承三一集团“品质改变世界”的理念，形成了基础扎实、思维活跃、适应能力强、综合素质高的人才培养特色，办学规模逐年快速增长。一流的教学设施学院',28185);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('甘肃卫生职业学院',' ','甘肃',0,3,0,0,0,' ','暂空',' ','http://www.gswx.com.cn/','甘肃省卫生学校地处兰州市城关区东岗西路60号，与兰州大学第一医院、省人民医院、甘肃中医学院附属医院等三甲医院和兰州大学、甘肃中医学院、兰州商学院等高校相邻，是甘肃省卫生厅主管的首批国家级重点中等卫生学校、首批国家中等职业教育改革发展示范学校项目建设学校、全国护理专业技能型紧缺人才示范性培训基地。1、久远的办学历史。学校发端于1956年创立的“甘肃省兰州护士学校”，1962年6月更名为“甘肃省兰州卫生学校”，1969年经甘肃省革委会决定撤销“甘肃省兰州卫生学校”，并于1970年搬迁至定西，创立了“',28098);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东职业学院西校区',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdzyxx.net/','山东职业学院西校区位于济南市高新技术开发区新泺大街201号，属于济南CBD拓展地带，南临齐鲁软件园、国家动漫产业基地和山东大学齐鲁软件学院，北靠国际会展中心，毗邻山东松下电子、浪潮集团、大陆机电等高新技术企业。周边有济南奥体中心、万达广场、美莲广场、雨滴广场等。87、63、K163、K166、123、202等多路公交车停靠学校门口，可直达泉城广场、解放桥、燕山立交、奥体中心等城市观光中心和交通枢纽地带，是感悟泉城济南、接受现代教育的理想场所。山东职业学院西校区2016年普通高职招生计划为1000',119909);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('辽宁特殊教育师范高等专科学校',' ','辽宁',0,4,0,0,0,' ','暂空',' ','http://www.lntjw.com/','辽宁特殊教育师范高等专科学校,前身为1995年建校的辽宁省残疾人中等职业技术学校，2012年经省政府批准成立辽宁特殊教育职业学院，2013年3月经省政府批准，成立辽宁特殊教育师范高等专科学校,是我省高等特殊教育独立院校。省教育厅批准实行“3+2”高职专业单独招生、单独考试、单独录取,3年制高职专业与辽宁卫生职业技术学院、辽宁经济职业技术学院、辽宁民族师范高等专科学校联合办学。作为省政府重点民生工程的新校区位于沈阳市东陵区，现代化的校园，完善的教学设备，全国领先的无障碍设施，为学生的发展奠定了坚实',24012);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州建设职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://www.gzjszy.cn/','贵州建设职业技术学院是贵州省教育厅直属的全日制公办普通高等院校，以全日制普通高等教育为主、中等职业教育为辅，同时举办各类成人及非学历教育。学院代码：4152014516办学层次：高职(大专)教育办学类型：普通高等教育学习形式：普通全日制办学性质：公办学院坐落于贵阳市清镇职教城东区，占地511亩，与百花湖毗邻，设施完善，风景优美。经过三十余年发展，目前开设有“建筑工程、城市建设、建筑设备、建筑材料、经济管理、艺术设计”等六大类几十个专业，各类在校学生逾万人。多年来，学院秉承“厚德、精技、乐学、善教',15865);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('成都工贸职业技术学院',' ','四川',0,0,0,0,0,' ','暂空',' ','http://www.cdgmxy.com','成都工贸职业技术学院位于成都市郫县红光镇，毗邻国家级高新技术产业开发区--高新西区。学院占地350余亩，建筑面积15.81万平方米，是经国家教育部批准成立的一所独立设置的公办全日制普通高等职业技术学院，由成都市人民政府主办，四川省教育厅主管；是“国家高技能人才培养示范基地”、“全国物流职业教育人才培养基地”、“四川省再就业培训基地”、“四川省高新技术公共实训基地”。学院坚持“工学一体、德能并重、品质就业、助推产业”的办学理念，坚持“高端引领、校企合作、培养高素质技术技能人才”的办学思路，瞄准成都',90368);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州工程职业学院',' ','贵州',0,0,0,0,0,' ','暂空',' ','http://www.gzieu.com','贵州工程职业学院是经贵州省人民政府批准设立，教育部于2015年6月5日备案审核的一所综合性高等职业院校，部编代码：14558。学院由香港科创集团与贵州西凯恒星教育投资有限公司联合举办。学院坐落于贵州省德江县城南新区，总投入15亿元，规划占地700亩，建筑面积25万平方米。2014年3月底前完成一期工程建筑面积约15.26万平方米，能满足5000名新生需求。二期工程建筑面积约10万平方米，计划2016年8月全面竣工，届时在校生规模可达10000人。【办学宗旨】即“兴教为民、启佑乡邦”。学院以市场需',2428);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京网络职业学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.bjwlxy.org.cn/','信息化时代，互联网技术深刻影响着全球社会与经济的发展。互联网技术已经成为我国经济转型和产业升级的重要引擎。在新的经济形态下，各行各业都在努力融入互联网发展的大潮之中，迎接互联网技术带来的新机遇，社会对互联网人才的需求持续增长。北京网络职业学院是为我国大力实施网络强国战略、国家大数据战略、“互联网+”行动计划培养人才的一所全日制高等职业院校。',2720);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵阳幼儿师范高等专科学校',' ','贵州',0,4,0,0,0,' ','暂空',' ','http://www.gypec.edu.cn/','贵阳幼儿师范高等专科学校前身系贵阳幼儿师范学校，创建于1984年，是贵州省最早的一所培养、培训幼儿师资的专业学校，在贵州学前教育领域具有重要的影响和地位。2012年12月20日，经贵州省人民政府批准(黔府函【2012】299号《省人民政府关于同意建立贵阳幼儿师范高等专科学校的批复》)，正式升格为贵阳幼儿师范高等专科学校。学校占地总面积474亩,其中清镇新校区453亩、忠烈街校区21亩。新校区采用英伦风格的布局方式，不同几何形状的构成要素形成不同的视觉中心。各中心互相渗透、交错贯通，又保持每个院系',699);
