insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('贵州水利水电职业技术学院',' ','贵州',0,1,0,0,0,' ','暂空',' ','http://sdzy.gzssdx.cn','贵州水利水电职业技术学院是经贵州省人民政府批准成立，国家教育部备案的全日制公办普通高校。学院隶属于贵州省水利厅，以全日制普通高等教育为主、中等职业教育为辅，同时举办继续教育。学院秉承“自强崇实、德技双馨”的校训，奉行“德育为先，能力为重，全面发展”的育人理念，大力推行工学结合、校企合作的人才培养模式。历经近60年的发展，学院孕育了数以万计的技能型人才，被誉为“贵州水利人才摇篮”。学院位于贵阳清镇职教城，占地面积600亩，规划建筑面积31.4万平方米。',215);
