insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东茂名健康职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.mmjkzyxy.com','广东茂名健康职业学院于2015年3月经广东省人民政府批准、报教育部备案设立的全日制公办普通高等院校，是广东省第一所，全国第二所职业学院。学院位于茂名滨海新区水东湾新城的安乐大道，北靠沈海高速、南面水东湾，面临包茂高速，东接325国道，交通便利，环境宜人。市委市政府对学院校区项目建设高度重视，把它纳入2014年市政府工作报告重要事项和2014年市重点建设工程。该项目总体规划用地550多亩，建筑面积约130000平方米，总投资5.5亿元。首期建设用地356亩，校舍85000平方米，室外运动场地面积3',22);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东碧桂园职业学院',' ','广东',0,0,0,0,0,' ','暂空',' ','http://www.bgypt.com','广东碧桂园职业学院由碧桂园控股有限公司董事局主席杨国强先生、副主席杨惠妍女士创立的广东省国强公益基金会投资主办，经广东省人民政府批准、教育部备案，由广东省教育厅主管，是一所慈善性质的全日制普通高等学校。学院缘于杨国强主席中华民族伟大复兴的中国教育梦。继成功创办慈善性质的国华纪念中学之后，他毅然注册成立了广东省国强公益基金会，并以基金会为主体，创办了广东碧桂园职业学院，一方面系统地培养精技善用的高素质技术技能型精英人才，服务当前经济社会发展;另一方面希望广东碧桂园职业学院的举办，以其精英式培养、高',28);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东茂名幼儿师范专科学校',' ','广东',0,4,0,0,0,' ','暂空',' ','http://www.gdgzsf.cn','广东茂名幼儿师范专科学校是茂名市人民政府主办的高等师范院校，是广东省第一批幼儿师范专科学校。学校前身为广东石油化工学院高州师范学院，始建于1930年，时为茂名县立乡村师范学校;1949年与广东省立高州女子师范学校合并为广东省立高州师范学校;1980年经省人民政府批准更名为广东高州师范学校;1993年试办“三二分段”大专班，是全国36所试办学校之一;2001年作为二级学院并入茂名学院，更名为茂名学院高州师范分院;2010年更名为广东石油化工学院高州师范学院。2016年2月29日，广东省人民政府批复',38);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广东生态工程职业学院',' ','广东',0,2,0,0,0,' ','暂空',' ','http://www.gdsty.cn','广东生态工程职业学院是由广东省林业厅主办，经广东省人民政府批准成立、教育部备案的一所公办普通高等职业学院。学院是在具有63年办学历史的“广东省林业职业技术学校”基础上创办的。学院位于广州市天河区广汕一路297号，毗邻华南植物园，占地面积633亩;学院是广东省“文明单位”、广州市“花园式单位”、全国“五四红旗团委”单位、全国“最具就业竞争力示范院校”单位、全国“群众体育先进”单位、全国“模范职工之家”，是“广东农业职业教育集团”副理事长单位和“广东女性职业教育”理事单位。学院负责人是博士生导师廖金',67);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京第二外国语学院HND项目',' ','北京',0,12,0,0,0,' ','暂空',' ','http://hnd.bisu.edu.cn','学院简介北京第二外国语学院是在敬爱的周恩来总理的亲切关怀下于1964年创立的，是一所以外国语言文学为主体学科、以旅游管理为特色学科，文学、管理学、经济学、法学等多学科门类协调发展的知名特色大学，是中国外语、翻译、旅游、经贸教学与研究的重要基地。秉承明德、勤学、求是、竞先的办学理念，提出“建设国际化、有特色、高满意度的教学研究型大学”的发展目标，立足于北京，服务首都，辐射全国，为国家的经济社会发展培养外事、旅游、经贸、文化等方面的国际性应用型人才。学校注重国际化办学，与美国、日本、德国、法国、澳大',487940);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南大学国际教育学院',' ','湖南',0,0,0,0,0,' ','暂空',' ','http://gjxy.hnu.cn','湖南大学坐落在湖南省长沙市岳麓山下，是由岳麓书院衍续至今、办学历史悠久的全国重点大学，直属国家教育部，是国家“211工程”和“985工程”重点建设的高校，设有研究生院和国家示范性软件学院。湖南大学国际教育学院成立于1998年，原名湖南大学美雅国际教育学院，是湖南大学30个学院之一，具体承办湖南大学与国外高校合作办学项目以及课程修读项目，招收中外双校园本科、研究生等出国留学生，培养具有国际视野的东西方先进文化互补的国际型高级专门人才。学院现与多个国家的多所高校形成了稳定的合作关系，其中包括加拿大里',107334);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京建设大学',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.bjjsdx.com','北京建设大学是由中国农业大学下属科技孵化器举办的一所以高等教育、对外合作办学和综合职业技能培训为主的综合性民办大学，是中国农业大学坚持开拓创新，深化教育教学改革，实施科教兴国战略，不断拓展服务社会功能的创新和有效模式的探索与实践，开创了“名校办民校”的先河。北京建设大学是国内创办较早的民办高等教育大学之一，1989年2月在九三学社中央和周培源先生的倡导下，启动创办，其发展得到了政府和社会各界的广泛支持，取得了一系列令人瞩目的成就：1993年被市教委批准为北京市首批国家高等教育学历文凭考试学校。1',112907);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京计算机专修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.jsjedu.com','北京计算机专修学院是专门从事计算机教育，以计算机技术、信息技术、网络技术是其主导专业，还包括网络游戏与影视动画设计、计算机艺术设计等社会急需的技能型专业。同时，还设有电子商务、管理、金融等其他专业。2003年、2007年被专家和教委评为合格院校；2004年被授予国家学历文凭考试试点单位；2004年底被中国教育研究所人力资源部授予“人民满意的民办院校”；2005年被信息产业部电子行业职业技能鉴定指导中心授予“优秀参与奖”，授予“IT职业技术教育工程定点院校”资质；2005年获得中国教育研究所、联合',27254);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林大学—莱姆顿学院',' ','吉林',0,0,0,0,0,' ','暂空',' ','http://www.julc.cn','中国第一所经教育部批准完整引进北美高校教育体系的中外合作办学本科院校。吉林大学-莱姆顿学院成立于1999年，是经教育部批准、由吉林大学与加拿大纽芬兰纪念大学、美国诺斯伍德大学等国际知名高校合作，实施加拿大、美国全日制本科学士学位教育的高等院校。学院与美国中密西根大学合作举办研究生项目，学生可实现本硕连读。学院培养具有国际视野、通晓国际规则，既懂专业又具备较强英语交流能力的国际化复合型人才。对按要求完成规定学业的学生，颁发加拿大或美国合作大学的本科学士学位证书，中国教育部承认学历及学位，并颁发就业',232284);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京人文大学',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.bjrwdx.com/','北京人文大学是经北京市教委批准、国家教育部备案成立的一所文、理、工、经、管、武相结合的综合性全日制民办高等学校，创办于1984年，是全国改革开放以来成立最早的民办高校之一。二十二年来，以其强大的办学背景、科学的管理、雄厚的师资力量和严谨的治学态度，先后为国家各条战线培养了50余万高素质人才，其中包括大家熟知的著名演员洪学敏、朱时茂，中国足协前主席年维泗以及中央电视台《军事天地》节目主持人唐剑等。目前，学校设有计算机学院、国际会计金融学院、对外经济贸易学院、国际工商管理学院、法学院、外语学院、文学',389459);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国管理软件学院',' ','北京',0,12,0,0,0,' ','暂空',' ','oldname": "中国管理软件学院','中国管理软件学院是一所经北京市教委批准，合法的非盈利性的全日制民办高等学府。成立于1984年，是中国第一所计算机软件学院，是培养IT高科技人才的摇篮。自成立以来，学院一直致力于各学科的研究与教学。现已开设50多个专业，其中以计算机控制及应用专业而闻名，以通信工程为名牌，以计算机网络专业为特色，以计算机软件及应用为优势，尤其是所设工、理、管、文、法等各专业紧密与软件相结合而著称，所设置的课程在北京乃至全国都享有盛誉。中国管理软件学院实行由清华大学侯炳辉教授等计算机和电子信息专家、学者组成的董事会领',89995);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('立信国际',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.lixinedu.cn/','立信国际上海立信会计学院国际财经学院（简称立信国际），为上海立信会计学院直属学院。立信国际自2005年开办至今，集多年的中西教育理念和教育实践于一体，努力探索高等教育与职业规划充分融合协调发展的教育理念，成就了颇具开拓精神的合作模式，并获得了显著的成功，为社会培养了一大批具有国际视野、国际知识及国际业务能力的财会、金融、管理领域的复合型人才。早在1997年，立信与英国国际会计师公会携手开办英国国际会计师方向班，集多年的中西方教育理念和教学实践于一体，努力探索高等教育与职业发展充分融合协调的教育新',815801);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京东方大学',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.yuloo.com/mbgx/dongfang/','北京东方大学创办于1984年8月，是经北京市教委批准、备案的全日制、综合性民办非学历高等教育机构。学校现设有孔子学院、建筑工程学院、国际商学院、文化创意学院、民航管理学院、国际护理学院、传统文化学院、国际珠宝学院、国际旅游学院、体育学院10个学院，开设了近40个职业教育热门专业，100多个培养方向，涵盖了文、法、理、工、经、管、艺等多个学科。地理位置学校地理位置优越，位于北京城市总规划中核心经济区的重点发展带上，北为CBD(CentralBusinessDistrict，中央商务区，是首都商业和',176062);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京华夏管理学院',' ','北京',0,6,0,0,0,' ','暂空',' ','oldname": "北京华夏管理学院','北京华夏管理学院，始创于1994年，是教育部批准成立最早的中外合作高等院校之一；2009年1月，世华集团凭借其强烈的民族责任感和丰富的教育资源对北京华夏管理学院进行重新整合优化；2010年3月起经北京市教委批准正式转为具有独立法人资格的全日制民办高校（《中华人民共和国民办学校办学许可证》教民111010020000480号）。2010年5月北京华夏管理学院重新定位，确立“铸造民族之魂、传承管理大道、培育华夏英才、促进世界发展”办学使命，将华夏管理学院办成“中国管理人才首选培育基地”办学定位。遵循',87350);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东省工会管理干部学院',' ','山东',0,0,0,0,0,' ','暂空',' ','http://www.sdmu.edu.cn/','山东省工会管理干部学院是国家教育部和山东省人民政府批准的全日制公办高等院校。1987年建院以来，学院始终坚持“依托工会，突出特色，为经济社会和谐发展服务”的办学方针，遵循高等教育发展规律，经过不断探索和实践，现已成为全国同类院校中规模最大、专业齐全、实力较强的高等院校，是全国学校艺术教育先进单位，山东省高职高专人才培养工作水平评估优秀学校，山东省高校德育教育先进单位和山东省毕业生就业先进单位，2011年被山东省总工会授予“富民兴鲁劳动奖状”。目前学院正在聚集发展合力，加快发展步伐，全力推进改建升',253426);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉电子信息专修学院',' ','湖北',0,0,0,0,0,' ','暂空',' ','http://www.whue.edu.cn/','武汉电子信息专修学院是具有高等教育自学考试办学资质的助学机构，是武汉市教育局批准、湖北省教育厅认定的高等教育自学考试助学单位，是全国高等教育自学考试委员会（教育部考试中心）登记备案的高等教育自学考试社会助学组织、中国民办教育协会理事单位（湖北省教育厅推选）、中国民办教育协会高专委常务理事单位、中国高等教育学会自考分会会员单位。从2002年开始与武汉理工大学合作举办高等教育自学考试助学班，是武汉理工大学8个高等教育自学考试助学站中最大的一个。武汉电子信息专修学院已有二十年的办学历史，坐落在马房山工',73539);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京工商管理专修学院',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.bjuba.com.cn/','北京工商管理专修学院（此学校为计划外招生学校）于1994年经北京市教育主管部门批准成立。现依托大型国有企业办学，坚持走“适度规模、内涵发展、校企合作、特色取胜”之路，主要举办高等教育自学考试助学和高等教育培训，实行多层次、多元化教育，致力于培养符合现代企业需求的高素质技能型、应用型人才，为首都经济社会发展服务。学院位于首都高校林立地，学术文化气息浓郁的北京昌平区，校园占地面积210余亩，建筑面积12万平方米；拥有图书馆、阅览室、实验室、学生公寓及食堂、浴室、超市、运动场等设施；拥有1200余台计',100846);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京京海研修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.jheduol.com/','北京京海研修学院（BeijingJingHaiInstitute）是经北京市教委注册批准的一所从事高等教育自学考试助学及非学历职业教育的综合性民办高等院校，创办于1988年，位于中国的硅谷北京中关村。过去的二十年里，在学院的创始人、著名教育家、民办教育成就奖、民办教育特别贡献奖获得者尚镭教授的带领下，以学院为平台，利用北京中关村教育、科研人才的聚集优势，发挥自身的教育资源优势，同时整合京城一类大学知名教授、高级管理人才，按照政府主导、行业管理、企业支持、院校参与的原则，以培养人才为手段，以行业、',13031);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东医药技师学院',' ','山东',0,3,0,0,0,' ','暂空',' ','http://www.sdyyjsxy.com','山东医药技师学院为直属山东省劳动和社会保障厅管理的国办学校，办学特色鲜明、实力雄厚，是集技师、高级技工、本专科学历教育和职业技能鉴定为一体的全日制国家级重点技术院校。建校三十多年来为全国医药企事业单位培养各类人才30000余人，技能鉴定20000余人。学院地处举世闻名的泰山脚下，北临泉城济南，南接孔孟之乡曲阜，占地340亩。学院秉承“明德厚能、敬业乐群”的职教理念，将保障人类健康的医药与百年大计教育相结合，遵循“就业才是硬道理”的指导思想，精心施教，保证每位莘莘学子能够学有所长、学以致用、学有所',103934);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海理工大学中英国际学院',' ','上海',0,1,0,0,0,' ','暂空',' ','http://www.sbc-usst.edu.cn/zh/','为实现上海市政府科教兴市战略，培养面向未来的“先进制造业和现代服务业”的急需人才，在上海市政府的大力支持下，上海市重点发展院校——上海理工大学与谢菲尔德大学、利兹大学等9所世界一流大学共同创办上海理工大学中英国际学院。上海理工大学中英国际学院是上海理工大学下属的非独立法人二级学院，也是目前国内领先的成体系、成规模、集约化引进海外一流大学优质教育资源的国际化大学学院。学院2006年经国家教育部正式批准，主要实施本科层次学历、学位教育和境外学士学位教育，也是目前国内少数采用“一对多”模式的中外合作大',433184);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京北大资源研修学院',' ','北京',0,12,1,0,0,' ','暂空',' ','http://www.pkurc.com/','校园简介北京北大资源研修学院成立于1992年5月，是经北京市教育主管部门批准成立的民办非学历高等教育机构。我校历经20年的发展，已成为一所有社会信誉度、办学经验丰富、教学特色鲜明、专业设置与市场衔接紧密的应用型学校。2010年被教育部中国教师发展基金会评为\"全国民办教育先进集体\"；2010年被北京市公安局海淀分局授予\"海淀区高校治安管理工作先进单位\"称号；2010年常务副校长李红梅被教育部中国教师发展基金会评为\"全国民办教育先进工作者\"。学校以培养具有创新精神和实践能力的应用型人才为宗旨，以大力',24248);
