insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京中新企业管理学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.zxcollege-edu.cn/','北京中新企业管理学院创建于1993年，经北京市教委批准，由前国家经委主任、中国人民大学校长袁宝华任名誉院长，由我国著名管理学家、中国人民大学教授夏光仁先生倡议并联合三十四家企业共同创办，以\"企业管理学院\"命名的全日制民办非学历高等学府。学院定位于为高科技岗位培养科技与管理人才，毕业生就业率2009年达到97%以上。创办16年来，为企业培养了众多的中高层骨干精英与管理人才，从而成为名副其实的高科技企业人才的\"孵化器\"和\"企业家摇篮\"。学院下设商学院、计算机与信息技术学院、数字艺术设计学院，共开设八',14134);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京东方研修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.bocch.com/','北京东方研修学院是经北京市教育委员会于2002年批准成立的。学院坐落在东方大学城内，教学资源完善、环境幽雅、设备先进、交通方便。东方大学城地处于北京东南郊接壤的廊坊，临近京津塘高速公路，城内配有现代化的教学楼、图书馆、文化宫、礼堂、体育馆、学生食堂、公寓以及标准运动场等，是实现后勤社会化管理的高校园区。当地政府为大学城专门设置了公安分局、交警支队、消防大队、城管大队、卫生防疫站、工商、税务等政府职能部门，确保大学城内安全稳定的学习生活环境。我院应时代发展的需要，开办了独具特色的：航空旅游分院、高',12191);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京兴华大学',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.bjxhu.com/','北京兴华大学创建于1983年，是北京市乃至全国建校最早的民办非学历高等教育机构之一。二十八年前，学校的前辈们用她们的智慧灵光，点燃了兴华大学这把星星之火，汇聚起数以万计的学子们的期盼与渴望，辛勤与汗水，浇铸成梦想与荣光，浇铸成校史长廊。二十八年来，兴华大学迎来送走了一批批来自祖国各地的莘莘学子，事业感、责任感伴随着风雨历程让我们分享到了青春的美好、快慰与喜悦，更让我们感受到大学殿堂的高洁、完美与珍贵！二十八年来，学校始终坚持发展才是硬道理的观点，求真务实，求新求变，变中不断拓展工作新局面；始终坚',28034);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京财经专修学院',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.beicai.com/','北京财经专修学院（此学校为计划外招生学校）隶属于北财教育集团教育事业部，是经北京市教委批准，由北京市财政局于1982年创办的民办高校。学院在最初开展学历文凭教育的模式上创新职业教育，实行“校企合一”的办学模式，以市场需求为导向，以岗位需求为标准，以高薪就业为目标，培养高技能、高素质、高质量的应用型人才。学院位于北京南中轴路东侧，地处拥有国家级亦庄经济技术开发区及即将建设的世界第一大机场（首都第二国际机场）的北京大兴新区，这里名企云集，人杰地灵，北财学子在这片充满生机的沃土上快乐地成长、成才。北财',49618);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京涉外经济专修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.bjaaa.com','北京涉外经济专修学院是由北京外国语大学陈琳教授发起，于1994年6月经国家教育行政部门批准创办，2001年、2006、2007年均通过北京市教委组织的专家评估，成为“首批合格院校”。它是一所面向全国招生、具有办学特色、与国际接轨的全日制大学。学院座落在北京中关村昌平园，毗邻长城、明十三陵和十三陵水库，学院内风景秀丽、空气清新，被誉为“花园式学校”，北京大学新校、中国政法大学、石油大学、国防大学等著名高校环布周围，文化气息浓郁。',12530);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京经济研修学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.bibac.com.cn/','办学资质北京经济研修学院，创建于1994年8月，是经北京市教委批准成立的一所综合性全日制民办非学历高等教育机构。办学宗旨学院始终秉承“教育就是服务”的办学理念，凭借优质的师资、严格而人性化的管理，畅通的就业渠道，把学生的成长和成才作为学院的最高使命。办学特色学院曾与中国人民大学合作多年，圆了万余名学生的成才梦。学院发挥师资优势，精心组织教学，开设了社会上热门的自考助学专、本科专业，涵盖文、法、经、管等多个学科，并成立了以院长为首的教学督导小组，亲自抓教学质量，并定期组织班主任和学生参与对任课教师',29488);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京金融学院',' ','北京',0,12,0,0,0,' ','暂空',' ','oldname": "北京金融学院','北京金融学院是教育体制改革中成立的一所民办高校，是高等教育学历文凭考试试点校。学院始终把培养德才兼备的人才和满足社会及行业发展的需要作为办学宗旨，坚持走“以质量求生存，以特色求发展”的道路。经过几年的努力，学院已初步建成一所以金融为主，文理兼有的多科性高等院校。为高中、中职高毕业生提供接受高等学历教育的机会。学院现有金融、法学、会计学、计算机、外语五个系，开设国际金融、保证券投资、金融会计、信息管理、商务英语、法律等专业。学院在专业和课程设置上，着眼于厚基础、宽口径、重操作，紧跟社会经济发展的步',53430);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('武汉大学武汉校友会专修学院',' ','湖北',0,0,0,0,0,' ','暂空',' ','http://www.whus.edu.cn\n','一、学院简介：武汉大学武汉校友会专修学院建院于1983年，位于东湖之滨、珞珈山下的武汉大学三环学生公寓，由时任武汉大学校长刘道玉教授亲自授牌成立，是全国高等教育自学考试委员会(教育部考试中心)登记备案的助学单位,湖北省教育厅确认合格的高等教育机构。下设有艺术学系、经济与管理系、法学系、护理学系、机电工程学系以及研究生处等6个系部，开设有广告学、环境艺术设计、金融、英语、财务管理、物流管理、行政管理、法学、护理学、计算机网络、电子工程、建筑工程等12个本科专业、15个专科专业及工程硕士、农业推广硕',30167);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('郑州大学国际学院',' ','河南',0,0,0,0,0,' ','暂空',' ','http://www5.zzu.edu.cn/gjxy/','国际学院是郑州大学内设的专门开展中外合作办学教学、管理与研究的综合性学院。国际学院前身是2007年成立的佛罗里达国际学院。经过近8年的建设与发展，学院合作办学范围逐步扩大，办学层次、质量与水平不断提升。为适应学院建设发展的实际需要，2013年元月，经学校研究决定，更名为国际学院。学院位于郑州市大学北路75号郑州大学南校区。郑州大学南校区教14号楼、15号楼、16号楼、17号楼等为学院教学实验的主要场地，教学设施先进完备。建有本院现代化多媒体教室64个，高宽敞明亮的语音室13个，专业的制图室16个',296545);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河南教育学院',' ','河南',0,4,0,0,0,' ','暂空',' ','http://www.haie.edu.cn/','河南教育学院是经国家批准具有普通高等教育招生资格的成人本科院校。学院前身是成立于1955年的河南省教育行政干部学校，1978年定名为河南教育学院。学院自1993年开始招收普通高等教育专科学生，2004年开始与河南师范大学联合招收普通高等教育本科学生。学院地处河南省省会郑州市郑东新区龙子湖高校园区，交通便利，环境优雅，信息快捷，是青年学子求知、学习的理想场所。学院现有中文、外语、教育、政治、法律与经济、历史、管理、数学、物理、化学、信息技术、人口与生命科学、地理、旅游、艺术、体育共十六个系，一个二',351298);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京旅游专修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.airclg.com/','北京旅游专修学院（原北京逸仙专修学院）是北京市教委批准的专门培养旅游服务管理高技能人才的民办非学历高等教育机构。学院的前身为中国大学，是中国民主主义革命先驱孙中山先生1913年亲手创立，1994年经北京市教委批准以孙中山先生号“逸仙”为名成立北京逸仙专修学院。2009年北京市教委（京教函【2009】541号）批复“北京逸仙专修学院变更为北京旅游专修学院”，北京恒岩基业教育投资有限责任公司为学院举办者。我院是北京市教委批准的民办高等教育机构。学生在校期间，可以通过自考、成考、电大、网络教育等多种形',38764);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京理工大学SQA-HND项目',' ','北京',0,12,0,0,0,' ','暂空',' ','http://bitsqa.lencom.cn/','北京理工大学隶属于工业和信息化部，是一所理工为主、工理文协调发展的全国重点大学，是建国后国家历批次重点建设的高校之一，是首批设立研究生院的高校之一，是首批进入“211工程”和“985工程”建设行列的高校之一。她的前身是创办于1940年的延安自然科学院，是中国共产党创办的第一所理工科大学，1943年学校并入延安大学，1946年改名为晋察冀边区工业专门学校，1948年与北方大学工学院合并成立华北大学工学院，1949年迁入北京，1950年，中法大学校本部和数理化三系并入，1951年中央人民政府教育部将',19823);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('对外经济贸易大学远程教育学院',' ','北京',0,6,0,0,0,' ','暂空',' ','http://www.euibe.com/','对外经济贸易大学远程教育学院（简称“贸大远程”）是对外经济贸易大学的直属学院之一，经教育部批准于2002年3月成立，归口管理全校网络教育的专科、本科、研究生等各层次的日常工作。贸大远程以学校深厚的国际化背景和国内首屈一指的外经贸特色为依托，立足高、精、特，涵盖专、本、研，面向广大在职从业人员，通过建立远程学习中心，覆盖全国、面向国际，在虚拟空间开展非全日制高等教育，树立对外经济贸易大学数字教育品牌。贸大远程通过各地远程学习中心面向全国招生，目前开设了国际经济与贸易、金融学、法学、商务英语、会计学',25933);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京理工大学远程教育学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://www.bitsde.com/','北京理工大学隶属于工业和信息化部，是一所理工为主、工理文协调发展的全国重点大学，是建国后国家历批次重点建设的高校之一，是首批设立研究生院的高校之一，是首批进入“211工程”的高校之一，还是第10所进入“985工程”建设的高校。她的前身是创办于1940年的延安自然科学院，是中国共产党创办的第一所理工科大学。北京理工大学远程教育学院成立于2000年，是经教育部2000年批准的15所远程教育试点高校之一。学校十分重视发展远程教育，将其列入了学校中长期发展规划。学院坚持“依法办学、规范管理、注重质量、提',81496);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京科技大学国际学院',' ','北京',0,0,0,0,0,' ','暂空',' ','http://is.ustb.edu.cn/','北京科技大学于1952年由天津大学（原北洋大学）、清华大学等6所国内著名大学的矿冶系科组建而成，现已发展成为以工为主，工、理、管、文、经、法等多学科协调发展的教育部直属全国重点大学，是全国首批正式成立研究生院的高等学校之一。1997年5月，学校首批进入国家“211工程”建设高校行列。2006年，学校成为首批“985工程”优势学科创新平台建设项目试点高校。国际学院作为北京科技大学的直属学院，承担了学校与教育部留学服务中心合作的国际本科项目的招生，教学与管理工作。学院先后与美国，日本等国的大学合作开',76057);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都经济贸易大学HND',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.bj-btec.com.cn','首都经济贸易大学创建于1956年，是由原北京经济学院和原北京财贸学院于1995年6月合并、组建的北京市属重点大学。五十余年来，学校以“培养崇德尚能，经世济民”之才为己任，经过几代首经贸人自强不息地开拓和建设，已经成长为拥有经济学、管理学、法学、文学、理学和工学等六大学科，以经济学、管理学为重要特色和突出优势，各学科相互支撑、协调发展的现代化、多科性财经类大学。校园占地总面积48万平方米,建筑面积36.5万平方米；图书馆藏书100余万册,拥有中外文期刊2000多种。学校设有工商管理、会计、财政、金',14964);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中法艾蒙时尚学院',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.ismod.org/','法国ESMOD高级时装艺术学院是世界上第一所时装学院，迄今已有170年的历史，誉满全球。ESMOD北京是2004年经北京市教委批准引进的中外合作办学项目，是国际最优质的权威性时装教育资源--法国ESMOD高级时装艺术学院目前在中国唯一授权学校，原汁原味的法国时装教育精品。中国纺织工业协会会长、原纺织工业部部长杜钰洲亲笔为ESMOD北京题写了校名。学校位于北京最繁华的黄金宝地--CBD中央商务区，坐落在城中花园--团结湖公园西侧的团结湖大厦内，毗邻中央电视台新台址和北京三里屯，是中国时装教育办学条',32745);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('国家检察官学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.jcgxy.org/','国家检察官学院是直属于最高人民检察院的成人高等院校。其主要办学任务是根据检察官法、高等教育法和检察官培训条例，培训和培养检察官及其后备人才。同时根据需要，适当培养其他法律人才。国家检察官学院具有雄厚的师资力量和优越的教学条件。经最高人民检察院决定和教育部批准，国家检察官学院2011年计划招收高起本（法学专业）300名，专升本（法学专业）300名。学习形式全部为全日制脱产。高起本学制4年，专升本学制2年。学生均为住校学习，面授教学。本科的培养目标是能够通过国家司法考试的法律从业人员。国家检察官学院',44802);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京翻译研修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.bjfyedu.com','●学院概况：学院成立于2000年，2010年经北京市教育委员会批准，更名为北京翻译研修学院。学院位于北京市昌平区阳坊，交通方便、布局合理、基础设施完善，拥有浴室、超市、银行、餐厅、洗衣房、宿舍楼等相关的生活设施;拥有高标准的游泳馆、运动场和专业标准配置的篮球场、足球场、排球场等相关运动设施。学院教学设施完善配套，拥有先进的多媒体教室、计算机教室、语音教室和学术报告厅、各类实验室、图书馆等。学院充分发挥自身资源优势和首都区位优势，积极参与国家人才战略工程，面向社会发展，面向市场需求，科学制定培养方',7776009);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京舞蹈学院继续教育学院',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.bda.edu.cn','一、学校简介：北京舞蹈学院是中国高等艺术教育中唯一的舞蹈专业院校。经过五十多年的发展，学院认真贯彻党的教育方针，秉承“尊道崇德、天地人和、文舞相融、德艺双馨”的校训，努力弘扬“爱国、爱校、爱舞蹈”的传统精神，现已成为教学设施齐全、专业设置合理、课程体系完备、师资力量雄厚、教育成果丰硕、人才培养显著的集教学、科研于一体的国内外著名舞蹈高等学府，为国家培养了众多优秀的舞蹈演员、舞蹈教师、舞蹈编导和研究人才。北京舞蹈学院继续教育学院是北京舞蹈学院的二级学院，开创于80年代初期，经历了从培训中心到成人教',102360);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国政法大学职业能力教育中心',' ','北京',0,11,0,0,0,' ','暂空',' ','oldname": "中国政法大学职业能力教育中心','中国政法大学继续教育学院是专门从事成人学历教育和继续教育的办学实体。学院的发展历程是与大学的命运紧密相连的。1952年，大学创始初期，根据当时人民政府对政法人才的需要，就以在职干部培训的形式涉猎成人教育领域。党的十一届三中全会以后，为了解决政法专门人才紧缺的局面，满足政法干部在职学习的需要，我校于1981年11月，组建了成人高等教育管理的职能部门——函授部，并于1982年正式招收首届法律专业函授生。于1992年2月以函授部为基础组建了成人教育部。1993年为满足北京地区的社会需求，开始举办夜大学',23794);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京瀚林职业研修学院',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.3aedu.com/','北京瀚林职业研修学院，是经北京市教委批准、民办非学历高等教育机构。目前，学校依托北京丰富的教育资源，深厚的文化底蕴，以及雄厚的学科基础和强大的综合办学优势，根据现代科学技术发展趋势和经济及社会发展需求设置专业，下设传媒学院、信息技术学院、建筑工程学院、艺术学院、航空学院、铁路服务学院，海乘服务学院，护理学院国际学院、动漫学院、呼叫中心学院、海港学院、文法学院、工商管理学院、铁路学院、微软IT学院等。北京瀚林职业研修学院，旨在打造强势教育品牌，一直以来与国家级协会深层合作和中外企业定向实训、毕业生',8580);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京现代音乐研修学院',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.bjcma.com','北京现代音乐研修学院是一所顺应时代发展，以培养音乐传媒、国际演艺、影视娱乐等应用型、复合型、原创型、管理型人才为主要办学定位的民办高等教育机构。学院创立于1993年。1996年，开始招收全日制学生。学院现开设流行演唱、音乐剧演唱、爵士乐演奏、音乐制作、音乐多媒体、录音艺术、音乐教育、舞蹈表演、现代歌舞、国际标准舞、影视表演、播音主持、影视制作、多媒体与动漫、电视摄像、艺术设计、艺术管理等专业。学院下设流行音乐研究所、音乐传媒研究所等6个科研机构以及北京现代歌舞团、北京爵士乐团等专业表演团体、被业',70596);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京珠宝首饰研修学院',' ','北京',0,12,0,0,0,' ','暂空',' ','http://www.gemxy.com/','北京珠宝首饰研修学院简介：北京珠宝首饰研修学院是1998年经北京市教委批准建立的民办非学历全日制高等学府。是以培养高层、复合型珠宝专业人才为目标的珠宝学院，主要从事高等教育培训。院长李劲松教授为中国宝玉石协会和亚洲珠宝联合会的创始人、中国珠宝学科的带头人和珠宝事业的开拓者、中国珠宝行业的领军人物，多年来为弘扬中华宝玉石文化、繁荣珠宝市场、培养珠宝人才做出了杰出的贡献。独具慧眼勇作发展职业培训教育的探路先锋————北京珠宝首饰研修学院走特色之路陈刚学院简介北京珠宝首饰研修学院是1998年经北京市教',15191);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都师范大学继续教育学院',' ','北京',0,4,0,0,0,' ','暂空',' ','http://jxjyxy.cnu.edu.cn','首都师范大学简况首都师范大学创建于1954年，是一所包括文、理、工、管、法、教育、外语、艺术等专业的综合性师范大学，是北京市属重点大学，教育部、北京市“省部共建大学”。建校61年来，共培养各类高级专门人才十余万名，是为北京市基础教育输送合格师资和培养其他现代化建设所需人才的重要基地。学校现有博士学位授权一级学科17个，博士点97个，博士后流动站16个，硕士学位授权一级学科26个，硕士点147个。国家重点学科4个，国家重点培育学科1个，北京市一级重点学科8个，北京市二级重点学科6个，北京市一级重点',138739);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('天津商业大学宝德学院莱佛士设计学院',' ','天津',0,8,0,0,0,' ','暂空',' ','http://tj.raffles.edu.cn','天津商业大学宝德学院新加坡莱佛士设计学院于2008年经天津市教育主管部门批准成立(津教委国际[2008]46号)，同年10月获得国家教育部批复及颁证（PDE12SG4N20080227N/228N/229N）。是一所国际高等教育学院，每年面对全国招收高中及同等学历以上毕业生，采用国际接轨的2+1学制，学员在学院学习两年后，成绩合格即可获得由新加坡莱佛士设计学院颁发的、国际承认的高级文凭，最后一年英语成绩达到要求的同学即可赴英、美、加、澳或新西兰等地包括莱佛士大学在内的众多大学，并根据各自学习的专',69592);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('上海大学ACCA国际项目',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.shu-acca.com/','上海大学--------------------------------------------------------------------------------------------------------------------------------------------------------------------------上海大学（ShanghaiUniversity）是上海市属、国家“211工程”重点建设综合性大学。1994年5月由上海工业大学、上海科技大学、上海科技高',171141);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('河北青年管理干部学院',' ','河北',0,0,0,0,0,' ','暂空',' ','http://www.hbqgy.cn','河北青年管理干部学院是隶属于共青团河北省委的公办高等专科院校。学院的前身是成立于1950年的河北省团校。1987年经国家教委批准成立河北青年管理干部学院，从1999年开始，经国家教育部批准，招收普通高职专科学生。学院办学历史悠久，文化底蕴深厚。教学设施及文体活动设施完备，有礼堂、大型微机房、语音室、多功能厅、会计电算化实验室、餐饮客房实训室、综合演播室、校园电视台、线性非线性音像编辑室、形体训练室、塑胶球场等设施，并且所有教室均为多媒体教室。多年来，学院秉承勤奋、严谨、求实、创新的治校理念，拥有',23003);
