insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林省经济管理干部学院',' ','吉林',0,6,0,0,0,' ','暂空',' ','http://www.jemcc.net','吉林省经济管理干部学院暨吉林经济职业技术学院成立于1989年，是一所以经济学、管理学为主，兼有工学、法学、文学等多学科协调发展的，具有普通高等教育、成人高等教育、短期培训与继续教育多种办学资格的省属公办高校，也是吉林省首批开展高等职业教育的院校之一，被誉为“企业家的摇篮”。学院座落在风光秀丽的北国春城——长春。现有院部、高新、红旗3个校区，设置了财政金融、工商管理、计算机、国际商务、会计、资源与环境管理、外语、人文科学8个系和政治与公共教学部，开设了8个成人本科专业（专科升本科）和40余个高等职',48467);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('南昌教育学院',' ','江西',0,4,0,0,0,' ','暂空',' ','oldname": "南昌教育学院','schoolcode": "367000800',25728);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('吉林省教育学院',' ','吉林',0,4,0,0,0,' ','暂空',' ','http://www.jledu.net','吉林省教育学院成立于1957年,是省政府设立,省教育厅主管,国家教育部批准备案,具有师范性质的成人高等院校.48年来，学院始终坚持为基础教育服务的办学宗旨,在教学、教研、教师培训和中小学校长培训方面,为吉林省基础教育做出了重要的贡献.改革开放以来,学院的事业有了很大的发展,现设有中文、政治、教育管理、外语、数学、艺术、计算机、现代教育技术等8个系;设有中文、公共关系与文秘、政治、市场营销、历史、教育管理、英语、商务英语、数学、信息与多媒体、物理、化学、电脑艺术设计、计算机及应用、现代技术教育、传',21130);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东省聊城教育学院',' ','山东',0,4,0,0,0,' ','暂空',' ','http://www.lcjyxy.com/','聊城教育学院是国家教育部批准的具有高等学历教育招生资格的成人高等院校。学院历史悠久，文化底蕴丰厚；本院校址曾为明、清两代考院，办学已有百余年历史。1904年即为东昌简易学堂，后为山东省立第三师范、聊城师范学校、聊城师范专科学校、聊城教育学院。2005年8月，与普通本科院校聊城大学东昌学院合并办学，提升了办学层次，为学生的进一步发展延拓了空间。学院新校区位于山东省聊城市经济技术开发区，占地500余亩（规划占地1000亩）。',38856);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京邮电大学继续教育学院职业教育',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.beiyou.cn/','北京邮电大学就培训教育中心的前身是北京邮电大学培训中心,成立于一九八六年，是一个集培训、科研和研究生培养于一体的高级人才培养基地。北京邮电大学培训教育中心多年来一直坚持面向企业开展继续教育，与众多的国内外通信运营商、通信设备制造商、业务提供商、内容服务商和其它行业中具有通信需求的企业一直都保持着长期的培训合作关系，在业界享有很高的声誉。北京邮电大学培训教育中心现有专职教师49名。其中博士生导师2名,硕士生导师5名。专职教师具有丰富的业内工作经验，平均行内工作经验5年以上。北京邮电大学培训教育中心',17314);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('首都师范大学科德学院高教自考',' ','北京',0,8,0,0,0,' ','暂空',' ','http://www.sd1954.cn/','首都师范大学科德学院（学校代码13629）是经教育部批准实施本科层次学历教育的独立学院。在校学生5000余人。学校招生列入全国普通高等学校统一招生计划，在全国30余个省市招生。学校下设艺术设计学院、传媒学院、演艺学院、国际商学院，共开设32个本科专业，2010年在15个专业和专业方向招生。学校位于北京大兴区，主校区占地近500亩，另有1400余亩校内教学实践基地。学校已建成数字化校园，拥有设施先进的实验室、图书馆、运动场，建筑风格独特，校园环境优美。学校有独特的教育理念，创新的教学模式。为追求不',20047);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京理工大学继续教育学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://sce.bit.edu.cn/sce/','北京理工大学成人高等教育始于1956年，经过多年的发展和建设，现在建制为四院一中心一基地，即：继续教育学院，远程教育学院，国际教育合作学院，职业技术教育学院，北京理工大学教育培训中心，全国重点建设职业教育师资培养培训基地。学院充分依托学校雄厚的师资队伍和丰富的办学资源，坚持以“依法办学、规范管理、注重质量、提高效益”为工作方针，努力营造和谐氛围。学院加强统筹协调，强化服务能力，注重内涵建设，在稳定教育教学工作秩序、提高教育教学质量的基础上，推进改革，学院各方面都得到了快速发展。学院现有包括成人高',30857);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北京理工大学国际教育学院',' ','北京',0,1,0,0,0,' ','暂空',' ','http://sice.bit.edu.cn','北京理工大学简介北京理工大学创办于1940年，前身是诞生于延安的“自然科学院”，是中国共产党创办的第一所理工科大学，是新中国成立以来国家历批次重点建设的高校，首批设立研究生院，首批进入国家“211工程”和“985工程”建设行列，现隶属于工业和信息化部。历经70多年发展，如今的北京理工大学已在人才培养、科学研究、社会服务、文化传承等方面居于国内研究型大学前列，跻身于国内一流研究型大学。2012年，学校首次进入在全球具有广泛影响力的英国QS世界大学排名“亚洲大学100强”和“世界大学500强”，在入',59702);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('黑龙江省教育学院',' ','黑龙江',0,4,0,0,0,' ','暂空',' ','oldname": "黑龙江省教育学院','黑龙江省教育学院高等职业技术分院是一所全日制高职（高专）院校，隶属于黑龙江省教育学院。黑龙江省教育学院始建于1959年，是省政府领导、省教育厅直属的全省基础教育、职业教育、成人教育、电化教育及其他各类教育的师资培训基地。学院内设中文、数学、物理、英语、生化、教育等教学系及计算机教育中心、中小学教育培训中心、电化教育等教学、教研辅助部门。建院40余年来，积累了丰富的办学、教学经验，办学条件优越、师资力量雄厚，为全省教育和社会各行各业培养了4万多名本、专科毕业生。学院坐落在北方名城哈尔滨市，分南岗和',11077);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山西能源学院',' ','山西',0,1,0,0,0,' ','暂空',' ','http://www.sxmtxy.net/','山西能源学院是教育部批准，山西省人民政府举办，山西省教育厅管理的一所应用技术型普通本科学校。学院以煤炭、电力、新能源类专业为主体，主要培养基础理论扎实，专业技能突出，实践动手能力强，直接为能源企业服务，为区域经济社会发展服务的工程技术人才和管理人才。学院位于大学林立、学风浓郁的山西省高校园区，占地面积675.52亩，校舍建筑面积19.69万平方米，教学科研仪器设备值4079.35万元，图书42.33万册，校内实验实训室76个，校外实习基地66个。现有在校生5400余人，专任教师300余人，其中正',15305);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('立信财会',' ','上海',0,6,0,0,0,' ','暂空',' ','http://www.lixinedu.com.cn','立信财会隶属于上海立信会计学院，是国内知名财会教育媒体群及资讯平台，2012年创立于上海立信会计学院。立信财会旗下拥有财会资讯网、立信财会网校、立信财会书店、等多个专业媒体，以及财会论坛、财会91、财会SNS空间等互动社区。[1]关于立信财会上海立信会计学院是上海市人民政府举办的全日制普通高等学校，其前身为立信会计专科学校，由“中国现代会计之父”、教育家潘序伦先生始创于1928年，并在教育实践过程中，开辟了会计学校、会计师事务所、会计图书用品社三位一体的办学模式。1952年，全国院系调整时，学校',265082);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中山大学翻译学院',' ','广东',0,5,0,0,0,' ','暂空',' ','http://www.sysu-edu.org/','中山大学留学预科简介中山大学翻译学院是一所以培养双外语人才为宗旨的新型学院，旨在适应经济全球化和文化区域化带来的人才市场多元化需要，现设有翻译系、商务外语系、对外汉语系、国际事务系、朝鲜语系、西班牙语系、阿拉伯语系和俄语系等8个系。学院师资力量雄厚，专职教师全部具有硕士或博士学位，其中三分之一是从英国、美国、澳大利亚、加拿大等国学成归来的硕士和博士，外籍教师占三分之一，具有开办国际教育项目得天独厚的优势。为了满足华南地区教育国际化发展需求，2008年中山大学翻译学院经学校批准成立了中山大学留学预',30527);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('湖南航空工业职工工学院',' ','湖南',0,12,0,0,0,' ','暂空',' ','oldname": "湖南航空工业职工工学院  曾用名：南方动力机械公司职工工学院','schoolcode": "437001000',7135);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('兰州航空工业职工大学',' ','甘肃',0,12,0,0,0,' ','暂空',' ','http://www.lzavu.cn/','兰州航空工业技工学校始建于1958年，属省劳动厅主管，是一所以培养机械制造和航空机电技能人才为特色的公办技工学校.学校举办高级技工班和中级技工班。目前开设数控车工、数控铣工、模具制造与维修、机电一体化、数控机床维修等5个高级班专业和铆焊工、机修钳工、电子技术应用、机械技术、机械技术产品检验、计算机应用、钳工、数控车工、数控铣工等11个中级班专业。学校教育教学设施齐全，现有各类实验室8个(其中计算机网络实验室3个、电工电子实验室1个、财会模拟实验室1个、机械实验室1个、数控仿真实验室1个、综合实验',23635);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('江苏省青年管理干部学院',' ','江苏',0,12,0,0,0,' ','暂空',' ','oldname": "江苏省青年管理干部学院','江苏省青年管理干部学院其前身为江苏省团校。1987年，经省政府、国家教育委员会批准成立江苏省青年管理干部学院，与江苏省团校为同一建制，列入国民教育高校序列。1999年9月，经省教育厅批准，开始招收国民教育普高系列高中毕业生，属大学专科，学制三年，是一所公办高等专科学校。2001年1月10日，我院正式并入江苏省委党校（江苏省行政学院）。在江苏省教育主管部门的直接指导下，随着学院办学规模不断扩大，从单一招收成高生、普高生，逐步发展成为同时招收成高生、普高生和远程教育本科生。江苏省委党校（江苏省行政学',8673);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('广西经济管理干部学院',' ','广西',0,12,0,0,0,' ','暂空',' ','oldname": "广西经济管理干部学院','广西经济管理干部学院创办于1951年5月，其前身是广西省行政干部训练班，其间历经变革，1983年7月改为现校名。学院是一所国家公办的以培养各类经济管理人才为主的综合性高校，集成人高校、高职高专、干部培训基地于一体，是原国家经贸委指定的国有大中型企业领导干部培训院校，也是教育部批准的具有高等学历教育招生资格的高等院校。创办60年来特别是近30年开办高等学历教育以来，已为国家培养了5万余名人才，近年被广西壮族自治区教育厅评为“高等教育评估优秀学校”。现有在职教职工440多人，其中高中级职称教师约占专',21321);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('厦门大学国际学院',' ','福建',0,0,0,0,0,' ','暂空',' ','http://liuxue.xmu.edu.cn/','厦门大学国际学院直属于厦门大学，公立办学。学院以“发展中国与世界各国的教育文化交流与合作”为宗旨，积极引进国外优质的教育资源，结合厦门大学完善的教学软硬件条件，为中国学生提供接受国际教育的平台，努力实现国内外优质教育的有效衔接，致力于培养国际化、复合型的人才。学院采用国外合作院校认可的课程体系，引进英语原版教材及教学方式，采取集中全英文授课，注重将英语强化培训、专业基础课程和德育教育有机结合，从语言、学习技能、独立生活等方面为学生尽早融入留学国家的学习生活提供一个可靠平台。个性化的留学方案根据学',38907);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中南林业科技大学班戈学院',' ','湖南',0,0,1,0,0,' ','暂空',' ','http://io.csuft.edu.cn/','中南林业科技大学班戈学院(以下简称“班戈学院”)是经教育部批准(教外办学函[2013]70号)、由中南林业科技大学和英国班戈大学——世界大学300强(中国大陆有7所大学入选)合作建立的非独立法人的全日制公办院校，是中南林业科技大学的下属学院，是湖南省高起点、高标准、高定位的国际化创新人才培养基地。班戈学院采用英国班戈大学的课程模式开展本科层次的高等学历教育。全程在国内完成学业的学生也可获得班戈大学学士学位和中南林业科技大学学士学位及本科毕业证书。班戈学院现设有林学、电子信息工程、金融学、会计学4',11519);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民武装警察部队学院成人教育培训部',' ','河北',0,12,0,0,0,' ','暂空',' ','http://www.wjxy.edu.cn','中国人民武装警察部队学院(简称武警学院)于1981年创建，隶属于公安部，是一所为公安边防、消防、警卫部队培养指挥管理和专业技术警官的现役本科院校。学院集军事院校、公安院校和普通高等院校的特点于一体，具有鲜明的办学优势和专业特色，师资力量雄厚，教学管理规范，办学条件优越。学院坐落于现代化新兴城市——河北省廊坊市，毗邻京津，交通便利，环境优美。为适应国家经济建设需要，满足社会对消防专业人才需求，经全国高等教育自学考试指导委员会和公安部批准，高等教育自学考试消防管理专业（专科）、消防工程专业（独立本科',6799);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国人民大学(苏州校区)',' ','江苏',0,0,0,0,0,' ','暂空',' ','http://sc.ruc.edu.cn/','2012年9月4日，中国人民大学正式发文设立苏州校区，全面负责国际学院（苏州研究院）和中法学院等教学科研机构和社会服务机构的管理。中国人民大学苏州校区是人民大学整体事业发展的重要组成部分和新的增长点，是面向华东地区的教学科研基地；是面向国际、探索中外合作办学和培养高端人才的实验基地；是优势学科的拓展与提升基地。中国人民大学成立苏州校区管理委员会，作为苏州校区的领导机构，主要负责苏州校区的总体战略规划、重大事项决策和学校对苏州校区相关支持政策的制定和协调。中国人民大学校长陈雨露任苏州校区管理委员会',80140);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东广播电视大学',' ','山东',0,12,0,0,0,' ','暂空',' ','http://www.sdtvu.com.cn/','山东广播电视大学是以现代信息技术为主要手段，采用广播、电视、文字和音像教材、计算机网络等多种媒体，面向全省进行现代远程开放教育的高等学校。学校创办于1979年1月。目前，学校已形成了由省电大、18所市级电大、140个教学点组成的覆盖全省城乡的远程教育网络，组成了统筹规划、分级办学、分级管理的现代远程开放教育教学系统，举办本科、专科、中专层次学历教育和多种形式的非学历教育、继续教育和岗位培训，形成了多层次、多规格、多功能、多种形式办学，学历教育与非学历教育协调发展的教育格局。学校坚持“一体多元”的',22024);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('山东技师学院',' ','山东',0,12,0,0,0,' ','暂空',' ','http://www.sdjsxy.cn','山东技师学院是山东省人民政府批准设立、山东省人力资源和社会保障厅直属的全省第一所国办全日制技师学院，以培养高级技师、技师、高级技工等高技能人才为主。学院是全国职业教育先进单位、国家高技能人才培养示范基地、首批全国技工院校师资培训基地、黄炎培职业教育奖优秀学校、国家电子信息产业高技能人才培训基地、国家安全评价师定点培训单位、山东省二级安全培训机构、山东省退役士兵职业技能培训基地、山东省首批学历教育和技能教育对接院校、济南市就业培训定点机构，设有国家职业技能鉴定所。学院创建于1980年，位于美丽的泉',21854);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('中国海洋大学国际教育学院',' ','山东',0,12,0,0,0,' ','暂空',' ','http://www2.ouc.edu.cn/sie/index.html','中国海洋大学国际教育学院介绍中国海洋大学国际教育学院是中国海洋大学20所建制学院之一，成立于1999年，经过11年的发展，现有教职工（含外聘教师）50多人，每年在读学生数为500余人。作为国家“985工程”和“211工程”重点建设高校，中国海洋大学始终以培养德智体美全面发展，具有民族精神和社会责任感，具有国际视野和合作竞争意识，具有科学精神和人文素养、具有创新精神和实践能力的高素质创新型人才为目标。为此，国际教育学院以“服务于海大，服务于社会，打造坚实的国际教育平台”为宗旨，肩负起中国海洋大学国',30972);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('现代软件学院',' ','北京',0,0,0,0,0,' ','暂空',' ','oldname": "现代软件学院','现代软件学院成立于1993年，是经北京市教委批准成立的一所民办高等教育机构(办学许可证：111010020000330)。学院目前实行三年学制、四年学制、以及双证就业2+2年在校学制、学院始终让学生在有限的时间内尽可能地学习到最实用的专业知识，获得与就业相关的职业素质指导，并且让学生有条件进行不断地实践学习;接触到企业里的工程师，真实模拟企业的实际运作流程。学校为学生就业建立了系统的就业指导中心，与北京市中关村园区人才中心、百度、新浪、神舟数码、腾讯等一系列国内一流IT企业合作，将成绩合格的学生',33169);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('北方经贸学院',' ','北京',0,0,0,0,0,' ','暂空',' ','http://www.1993edu.cn/','北方经贸学院，1993年建校，办学资质许可证编号教民111010020000330号，由北京CBD中央商业区、中关村科技园等众多商业、科技龙头企业共同投资创办的一所新型2+2教育模式高等学府。位于北京市昌平区西北4公里天寿山脚下的北京大学昌平校区内，该校区曾为北京大学的分校区，校园占地面积550亩，建筑面积6万平方米，绿化面积占总面积的70%。校内环境优美，风景如画，绿树成荫,空气清新，每一个角落都充满了文化气息，享受北大百年资源，实现考生求学梦想。北方经贸学院是国内首批实行2+2教育模式的院校',34361);
insert into COLLEGE_INFO (NAME,LOGO,PROVINCE,LEVEL,TYPE,ZHISHU,IS211,IS985,TEL,ADDRESS,EMAIL,URL,REMARK,SEQ) values ('临沂电力学校',' ','山东',0,1,0,0,0,' ','暂空',' ','http://www.dlxxsd.com/ ','临沂电力学校是山东省唯一一所全日制国家级重点高等专业公办电力院校，是经山东省人民政府批准建立的临沂市首家专门培养高级技能型人才的技师学校，是一所公办、全日制、综合型职业院校，是财政全额拨款副厅级事业单位，隶属于临沂市人民政府领导。学校的前身是于1992年由原山东省国防工业技工学校与原临沂地区劳动局技工学校合并而成的原临沂市劳动局第一技工学校；1997年10月经原国家劳动和社会保障部和山东省人民政府批准，改建为临沂市高级技工学校；2002年12月，经山东省人民政府批准，更名为临沂电力学校，是山东省',11628);
